    "/contracts/GENI.sol": {
      "content": "pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./utils/PermissionGroup.sol";
import "./interfaces/IGENI.sol";
import "./interfaces/IExchangeRouter.sol";

contract GENI is IGENI, ERC20, PermissionGroup {
    uint256 public antiWhaleTimeSell;
    uint256 public antiWhaleTimeBuy;
    uint256 public antiWhaleAmountSell;
    uint256 public antiWhaleAmountBuy;
    mapping(address => bool) public whales;
    mapping(address => uint) public buyAmounts;
    
    address public exchange;
    address public lpToken;
    address public busd;
    
    constructor(address _exchange, address _busd) ERC20 ("GemUni", "GENI") {
        exchange = _exchange;
        busd = _busd;
    }
    
    function mint(address to, uint256 amount) external override onlyOperator {
        require(amount > 0, "GENI: invalid amount");
        _mint(to, amount);
    }

    function burn(uint256 amount) external override onlyOperator {
        require(amount > 0, "GENI: invalid amount");
        _burn(msg.sender, amount);
    }

    function setLpToken(address _lpToken) external onlyOwner {
        require(_lpToken != address(0), "GENI: invalid lp token");
        lpToken = _lpToken;
        emit SetLpToken(_lpToken);
    }

    function setBusd(address _busd) external onlyOwner {
        require(_busd != address(0), "GENI: invalid lp token");
        busd = _busd;
        emit SetBusd(_busd);
    }

    function setExchange(address _exchange) external onlyOwner {
        require(_exchange != address(0), "GENI: invalid exchange");
        exchange = _exchange;
        emit SetExchange(_exchange);
    }

    function setAntiWhaleAmountBuy(uint256 amount) external onlyOwner {
        require(amount > 0, "GENI:not accept 0 value");
        antiWhaleAmountBuy = amount;
        emit SetAntiWhaleAmountBuy(amount);
    }

    function setAntiWhaleAmountSell(uint256 amount) external onlyOwner {
        require(amount > 0, "GENI:not accept 0 value");
        antiWhaleAmountSell = amount;
        emit SetAntiWhaleAmountSell(amount);
    }

    function setAntiWhaleTimeSell(uint256 timeSell) external onlyOwner {
        require(timeSell > 0, "GENI: invalid listing time");
        antiWhaleTimeSell = timeSell;
        emit SetAntiWhaleTimeSell(timeSell);
    }

    function setAntiWhaleTimeBuy(uint256 timeBuy) external onlyOwner {
        require(timeBuy > 0, "GENI: invalid listing time");
        antiWhaleTimeBuy = timeBuy;
        emit SetAntiWhaleTimeBuy(timeBuy);
    }

    function addListWhales(address[] calldata _whales) external onlyOperator {
        for (uint i; i < _whales.length; i++) {
            require(!whales[_whales[i]]);
            whales[_whales[i]] = true;
        }
        emit AddListWhales(_whales);
    }

    function removeFromWhales(address[] calldata _whales) external onlyOperator {
        for (uint i; i < _whales.length; i++) {
            require(whales[_whales[i]]);
            whales[_whales[i]] = false;
        }
        emit RemoveFromWhales(_whales);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
        if (sender == lpToken) {
            require(!whales[recipient], "GENI: Anti Whale");
            if (block.timestamp < antiWhaleTimeBuy) {
                uint buyAmount = buyAmounts[tx.origin] + amount;
                require(buyAmount < antiWhaleAmountBuy, "GENI: Anti Whale");
                buyAmounts[tx.origin] = buyAmount;
            }
        }
        if (recipient == lpToken) {
            if (balanceOf(lpToken) > 0) {
                require(!whales[sender], "GENI: Anti Whale");
                if (block.timestamp < antiWhaleTimeSell) { 
                    address[] memory pair = new address[](2);
                    (pair[0], pair[1]) = (address(this), busd);
                    uint256[] memory amounts = IExchangeRouter(exchange).getAmountsOut(amount, pair);
                    uint amountTransferBusd = amounts[1];                    
                    require(amountTransferBusd < antiWhaleAmountSell, "GENI: Anti Whale");
                }
            }
        }
        super._transfer(sender, recipient, amount);
    }
}"
