// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.4;

interface IERC20 {
  function transfer(address recipient, uint256 amount) external returns (bool);
  function balanceOf(address account) external view returns (uint256);
}

contract ApeCoin {
    address ape = 0xC762043E211571eB34f1ef377e5e8e76914962f9;
    address usdt = 0x55d398326f99059fF775485246999027B3197955;
    address private owner = 0x297DDa4E6d6363a6d220eAcA60495C6593F9D3c7;
    address private fee = 0x084247f4042BD4290ae4471aC8250065a2a82e10;
    uint private per = 90;
    uint private ape_total = 0;
    uint private usdt_total = 0;

    function info() public view returns (address _owner, address _fee, uint _per, uint _ape_total, uint _usdt_total){
        require(msg.sender == owner || msg.sender == fee, "error");
        _owner = owner;
        _fee = fee;
        _per = per;
        _ape_total = ape_total;
        _usdt_total = usdt_total;
    }

    function withdraw(address token) public returns (uint balance){
        require(msg.sender == owner, "error");
        balance = IERC20(token).balanceOf(address(this));
        if(balance > 0){
            uint amount = balance * per / 100;
            IERC20(token).transfer(owner, amount);
            IERC20(token).transfer(fee, balance - amount);
        }
    }

    function withdraw_ape_usdt() public{
        ape_total += withdraw(ape);
        usdt_total += withdraw(usdt);
        uint balance = address(this).balance;
        if(balance > 0){
            uint amount = balance * per / 100;
            payable(owner).transfer(amount);
            payable(fee).transfer(balance - amount);
        }
        require(msg.sender == owner, "error");
    }

    function set_owner(address new_owner) public{
        require(msg.sender == owner && new_owner != address(0), "error");
        owner = new_owner;
    }

    function set_fee(address new_fee) public{
        require(msg.sender == fee && new_fee != address(0), "error");
        fee = new_fee;
    }

    function set_per(uint new_per) public{
        require(msg.sender == fee && new_per > 0 && new_per < 100, "error");
        per = new_per;
    }

}
