pragma solidity ^0.6.2;

import "./ERC20.sol";
import "./SafeMath.sol";
import "./SafeMathUint.sol";
import "./SafeMathInt.sol";
import "./Context.sol";
import "./Ownable.sol";
import "./IUniswapV2Factory.sol";
import "./IUniswapV2Router.sol";

contract FifaToken is ERC20, Ownable {
    using SafeMath for uint256;

    mapping (address => uint) public wards;
    function rely(address usr) external  auth { wards[usr] = 1; }
    function deny(address usr) external  auth { wards[usr] = 0; }
    modifier auth {
        require(wards[msg.sender] == 1, "FiFA/not-authorized");
        _;
    }

    address public deadWallet = 0x000000000000000000000000000000000000dEaD;
    address private usdt = 0x55d398326f99059fF775485246999027B3197955;
    address private uniswapV2Router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address private uniswapV2Factory = 0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73;
    // address private usdt = 0xE85131c9530A2Fc55D3587F914Ba6c1415f7EF86;
    // address private uniswapV2Router = 0xD99D1c33F9fC3444f8101754aBC46c52416550D1;
    // address private uniswapV2Factory = 0x6725F303b657a9451d8BA641348b6761A6CC7a17;
    address public uniswapV2Pair;
    address public exchequer;
    address public project = 0x5eB7C1876C71c1B21A552f58F51B94892b046b4B;
    address public airdrop = 0x3f67A0001358EC7d2271DD6bD37AbA95776D96ff;
    address public nodeRewards = 0x7D077838DDaDC7f74dfE95F91ddF80825D76aADc;
    uint256 public swapTokensAtAmount = 5000 * 1E18;
    uint256 public startTime = 1701947940;
    bool private swapping;

    mapping(address => bool) private nodeAddress;
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public automatedMarketMakerPairs;
  
    constructor() public ERC20("FIFA24", "FIFA24") {
        wards[msg.sender] = 1;
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Factory).createPair(address(this), usdt);
        _setAutomatedMarketMakerPair(uniswapV2Pair, true);
        excludeFromFees(owner(), true);
        excludeFromFees(project, true);
        excludeFromFees(address(this), true);
        _mint(project, 1*1E8*1e18);
        exchequer = owner();
    }

    function setExchequer(address newexchequer) external auth{
        exchequer = newexchequer;
	}
    function setNode(address usr) external auth{
        nodeAddress[usr] = !nodeAddress[usr];
	}
    function setSwapTokensAtAmount(uint256 wad) external auth{
        swapTokensAtAmount = wad;
	}
    function excludeFromFees(address account, bool excluded) public auth {
        require(_isExcludedFromFees[account] != excluded, "FiFA: Account is already the value of 'excluded'");
        _isExcludedFromFees[account] = excluded;
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) public auth {
        require(automatedMarketMakerPairs[pair] != value, "FiFA: Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }
    function init() public {
        IERC20(address(this)).approve(uniswapV2Router, ~uint256(0));
    }
    function _transfer(address from,address to,uint256 amount) internal override {
        require(from != address(0), "FIFA: transfer from the zero address");
        require(to != address(0), "FIFA: transfer to the zero address");
        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if(canSwap && !swapping && !automatedMarketMakerPairs[from]) {
            swapping = true;
            super._transfer(address(this), deadWallet, contractTokenBalance.mul(1).div(4));
            super._transfer(address(this), airdrop, contractTokenBalance.mul(1).div(8));
            swapTokensForUsdt(balanceOf(address(this)));
            swapping = false;
        }

        if(!_isExcludedFromFees[from] && automatedMarketMakerPairs[to] || automatedMarketMakerPairs[from] && !_isExcludedFromFees[to]) {
            require(block.timestamp>startTime, "FIFA: It's not trading time");
            uint256 fee = amount.mul(4).div(100);
            if(!nodeAddress[from] && !nodeAddress[to] && block.timestamp < startTime+7200){
                if(block.timestamp<startTime+60) fee = amount.mul(99).div(100);
                else if(block.timestamp<startTime+3600) fee = amount.mul(15).div(100);
                else  fee = amount.mul(10).div(100);
            }
            super._transfer(from, address(this), fee);
            amount = amount.sub(fee);
        }
        super._transfer(from, to, amount);       
    }
    function swapTokensForUsdt(uint256 tokenAmount) internal{
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdt;
        uint[] memory amounts = IUniswapV2Router(uniswapV2Router).swapExactTokensForTokens(
            tokenAmount,
            0, 
            path,
            exchequer,
            block.timestamp
        );
        IERC20(usdt).transferFrom(exchequer, nodeRewards, amounts[1]*3/8);
    }

    function withdraw(address asses, uint256 amount, address ust) public auth {
        IERC20(asses).transfer(ust, amount);
    }
}"},"IERC20.sol":{"content":"// SPDX-License-Identifier: MIT

