    "contracts/strategies/SoftStaking5PercentAPY/CompoundSoftStaking5PercentAPY.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "../../external/nimbus/IStakingRewards.sol";
import "../../external/tokens/ERC20TOKEN.sol";
import "../../external/exchanges/pancakeswap/IRouter.sol";
import "../StrategyBase/StrategyManager.sol";

/**
 * @author Pedro Reyes
 *
 * @title Strategy - NBU reward compound of NBU 5% APY soft staking from Nimbus Platform
 *
 * @notice This contracts holds the ownership of NBU tokens and allows the
 * OPERATIONAL_ROLE to reinvest the rewards in the staking contract.
 *
 * @dev This contract uses LockStakingRewardMinAmountFixedAPY.sol from
 * NimbusPlatform to execute the strategy. In particular we use
 * NBU Soft Staking — APY 5% with no locking period. There are 3 main methods in this contract
 * that are inherited from StrategyManager.sol:
 *
 * ✍ executeDeposit: user send to the contract NBU and the contract deposits
 * NBU tokens in the staking contract.
 * This method can be executed by any address.
 *
 * ✍ executeStrategy: retrieves the rewards from the staking contract and re-stake them.
 * This mehtod can only be executed by the OPERATIONAL_ROLE address.
 *
 * ✍ executeWithdraw: the contract withdraws NBU tokens rewards and body
 * from the staking contract and sends them to the user. After that, the remaining NBU tokens
 * are sent to the staking contract again.
 * This method can be executed by any address.
 *
 * The rest of methods are used to manage changes in the strategy and admin operations.
 *
 * @dev NBU Soft Staking from Nimbus Platform — APY 5% - Compound strategy
 */
contract CompoundSoftStaking5PercentAPY is ERC4626, StrategyManager, IStrategy {
    /** @dev External contract used in the strategy */
    IStakingRewards public immutable nimbusStaking;
    ERC20TOKEN public immutable tokenNBU;

    /** @dev Partial swaps of shares */
    bool public partialSwapsEnabled = false;

    /** @dev Requirement - Check user shares balance */
    modifier senderHasShares() {
        require(balanceOf(msg.sender) > 0, "No shares");
        _;
    }

    /** @dev Events for tracking the strategy */
    event NewDeposit(address indexed user, uint256 amount, uint256 fee);
    event StrategyExecution(
        address indexed relayer,
        uint256 rewardsReStaked,
        uint256 balanceStaked,
        uint256 rewardsReStakedFee
    );
    event Withdrawal(address indexed user, uint256 amount, uint256 fee);

    /** @dev Events related to income strategy setup */
    event PartialSwapsUpdate(bool partialSwapsEnableStatus);

    /**
     * @notice Constructor function to initialize the role for reinvestments.
     * @dev This function is called by the user that wants to start this strategy
     */
    constructor(
        address _admin,
        address _operational,
        address _finance,
        address _nimbusStakingAddress,
        address _tokenDepositAddress
    )
        ERC4626(IERC20Metadata(_tokenDepositAddress))
        ERC20("shares", "SHA")
        StrategyManager(_admin, _operational, _finance)
    {
        require(
            _nimbusStakingAddress != address(0),
            "Provide a staking address"
        );
        require(
            _tokenDepositAddress != address(0),
            "Provide a token deposit address"
        );

        // Cast external contracts
        nimbusStaking = IStakingRewards(_nimbusStakingAddress);
        tokenNBU = ERC20TOKEN(payable(_tokenDepositAddress));
        require(
            tokenNBU.approve(_nimbusStakingAddress, type(uint256).max),
            "Staking access to tokens granted"
        );

        // Set swap router
        exchangeSwap = IRouter(swapRouter);
    }

    /** @notice User deposits to be part of the strategy */
    function executeDeposit(
        uint256 tokenAmount
    ) external payable override nonReentrant {
        require(tokenAmount > 0, "Zero token sent");
        // Deposit and send shares to user
        deposit(tokenAmount, msg.sender);

        // Pay fee per transaction (i.e. 1$)
        payTransactionFee(depositFeeAmount);

        emit NewDeposit(msg.sender, tokenAmount, depositFeeAmount);
    }

    /** @notice OPERATIONAL_ROLE executes the strategy to
     * reinvest the rewards from the NFT SmartStaker.sol.
     */
    //  @todo Check how to implement this in a proxy contract
    function executeStrategy()
        external
        override
        onlyRole(OPERATIONAL_ROLE)
        nonReentrant
    {
        // Get current balance of the staking contract
        uint256 balanceInStaking = nimbusStaking.balanceOf(address(this));

        // Get earned rewards from the staking contract
        uint256 rewardsReStaked = earned();

        if (rewardsReStaked > 0) {
            // Get just the rewards from the staking contract
            // nimbusStaking.getReward();

            // Withdrawing all from the staking contract (rewards + body)
            nimbusStaking.exit();
        }

        // Get fee from the rewards in the staking contract
        uint256 strategyFee = getStrategyFee(rewardsReStaked);

        if (strategyFee > 0) {
            // Transfer fee to the finance relayer
            require(
                tokenNBU.transfer(financeRelayer, strategyFee),
                "Error sending strategyFee"
            );
        }

        // Execute staking with the remaining balance
        uint256 balanceStaked = stakeRemainingBalance() - balanceInStaking;
        // uint256 balanceStaked = stakeRemainingBalance();

        // Emit withdrawal event
        // slither-disable-next-line reentrancy-events
        emit StrategyExecution(
            msg.sender,
            rewardsReStaked,
            balanceStaked,
            strategyFee
        );
    }

    /** @notice User unsubscribes from the strategy. */
    function executeWithdraw()
        external
        payable
        override
        senderHasShares
        nonReentrant
    {
        // Swap all user's shares
        uint256 totalShares = balanceOf(msg.sender);

        uint256 assets = _swapShares(totalShares);

        // User withdraws all the shares
        emit Withdrawal(msg.sender, assets, withdrawalFeeAmount);

        // Pay fee per transaction (i.e. 1$)
        payTransactionFee(withdrawalFeeAmount);
    }

    /** @notice User swap shares for NBU tokens */
    /** @notice This is disable for the moment
     * as probably this will not be an option in the final UI
     */
    function swapShares(
        uint256 shares
    ) external senderHasShares nonReentrant returns (uint256 assets) {
        // Check partial swap enabled
        require(partialSwapsEnabled, "Partial swaps disabled");

        assets = _swapShares(shares);
    }

    /** @dev Set partial swaps enabled/disabled */
    function setPartialSwapsEnabled(
        bool newPartialSwapsEnabled
    ) external onlyRole(GOVERNANCE_ROLE) {
        partialSwapsEnabled = newPartialSwapsEnabled;

        emit PartialSwapsUpdate(partialSwapsEnabled);
    }

    /** @notice Return the current reward of the pool */
    function earned() public view returns (uint256) {
        return nimbusStaking.earned(address(this));
    }

    /** @dev See {IERC4262-totalAssets}. */
    function totalAssets() public view override returns (uint256) {
        return ERC4626.totalAssets() + nimbusStaking.balanceOf(address(this));
    }

    /** @notice User swap shares for NBU tokens */
    function _swapShares(uint256 shares) private returns (uint256 assets) {
        // Swap all user's shares
        uint256 totalShares = balanceOf(msg.sender);

        require(shares <= totalShares, "Input error: shares > totalShares");

        // Withdrawing all from the staking contract
        nimbusStaking.exit();

        // Sending assets equivalent in assets to user
        address assetsReceiver = msg.sender;
        address sharesOwner = msg.sender;
        assets = redeem(shares, assetsReceiver, sharesOwner);

        // Execute staking with the remaining balance
        stakeRemainingBalance();
    }

    /** @dev Stake the remaining balance in the contract */
    function stakeRemainingBalance() private returns (uint256) {
        uint256 remainingBalance = tokenNBU.balanceOf(address(this));

        if (remainingBalance > 0) {
            nimbusStaking.stake(remainingBalance);
        }

        return remainingBalance;
    }
}
"
