    "contracts/external/tokens/ERC20TOKEN.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract ERC20TOKEN is ERC20 {
    uint256 public constant INITIAL_SUPPLY = 100_000_000_000_000e18;

    constructor(string memory name) ERC20(name, name) {
        _mint(msg.sender, INITIAL_SUPPLY);
    }

    function mintTo(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function mint(uint256 amount) external {
        _mint(msg.sender, amount);
    }
}
"
    },
    "contracts/external/exchanges/pancakeswap/IRouter.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

/**
 * https://docs.pancakeswap.finance/code/smart-contracts/pancakeswap-exchange/v2/router-v2
 * https://docs.uniswap.org/contracts/v2/guides/smart-contract-integration/trading-from-a-smart-contract
 *
 * 📘 Binance smart chain
 * Contract address: 0x10ED43C718714eb63d5aA57B78B54704E256024E
 *
 * 📘 Ethereum mainnet
 * Contract address: 0xEfF92A263d31888d860bD50809A8D171709b7b1c
 *
 * @dev Router interface
 */
interface IRouter {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB, uint256 liquidity);

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (uint256 amountToken, uint256 amountETH, uint256 liquidity);

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(
        uint256 amountIn,
        address[] calldata path
    ) external view returns (uint256[] memory amounts);

    function getAmountsIn(
        uint256 amountOut,
        address[] calldata path
    ) external view returns (uint256[] memory amounts);
}
"
    },
    "contracts/strategies/StrategyBase/StrategyManager.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "../../external/nimbus/IStakingRewards.sol";
import "../../external/tokens/ERC20TOKEN.sol";
import "../../external/exchanges/pancakeswap/IRouter.sol";
import "./IStrategy.sol";

/**
 * @author Pedro Reyes
 *
 * @dev This contract is used for mananging all the finance logic related to a strategy.
 * What you can manage from this contract is:
 * - The fee percentage
 * - The fee percentage precision
 * - The fee token
 * - The swap router
 * - The deposits lock time
 * - Rescue tokens
 * - Rescue ETH
 * - Set governance, finance, and operational roles
 */
contract StrategyManager is AccessControlEnumerable, ReentrancyGuard {
    /** @dev Income */
    uint256 public rewardFeePercentage;
    uint256 public depositFeeAmount;
    uint256 public withdrawalFeeAmount;
    uint256 public percentagePrecision;

    /** @dev Income setup */
    address public transactionFeeTokenPairAddress =
        0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c; // WBNB initially
    address public transactionFeeTokenAddress =
        0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56; // BUSD initially
    address public swapRouter = 0x10ED43C718714eb63d5aA57B78B54704E256024E; // PancakeSwap initially
    IRouter public exchangeSwap;

    /** @dev Strategy data */
    // User deposit data
    struct UserDeposit {
        uint256 amount;
        uint256 timestamp;
        uint256 blockNumber;
        uint256 depositFee;
    }

    // User withdrawal data
    struct UserWithdrawal {
        uint256 amount;
        uint256 timestamp;
        uint256 blockNumber;
        uint256 withdrawalFee;
    }

    // Strategy execution data
    struct StrategyExecutionData {
        address relayer;
        uint256 timestamp;
        uint256 blockNumber;
        uint256 rewardsReStaked;
        uint256 balanceStaked;
        uint256 rewardsReStakedFee;
    }

    /** @dev Roles array */
    string public constant FINANCE_ROLE_NAME = "FINANCE_ROLE";
    string public constant OPERATIONAL_ROLE_NAME = "OPERATIONAL_ROLE";
    string public constant GOVERNANCE_ROLE_NAME = "GOVERNANCE_ROLE";
    string[] public roles = [
        "DEFAULT_ADMIN_ROLE", // Defined by AccessControlEnumerable
        FINANCE_ROLE_NAME,
        OPERATIONAL_ROLE_NAME,
        GOVERNANCE_ROLE_NAME
    ];

    /** @dev FINANCE_ROLE is in charge of
     * - recovering ethers from the contract
     * - recovering tokens from the contract
     */
    bytes32 public constant FINANCE_ROLE = keccak256(bytes(FINANCE_ROLE_NAME));

    /** @dev Wallet in charge of receiving incomes from the strategy */
    address public financeRelayer;

    /** @dev OPERATIONAL_ROLE is in charge of
     * - executing the strategy
     */
    bytes32 public constant OPERATIONAL_ROLE =
        keccak256(bytes(OPERATIONAL_ROLE_NAME));

    /** @dev Wallet in charge of executing the strategy */
    address public operationalRelayer;

    /** @dev GOVERNANCE_ROLE is in charge of
     * - changing the operationalRelayer
     * - changing the rewardFeePercentage
     */
    bytes32 public constant GOVERNANCE_ROLE =
        keccak256(bytes(GOVERNANCE_ROLE_NAME));

    /** @dev Events related to income tracking - Governance role */
    event SwapRouterUpdate(address oldSwapRouter, address newSwapRouter);
    event PercentagePrecisionUpdate(
        uint256 oldPercentagePrecision,
        uint256 newPercentagePrecision
    );
    event RewardFeePercentageUpdate(
        uint256 oldRewardFeePercentage,
        uint256 newRewardFeePercentage
    );
    event DepositFeeAmountUpdate(
        uint256 oldDepositFeeAmount,
        uint256 newDepositFeeAmount
    );
    event WithdrawalFeeAmountUpdate(
        uint256 oldWithdrawalFeeAmount,
        uint256 newWithdrawalFeeAmount
    );
    event TransactionFeeTokenUpdate(address oldFeeToken, address newFeeToken);
    event TransactionFeeTokenPairUpdate(
        address oldFeePairToken,
        address newFeePairToken
    );
    event LockTimeUpdate(uint256 oldLockTime, uint256 newLockTime);

    /** @dev Events for tracking RBAC roles */
    event FinanceRelayerUpdate(
        address oldFinanceRelayer,
        address newFinanceRelayer
    );
    event OperationalRelayerUpdate(
        address oldOperationalRelayer,
        address newOperationalRelayer
    );

    /**
     * @notice Constructor function to initialize the role for reinvestments.
     * @dev This function is called by the user that wants to start this strategy
     */
    constructor(address _admin, address _operational, address _finance) {
        require(_admin != address(0), "Provide an admin address");
        require(_operational != address(0), "Provide an operational address");
        require(_finance != address(0), "Provide a finance address");

        operationalRelayer = _operational;
        financeRelayer = _finance;

        // Grant the roles to the specified addresses
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);
        _setupRole(GOVERNANCE_ROLE, _admin);
        _setupRole(OPERATIONAL_ROLE, operationalRelayer);
        _setupRole(FINANCE_ROLE, financeRelayer);

        // Set fee percentage
        rewardFeePercentage = 5 * 10 ** 18; // 5%

        // Set transaction fee
        depositFeeAmount = 1 * 10 ** 18; // 1 BUSD
        withdrawalFeeAmount = 1 * 10 ** 18; // 1 BUSD

        // Set percentage precision
        percentagePrecision = 100 * 10 ** 18; // 100 = 2 decimals
    }

    /** @dev Adding receive to allow the reception of ether */
    receive() external payable {
        // solhint-disable-previous-line no-empty-blocks
    }

    /** @dev Adding fallback to allow the reception of ether */
    fallback() external payable {
        // solhint-disable-previous-line no-empty-blocks
    }

    /** @dev Get list of roles */
    function getRoles() external view returns (string[] memory) {
        return roles;
    }

    /** @dev Set new deposit and withdrawal fee */
    function setTransactionFeeTokenAmount(
        uint256 newTransactionFeeAmount
    ) external onlyRole(GOVERNANCE_ROLE) {
        setDepositFeeTokenAmount(newTransactionFeeAmount);
        setWithdrawalFeeTokenAmount(newTransactionFeeAmount);
    }

    /** @dev Set new deposit fee */
    function setDepositFeeTokenAmount(
        uint256 newDepositFeeAmount
    ) public onlyRole(GOVERNANCE_ROLE) {
        uint256 oldDepositFeeAmount = depositFeeAmount;

        depositFeeAmount = newDepositFeeAmount;

        emit DepositFeeAmountUpdate(oldDepositFeeAmount, newDepositFeeAmount);
    }

    /** @dev Set new withdrawal fee */
    function setWithdrawalFeeTokenAmount(
        uint256 newWithdrawalFeeAmount
    ) public onlyRole(GOVERNANCE_ROLE) {
        uint256 oldWithdrawalFeeAmount = withdrawalFeeAmount;

        withdrawalFeeAmount = newWithdrawalFeeAmount;

        emit WithdrawalFeeAmountUpdate(
            oldWithdrawalFeeAmount,
            newWithdrawalFeeAmount
        );
    }

    /** @dev Set new fee token address pair */
    function setTransactionFeeTokenPair(
        address newTokenAddress
    ) external onlyRole(GOVERNANCE_ROLE) {
        require(newTokenAddress != address(0), "Zero address");

        address oldTransactionFeeTokenPairAddress = transactionFeeTokenPairAddress;

        transactionFeeTokenPairAddress = newTokenAddress;

        emit TransactionFeeTokenPairUpdate(
            oldTransactionFeeTokenPairAddress,
            newTokenAddress
        );
    }

    /** @dev Set new token fee address */
    function setTransactionFeeToken(
        address newTokenAddress
    ) external onlyRole(GOVERNANCE_ROLE) {
        require(newTokenAddress != address(0), "Zero address");

        address oldTransactionFeeTokenAddress = transactionFeeTokenAddress;

        transactionFeeTokenAddress = newTokenAddress;

        emit TransactionFeeTokenUpdate(
            oldTransactionFeeTokenAddress,
            newTokenAddress
        );
    }

    /** @dev Set the fee when user make a deposit */
    function setRewardFeePercentage(
        uint256 newRewardFeePercentage
    ) external onlyRole(GOVERNANCE_ROLE) {
        uint256 oldRewardFeePercentage = rewardFeePercentage;

        rewardFeePercentage = newRewardFeePercentage;

        emit RewardFeePercentageUpdate(
            oldRewardFeePercentage,
            newRewardFeePercentage
        );
    }

    /** @dev Set percentage precision */
    function setPercentagePrecision(
        uint256 newPercentagePrecision
    ) external onlyRole(GOVERNANCE_ROLE) {
        uint256 oldPercentagePrecision = percentagePrecision;

        percentagePrecision = newPercentagePrecision;

        emit PercentagePrecisionUpdate(
            oldPercentagePrecision,
            newPercentagePrecision
        );
    }

    /** @dev Update operational relayer */
    function setOperationalRelayer(
        address newOperationalRelayer
    ) external onlyRole(GOVERNANCE_ROLE) {
        require(newOperationalRelayer != address(0), "Zero address");

        revokeRole(OPERATIONAL_ROLE, operationalRelayer);
        grantRole(OPERATIONAL_ROLE, newOperationalRelayer);

        operationalRelayer = newOperationalRelayer;

        emit OperationalRelayerUpdate(
            operationalRelayer,
            newOperationalRelayer
        );
    }

    /** @dev Update finance relayer */
    function setFinanceRelayer(
        address newFinanceRelayer
    ) external onlyRole(GOVERNANCE_ROLE) {
        require(newFinanceRelayer != address(0), "Zero address");

        revokeRole(FINANCE_ROLE, financeRelayer);
        grantRole(FINANCE_ROLE, newFinanceRelayer);

        financeRelayer = newFinanceRelayer;

        emit FinanceRelayerUpdate(financeRelayer, newFinanceRelayer);
    }

    /** @dev Set new swap router */
    function setSwapRouter(
        address newSwapRouter
    ) external onlyRole(GOVERNANCE_ROLE) {
        require(newSwapRouter != address(0), "Zero address");

        address oldSwapRouter = swapRouter;

        swapRouter = newSwapRouter;

        exchangeSwap = IRouter(newSwapRouter);

        emit SwapRouterUpdate(oldSwapRouter, newSwapRouter);
    }

    /** @dev Get estimated ether for paying the transaction fee */
    function getEstimatedBNBforBUSD(
        uint256 stablecoinAmount
    ) external view returns (uint256[] memory) {
        address[] memory path = new address[](2);
        path[0] = transactionFeeTokenPairAddress;
        path[1] = transactionFeeTokenAddress;

        return exchangeSwap.getAmountsIn(stablecoinAmount, path);
    }

    /**
     * @dev Recover ether in the contract in case Firelabs or any
     * user sends tokens ERC20 here by mistake
     */
    function rescueERC20Token(
        address tokenAddress,
        uint256 tokens
    ) external onlyRole(FINANCE_ROLE) returns (bool success) {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    /**
     * @dev Recover ether in the contract in case Firelabs or any
     * user sends ether here by mistake
     */
    function rescueEther() external onlyRole(FINANCE_ROLE) {
        // slither-disable-next-line arbitrary-send-eth,low-level-calls
        (bool sent, ) = financeRelayer.call{value: address(this).balance}(""); // solhint-disable-line avoid-low-level-calls

        require(sent, "Failed to send Ether");
    }

    /// @dev Get fee for an amount of tokens
    /// @param amount - bigger than zero if sending ERC20 tokens
    function getStrategyFee(uint256 amount) public view returns (uint256) {
        return (amount * rewardFeePercentage) / (percentagePrecision);
    }

    /** @notice Pay fee per transaction (i.e. 1$) */
    function payTransactionFee(uint256 transactionFeeAmount) internal {
        if (transactionFeeAmount > 0) {
            require(
                msg.value > 0,
                "Provide ETH for paying the transaction fee"
            );

            // Path to swap BNB for BUSD
            address[] memory path = new address[](2);
            path[0] = address(transactionFeeTokenPairAddress);
            path[1] = address(transactionFeeTokenAddress);

            // Pay fee per transaction (i.e. 1$)
            uint256[] memory amounts = exchangeSwap.swapETHForExactTokens{
                value: msg.value
            }(
                transactionFeeAmount,
                path,
                financeRelayer,
                block.timestamp + 100 // solhint-disable-line not-rely-on-time
            );

            // Assert the transaction fee is paid
            require(
                amounts[1] >= transactionFeeAmount,
                "Transaction fee not paid"
            );
        }
    }
}
"
    },
