    "contracts/strategies/StrategyBase/IStrategy.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

/**
 * @dev Interface for a strategy. A strategy is a set of 3 methods.
 * The user can deposit and withdraw their assets. Firelabs can execute specific strategy.
 */
interface IStrategy {
    /** @notice User deposits to be part of the strategy */
    function executeDeposit(uint256 tokenAmount) external payable;

    /** @notice OPERATIONAL_ROLE executes the strategy to
     * reinvest the rewards from the NFT SmartStaker.sol.
     */
    function executeStrategy() external;

    /** @notice User unsubscribes from the strategy. */
    function executeWithdraw() external payable;
}
"
