    "src/SearcherBase.sol": {
      "content": "// SPDX-License-Identifier: GPL-2.0-or-later

/**
 * BaseContract for searchers
 * Designed by Wallchain in Metaverse.
 */

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";

abstract contract SearcherBase is Ownable {
    // Address to be invoked from as part of Wallchain's pipeline preparation.
    address public immutable trustedExecutionCapsule;
    // Address to be invoked from as part of Wallchain's pipeline execution.
    address public immutable trustedSearcherRequestCall;

    constructor(
        address _trustedExecutionCapsule,
        address _trustedSearcherRequestCall
    ) Ownable() {
        trustedExecutionCapsule = _trustedExecutionCapsule;
        trustedSearcherRequestCall = _trustedSearcherRequestCall;
    }

    // Should be applied to the searcher external main faction.
    modifier onlyTrustedRequestCall() {
        require(
            msg.sender == trustedSearcherRequestCall,
            "Msg sender is not trusted SearcherRequestCall"
        );
        _;
    }

    function prePayGas(uint256 gasAmount) external {
        require(
            msg.sender == trustedExecutionCapsule,
            "msg.sender not trusted"
        );
        (bool sent, ) = msg.sender.call{value: gasAmount}("");
        require(sent, "Gas PrePay Failed");
    }
}
"
    },
    "src/SearcherRequestCall.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.19;
import "./SearcherExecutionCapsule.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "lib/ExcessivelySafeCall/src/ExcessivelySafeCall.sol";

contract SearcherRequestCall {
    using ExcessivelySafeCall for address;
    SearcherExecutionCapsule public immutable trustedCapsule;
    IERC20 public immutable wethAddress;

    constructor(SearcherExecutionCapsule _trustedCapsule) {
        trustedCapsule = _trustedCapsule;
        wethAddress = trustedCapsule.wethAddress();
    }

    function executeSearcherCall(
        uint256 gas,
        address searcher,
        bytes calldata searcherData,
        uint256 bid
    ) external {
        require(
            msg.sender == address(trustedCapsule),
            "msg.sender is not trusted capsule"
        );
        uint256 balanceBefore = wethAddress.balanceOf(address(trustedCapsule));

        (bool success, ) = searcher.excessivelySafeCall(
            gas,
            0, // Value
            0, // Do not copy bytes to memory
            searcherData
        );
        require(success, "Searcher Call failed");

        require(
            balanceBefore + bid <=
                wethAddress.balanceOf(address(trustedCapsule)),
            "Bid was not payed"
        );
    }
}
"
