// File contracts/AngelFarming.sol

pragma solidity ^0.8.9;
pragma experimental ABIEncoderV2;



contract AngelFarming is Ownable, Pausable {
    using SafeMath for uint256;

    uint256 private constant MAGNITUDE = 2**128;
    IERC20 public erc20;

    struct Deposit {
        uint256 amount;
        uint256 initAmount;
        uint256 claimed;
        uint256 magnifiedRewardPerSecond;
        uint256 startRewardsAt;
    }

    mapping(address => Deposit) public deposits;

    uint256 public startReward;
    uint256 public rewardPeriod;

    address public treasury;

    constructor(
        address erc20_,
        address treasury_,
        uint256 startReward_,
        uint256 rewardPeriod_
    ) {
        erc20 = IERC20(erc20_);
        treasury = treasury_;
        startReward = startReward_;
        rewardPeriod = rewardPeriod_;
    }

// PUBLIC FUNCTIONS

    function claimReward() external whenNotPaused {
        Deposit storage deposit = deposits[msg.sender];
        require(deposit.amount > 0, "Deposit not found");

        if (deposit.initAmount == 0) {
            deposit.initAmount = startReward;
            deposit.magnifiedRewardPerSecond = deposit.amount.sub(startReward)
                .mul(MAGNITUDE).div(rewardPeriod);
        }

        uint256 amount;
        if (deposit.claimed == 0) {
            amount = deposit.initAmount;
            deposit.startRewardsAt = block.timestamp;
        }
        else {
            amount = _claimReward(deposit);
        }

        uint256 claimed = deposit.claimed.add(amount);

        if (claimed < deposit.amount) {
            deposit.claimed = claimed;
        }
        else {
            delete deposits[msg.sender];
        }

        _rewardSend(amount);
    }


// RESTRICTED FUNCTIONS

    function addDeposits(
        address[] memory accounts,
        uint256[] memory amounts,
        uint256[] memory claimed,
        uint256[] memory startRewards
    ) external onlyOwner {
        require(accounts.length == amounts.length
            && amounts.length == claimed.length
            && amounts.length == startRewards.length, "Data length mismatch");

        for(uint256 i; i < amounts.length; i++) {
            require(deposits[accounts[i]].claimed == 0, "Account already in proccess");
            require(amounts[i] > claimed[i], "amount must be grater than claimed");

            deposits[accounts[i]].amount = amounts[i];
            deposits[accounts[i]].claimed = claimed[i];
            deposits[accounts[i]].startRewardsAt = startRewards[i];
            if (deposits[accounts[i]].claimed > 0 && startRewards[i] == 0) {
                deposits[accounts[i]].startRewardsAt = block.timestamp;
            }
        }
    }

    function setPaused(bool value) external onlyOwner {
        if (value) {
            _pause();
        } else {
            _unpause();
        }
    }

    function withdrawTokens(uint256 amount) external onlyOwner {
        erc20.transfer(treasury, amount);
    }

// INTERNAL FUNCTIONS

    function _claimReward(Deposit storage deposit) internal view returns (uint256) {
        uint256 reward = deposit.initAmount;

        uint256 amount = block.timestamp.sub(deposit.startRewardsAt)
            .mul(deposit.magnifiedRewardPerSecond).div(MAGNITUDE);

        reward+= amount;
        if (reward >= deposit.amount) {
            reward = deposit.amount;
        }

        return reward.sub(deposit.claimed);
    }

    function _rewardSend(uint256 amount) internal {
        require(amount > 0, "Staking: Nothing to claim");

        erc20.transfer(msg.sender, amount);
    }
}
