        _box = IERC20Mintable(box);
    }

    function configsUm(uint16 _currentNumber) public onlyOwner {
        currentNumber = _currentNumber;
    }

    function configsFee(uint256 _fee) public onlyOwner {
        fee = _fee;
    }

