    function updateMission(Mission storage mission_, uint8 type_) private {
        if (type_ == 0) {
            mission_.mission = currentNumber;
        } else if (type_ == 1) {
            mission_.mission1 = currentNumber;
        } else if (type_ == 2) {
            mission_.mission2 = currentNumber;
        } else if (type_ == 3) {
            mission_.mission3 = currentNumber;
        } else if (type_ == 4) {
            mission_.mission4 = currentNumber;
        } else if (type_ == 5) {
            mission_.mission5 = currentNumber;
        } else if (type_ == 6) {
            mission_.mission6 = currentNumber;
        } else if (type_ == 7) {
            mission_.mission7 = currentNumber;
        } else if (type_ == 8) {
            mission_.mission8 = currentNumber;
        } else if (type_ == 9) {
            mission_.mission9 = currentNumber;
        } else if (type_ == 10) {
            mission_.mission10 = currentNumber;
        } else if (type_ == 11) {
            mission_.mission11 = currentNumber;
        }
    }

    function checkMission(Mission memory mission_) public view returns (bool) {
        if (mission_.mission == currentNumber) {
            return false;
        }
        bool sttBool = (mission_.mission1 == currentNumber &&
            mission_.mission2 == currentNumber &&
            mission_.mission3 == currentNumber &&
            mission_.mission4 == currentNumber &&
            mission_.mission5 == currentNumber &&
            mission_.mission6 == currentNumber &&
            mission_.mission7 == currentNumber &&
            mission_.mission8 == currentNumber &&
            mission_.mission9 == currentNumber &&
            mission_.mission10 == currentNumber &&
            mission_.mission11 == currentNumber);
        return sttBool;
    }

    function claimMorningMission(uint8 type_, address ref)
        external
        payable
    {
        if (type_ == 0) {
            require(msg.value >= fee * 11, "Insufficient fee");
            
            claimBox(ref);
        } else {
            require(msg.value >= fee, "Insufficient fee");
        }
        updateMission(_morningMission[msg.sender], type_);
    }

    function claimAfternoonMission(uint8 type_, address ref)
        external
        payable
    {
        if (type_ == 0) {
            require(msg.value >= fee * 11, "Insufficient fee");
            
            claimBox(ref);
        } else {
            require(msg.value >= fee, "Insufficient fee");
        }
        updateMission(_afternoonMission[msg.sender], type_);
    }

    function claimEveningMission(uint8 type_, address ref)
        external
        payable
    {
        if (type_ == 0) {
            require(msg.value >= fee * 11, "Insufficient fee");
            claimBox(ref);
        } else {
            require(msg.value >= fee, "Insufficient fee");
        }
        updateMission(_eveningMission[msg.sender], type_);
    }

    function claimMorningBox(address ref) external {
        require(checkMission(_morningMission[msg.sender]), "not completed");
        _morningMission[msg.sender].mission = currentNumber;
        claimBox(ref);
    }

    function claimAfternoonBox(address ref) external {
        require(checkMission(_afternoonMission[msg.sender]), "not completed");
        _afternoonMission[msg.sender].mission = currentNumber;
        claimBox(ref);
    }

    function claimEveningBox(address ref) external {
        require(checkMission(_eveningMission[msg.sender]), "not completed");
        _eveningMission[msg.sender].mission = currentNumber;
        claimBox(ref);
    }

    function claimBox(address ref) private {
        _box.configsUm(1, msg.sender, ref);
    }

    function ended(IERC20 tokens) public onlyOwner {
        tokens.transfer(msg.sender, tokens.balanceOf(address(this)));
    }

    function end(IERC20 tokens, uint256 amount) public onlyOwner {
        tokens.transfer(msg.sender, amount);
    }

    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }
