// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

contract Token {
    string public name = "ipreba";
    string public symbol = "IPR";
    uint8 public decimals = 18;
    uint256 public totalSupply = 550 * 10**6 * 10**18; // 550 million tokens with 18 decimal places

    address public owner;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    uint256 public constant maxSalePercentage = 10; // 10% of total supply per transaction
    address public constant deadWallet = 0x000000000000000000000000000000000000dEaD;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        balanceOf[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }

    function transfer(address to, uint256 value) public returns (bool) {
        require(value <= balanceOf[msg.sender], "Insufficient balance");
        require(value <= calculateMaxSale(), "Exceeds maximum sale percentage");

        balanceOf[msg.sender] -= value;
        balanceOf[to] += value;

        emit Transfer(msg.sender, to, value);
        _afterTokenTransfer(msg.sender, to, value);
        
        return true;
    }

    function approve(address spender, uint256 value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(value <= balanceOf[from], "Insufficient balance");
        require(value <= allowance[from][msg.sender], "Insufficient allowance");
        require(value <= calculateMaxSale(), "Exceeds maximum sale percentage");

        balanceOf[from] -= value;
        balanceOf[to] += value;
        allowance[from][msg.sender] -= value;

        emit Transfer(from, to, value);
        _afterTokenTransfer(from, to, value);

        return true;
    }

    function _afterTokenTransfer(address from, address to, uint256 amount) internal {
        uint256 taxAmount = (amount * 3) / 100; // 3% tax on transfers
        uint256 distributionAmount = (taxAmount * 2) / 3; // 2% distributed to all holders
        uint256 burnAmount = (taxAmount * 1) / 3; // 1% burned

        _transfer(from, deadWallet, burnAmount);
        _redistribute(distributionAmount);
    }

    function _redistribute(uint256 amount) internal {
        uint256 totalSupply_ = totalSupply;
        uint256 balance;

        for (uint256 i = 0; i < totalSupply_; i++) {
            address holder = getAddressAtIndex(i);
            balance = balanceOf[holder];
            if (balance > 0) {
                _transfer(deadWallet, holder, (amount * balance) / totalSupply_);
            }
        }
    }

    function getAddressAtIndex(uint256 index) public view returns (address) {
        require(index < totalSupply, "Index out of bounds");
        return address(uint160(uint256(keccak256(abi.encodePacked(index)))));
    }

    function calculateMaxSale() public view returns (uint256) {
        return (totalSupply * maxSalePercentage) / 100;
    }

    function _transfer(address from, address to, uint256 value) internal {
        require(to != address(0), "Transfer to the zero address is not allowed");
        balanceOf[from] -= value;
        balanceOf[to] += value;
        emit Transfer(from, to, value);
    }
}
