    "contracts/GROKI1.sol": {
      "content": "//SPDX-License-Identifier: MIT

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";

pragma solidity ^0.8.18;

contract GROKI1 is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    address public marketingWallet;
    address public devWallet;
    address public lpWallet;

    uint256 public buyMarketingFee;
    uint256 public buyDevFee;
    uint256 public buyLpFee;

    uint256 public sellMarketingFee;
    uint256 public sellDevFee;
    uint256 public sellLpFee;

    uint256 public marketingFeeAmount;
    uint256 public devFeeAmount;
    uint256 public lpFeeAmount;

    uint256 public buyFee;
    uint256 public sellFee;

    uint256 public maxWallet;
    uint256 public swapAmount;
    bool public swapEnabled;

    mapping(address => bool) public _isExcludedFromFees;
    mapping(address => bool) public _isExcludedFromWalletAmount;
    mapping(address => bool) public automatedMarketMakerPairs;

    event ExcludeFromFees(address indexed account, bool isExcluded);

    event MarketingWalletUpdated(
        address indexed oldWallet,
        address indexed newWallet
    );

    event DevWalletUpdated(
        address indexed oldWallet,
        address indexed newWallet
    );

    event LpWalletUpdated(address indexed oldWallet, address indexed newWallet);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    modifier checkMax(
        address from,
        address to,
        uint256 amount
    ) {
        if (
            from != owner() &&
            to != owner() &&
            to != address(0) &&
            to != address(0xdead) &&
            maxWallet > 0
        ) {
            if (!_isExcludedFromWalletAmount[to]) {
                require(
                    balanceOf(to) + amount < maxWallet,
                    "Max wallet exceeded!"
                );
            }
        }
        _;
    }

    constructor(
        address _uniSwapRouterAddress,
        address _marketingWallet,
        address _devWallet,
        address _lpWallet
    ) ERC20("GROKI1", "GR1") {
        uniswapV2Router = IUniswapV2Router02(_uniSwapRouterAddress);

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
                address(this),
                uniswapV2Router.WETH()
            );

        excludedFromWalletAmount(address(uniswapV2Pair), true);
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);

        lpWallet = _lpWallet;
        marketingWallet = _marketingWallet;
        devWallet = _devWallet;

        buyLpFee = 1;
        buyMarketingFee = 2;
        buyDevFee = 2;
        buyFee = buyDevFee + buyMarketingFee + buyLpFee;

        sellLpFee = 1;
        sellMarketingFee = 2;
        sellDevFee = 2;
        sellFee = sellLpFee + sellMarketingFee + sellDevFee;

        uint256 totalSupply = 100_000_000 ether;
        maxWallet = 1_000_000 ether;
        swapAmount = 5000 ether;
        swapEnabled = false;

        excludeFromFee(owner(), true);
        excludeFromFee(address(this), true);
        excludeFromFee(address(0xdead), true);

        excludedFromWalletAmount(owner(), true);
        excludedFromWalletAmount(address(this), true);
        excludedFromWalletAmount(address(0xdead), true);

        _mint(_msgSender(), totalSupply);
    }

    function excludeFromFee(address who, bool isEx) public onlyOwner {
        _isExcludedFromFees[who] = isEx;
        emit ExcludeFromFees(who, isEx);
    }

    function excludedFromWalletAmount(address who, bool isEx) public onlyOwner {
        _isExcludedFromWalletAmount[who] = isEx;
    }

    function setMaxWallet(uint256 newAm) public onlyOwner {
        maxWallet = newAm;
    }

    function setBuyFees(string memory name, uint256 newAm) public onlyOwner {
        if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("lp"))
        ) {
            require(
                sellFee + newAm + buyDevFee + buyMarketingFee < 20,
                "Total fee should be less then %20"
            );
            buyLpFee = newAm;
            buyFee = buyDevFee + buyMarketingFee + buyLpFee;
        } else if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("dev"))
        ) {
            require(
                sellFee + newAm + buyLpFee + buyMarketingFee < 20,
                "Total fee should be less then %20"
            );
            buyDevFee = newAm;
            buyFee = buyDevFee + buyMarketingFee + buyLpFee;
        } else if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("market"))
        ) {
            require(
                sellFee + newAm + buyLpFee + buyDevFee < 20,
                "Total fee should be less then %20"
            );
            buyMarketingFee = newAm;
            buyFee = buyDevFee + buyMarketingFee + buyLpFee;
        } else {
            revert("");
        }
    }

    function setSellFees(string memory name, uint256 newAm) public onlyOwner {
        if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("lp"))
        ) {
            require(
                buyFee + newAm + sellDevFee + sellMarketingFee < 20,
                "Total fee should be less then %20"
            );
            sellLpFee = newAm;
            sellFee = sellLpFee + sellMarketingFee + sellDevFee;
        } else if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("dev"))
        ) {
            require(
                buyFee + newAm + sellLpFee + sellMarketingFee < 20,
                "Total fee should be less then %20"
            );
            sellDevFee = newAm;
            sellFee = sellLpFee + sellMarketingFee + sellDevFee;
        } else if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("market"))
        ) {
            require(
                buyFee + newAm + sellLpFee + sellDevFee < 20,
                "Total fee should be less then %20"
            );
            sellMarketingFee = newAm;
            sellFee = sellLpFee + sellMarketingFee + sellDevFee;
        } else {
            revert();
        }
    }

    function setSwapAmount(uint256 newAm) public onlyOwner {
        swapAmount = newAm;
    }

    function setFeeWallet(string memory name, address newAdd) public onlyOwner {
        if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("lp"))
        ) {
            address oldWallet = lpWallet;
            lpWallet = newAdd;
            emit LpWalletUpdated(oldWallet, newAdd);
        } else if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("dev"))
        ) {
            address oldWallet = devWallet;
            devWallet = newAdd;
            emit DevWalletUpdated(oldWallet, newAdd);
        } else if (
            keccak256(abi.encodePacked(name)) ==
            keccak256(abi.encodePacked("market"))
        ) {
            address oldWallet = marketingWallet;
            marketingWallet = newAdd;
            emit MarketingWalletUpdated(oldWallet, newAdd);
        } else {
            revert();
        }
    }

    function releaseStuckTokens(address _tokenAddress) public onlyOwner {
        IERC20 token = IERC20(_tokenAddress);
        uint256 balance = token.balanceOf(address(this));
        if (balance > 0) {
            token.transfer(owner(), balance);
        }
    }

    function setAutomatedMarketMakerPair(
        address pair,
        bool value
    ) public onlyOwner {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override checkMax(from, to, amount) {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (
            (automatedMarketMakerPairs[to] &&
                !swapEnabled &&
                from != owner()) ||
            (automatedMarketMakerPairs[from] && !swapEnabled && to != owner())
        ) {
            revert("Swap disabled");
        }

        _swapBack(from, to);

        if (!_isExcludedFromFees[from] || !_isExcludedFromFees[to]) {
            amount = _safeFee(from, to, amount);
        }

        super._transfer(from, to, amount);
    }

    function _swapBack(address from, address to) internal {
        uint256 thisBalance = balanceOf(address(this));
        uint256 tokensToSwap = lpFeeAmount + devFeeAmount + marketingFeeAmount;
        bool swapAble = thisBalance >= swapAmount;
        bool success;

        if (thisBalance == 0 || tokensToSwap == 0) {
            return;
        }

        if (
            swapAble &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            uint256 initialBalance = address(this).balance;

            address[] memory path = new address[](2);
            path[0] = address(this);
            path[1] = uniswapV2Router.WETH();

            _approve(address(this), address(uniswapV2Router), tokensToSwap);

            uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
                tokensToSwap,
                0,
                path,
                address(this),
                block.timestamp
            );

            uint256 ethBalance = address(this).balance.sub(initialBalance);
            uint256 ethMarket = ethBalance.mul(marketingFeeAmount).div(
                tokensToSwap
            );
            uint256 ethDev = ethBalance.mul(devFeeAmount).div(tokensToSwap);

            marketingFeeAmount = 0;
            devFeeAmount = 0;
            lpFeeAmount = 0;

            (success, ) = address(devWallet).call{value: ethDev}("");
            (success, ) = address(marketingWallet).call{value: ethMarket}("");
            (success, ) = address(lpWallet).call{value: address(this).balance}(
                ""
            );
        }
    }

    function _safeFee(
        address from,
        address to,
        uint256 amount
    ) internal returns (uint256) {
        uint256 fee = 0;
        if (
            automatedMarketMakerPairs[to] &&
            sellFee > 0 &&
            !_isExcludedFromFees[from]
        ) {
            fee = (amount * sellFee) / 100;
            lpFeeAmount += (amount * sellLpFee) / 100;
            devFeeAmount += (amount * sellDevFee) / 100;
            marketingFeeAmount += (amount * sellMarketingFee) / 100;
        } else if (
            automatedMarketMakerPairs[from] &&
            buyFee > 0 &&
            !_isExcludedFromFees[to]
        ) {
            fee = (amount * buyFee) / 100;
            lpFeeAmount += (amount * buyLpFee) / 100;
            devFeeAmount += (amount * buyDevFee) / 100;
            marketingFeeAmount += (amount * buyMarketingFee) / 100;
        }

        if (fee > 0) {
            super._transfer(from, address(this), fee);
            amount -= fee;
        }
        return amount;
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;
        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function syncTotalFees() public onlyOwner {
        buyFee = buyDevFee + buyMarketingFee + buyLpFee;
        sellFee = sellDevFee + sellMarketingFee + sellLpFee;
    }

    function withdraw() public onlyOwner returns (bool) {
        bool success;
        (success, ) = address(owner()).call{value: address(this).balance}("");
        return success;
    }

    function enableSwap() public onlyOwner {
        swapEnabled = true;
    }

    receive() external payable {}
}
