    "contracts/MC.sol": {
      "content": "import "./IVesting.sol";
import "./interfaces/IERCFull20.sol";
import "./Supply.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract MasterChef is Ownable, ReentrancyGuard, Pausable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    struct DepositInfo {
        uint256 amount; // How many stake tokens the user has provided.
        uint256 weight; // weight of staked tokens
        uint256 timestamp; // the time at which user staked their tokens
        uint256 stakeUntill; // timestamp after which user can unstake
        uint256 stakeFor; // this will in days
        uint256 stakedBlock; // block of stake
        bool withdrawed;
    }

    // Info of each user.
    struct UserInfo {
        uint256 totalAmount; // How many LP tokens the user has provided.
        uint256 totalWeight;
        uint256 rewardDebt;
        DepositInfo[] deposits;
        //
        // We do some fancy math here. Basically, any point in time, the amount of CAKEs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accSmcwPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accSmcwPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    struct RewardsInfo {
        uint256 totalClaimed; // total rewards claimed
        uint256 totalUnstaked; // total tokens unstaked
        uint256 totalStaked; // total staked tokens
        uint256 totalWeight; // total weight of all tokens staked untill now.
        uint256 lastVest;
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 stakeToken; // Address of stake token contract.
        uint256 accSmcwPerShare; // Accumulated SMCWEs per share, times 1e12. See below.
        uint256 balance; // How many stake tokens the pool has.
        uint256 totalWeight; // total Weight of staked tokens
        uint256 lastRewardedBlock; // Last block number the user had their rewards calculated
    }

    mapping(uint256 => uint256) private daysToWeight;
    mapping(uint256 => uint256) private daysToTimestamp;

    // The TOKEN TOKEN!;
    IERC20 public token;
    IVesting public vesting;
    IERC20 public rewardingToken;
    Supply public supplyContract;

    uint256 private constant REWARDS_PRECISION = 1e18; // A big number to perform mul and div operations
    // SMCW tokens created per block.
    uint256 public tokenPerBlock;

    // Info of pool.
    PoolInfo public poolInfo;

    // Info of each user that stakes LP tokens.
    mapping (address => UserInfo) public userInfo;
    mapping (address => RewardsInfo) public rewardsInfo;

    // The block number when SMCW mining starts.
    uint256 public startBlock;

    // wallet => amount
    mapping(address => uint) public rewardsPerWallet;

    // event DepositInfo(address indexed user, uint256 indexed pid, uint256 amount);
    // event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);

    // event TokenPerBlockUpdate(uint256 tokenPerBlock);

    constructor(
        IERC20 _stakingToken,
        IERC20 _rewardingToken,
        address _vesting,
        uint256 _tokenPerBlock,
        address _supply
    ) public {

        token = _stakingToken;
        rewardingToken = _rewardingToken;
        vesting = IVesting(_vesting);
        supplyContract = Supply(_supply);
        tokenPerBlock = _tokenPerBlock;
        startBlock = block.number;


        poolInfo = PoolInfo({
            stakeToken: _stakingToken,
            balance: 0,
            totalWeight: 0,
            accSmcwPerShare: 0,
            lastRewardedBlock: block.number
        });

        daysToWeight[30] = 0.33 ether; // 0.33x
        daysToWeight[90] = 1 ether; // 1x
        daysToWeight[180] = 2 ether; // 2x
        daysToWeight[360] = 4 ether; // 4x

        daysToTimestamp[30] = 30 days;
        daysToTimestamp[90] = 90 days;
        daysToTimestamp[180] = 180 days;
        daysToTimestamp[360] = 360 days;

    }

    modifier onlyVester() {
        require(msg.sender == address(vesting));
        _;
    }

    modifier periods(uint256 _stakeFor) {
        require(
            (_stakeFor == 30) ||
                (_stakeFor == 90) ||
                (_stakeFor == 180) ||
                (_stakeFor == 360),
            "invalid staking period"
        );
        _;
    }

    function indexAt(uint256 index)
        public
        view
        returns (DepositInfo memory stakes)
    {
        stakes = userInfo[msg.sender].deposits[index];
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function updateTokenPerBlock(uint256 _amount) external onlyOwner {
        require(_amount <= tokenPerBlock + tokenPerBlock.div(5), "too large amount");
        updatePool();
        tokenPerBlock = _amount;
    }

    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from);
    }

    function getCurrentStaked(address user) public view returns (uint256) {
        return rewardsInfo[user].totalStaked;
    }

    function getCurrentClaimed(address user) public view returns (uint256) {
        return rewardsInfo[user].totalClaimed;
    }

    function getCurrentUnstaked(address user) public view returns (uint256) {
        return rewardsInfo[user].totalUnstaked;
    }

    function getStakes(address _user) public view returns (DepositInfo[] memory stakes) {
        stakes = userInfo[_user].deposits;
    }

    function getStakesLenght(address _user) public view returns (uint stakesLenght) {
        stakesLenght = userInfo[_user].deposits.length;
    }

    function pull(address user) external nonReentrant onlyVester returns (uint256 rewards) {
        harvestRewards(user);
        rewards = rewardsPerWallet[user];
        rewardsInfo[user].lastVest = block.number;
        rewardsPerWallet[user] = 0;
        rewardingToken.approve(address(vesting), rewards);
    }

    function claim(uint256 index) external nonReentrant {
        vesting.claimVestedTokens(msg.sender, index);
    }

    // View function to see pending CAKEs on frontend.
    // function pendingRewards(address _user) public view returns (uint256) {
    //     PoolInfo storage pool = poolInfo;
    //     UserInfo storage user = userInfo[_user];
    //     uint256 accSmcwPerShare = pool.accSmcwPerShare;
    //     uint256 lpSupply = rewardingToken.balanceOf(address(supplyContract));
    //     uint256 totalRewards;
    //     if (block.number > user.lastRewardBlock && lpSupply != 0 && user.totalAmount > 0) {
    //         uint256 multiplier = getMultiplier(user.lastRewardBlock, block.number);
    //         uint256 rewards = multiplier.mul(tokenPerBlock);
    //         accSmcwPerShare = accSmcwPerShare.add(rewards.mul(1e12)).div(pool.totalWeight);
    //         totalRewards = user.totalWeight.mul(accSmcwPerShare).div(1e12).sub(user.rewardDebt);
    //     }

    //     return totalRewards;
    // }

    function pendingRewards(address _user) public view returns (uint256) {
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[_user];

        uint256 accSmcwPerShare = pool.accSmcwPerShare;
        uint256 totalRewards;

        if (block.number > pool.lastRewardedBlock && pool.balance > 0) {
            uint256 blocksSinceLastReward = block.number - pool.lastRewardedBlock;
            uint256 rewards = blocksSinceLastReward * tokenPerBlock;
            accSmcwPerShare = accSmcwPerShare + (rewards * REWARDS_PRECISION / pool.balance);
        }

        totalRewards = (user.totalWeight * accSmcwPerShare / REWARDS_PRECISION) - user.rewardDebt;

        return totalRewards;
    }

    // Update reward variables of the given pool to be up-to-date.
    // function updatePool(address _user) public {
    //     PoolInfo storage pool = poolInfo;

    //     if (block.number <= userInfo[_user].lastRewardBlock) {
    //         return;
    //     }
    //     uint256 lpSupply = rewardingToken.balanceOf(address(supplyContract));
    //     if (lpSupply == 0) {
    //         userInfo[_user].lastRewardBlock = block.number;
    //         return;
    //     }

    //     uint256 rewards;
    //     rewards = pendingRewards(_user);
    //     rewardsPerWallet[_user] += rewards;
    //     if (pool.totalWeight > 0) {
    //         pool.accSmcwPerShare = pool.accSmcwPerShare.add(rewards.mul(1e12)).div(pool.totalWeight);
    //     }
    //     userInfo[_user].lastRewardBlock = block.number;
    // }

    function harvestRewards(address _user) private {
        updatePool();
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[_user];

        uint256 rewardsToHarvest = (user.totalWeight * pool.accSmcwPerShare / REWARDS_PRECISION) - user.rewardDebt;
        if (rewardsToHarvest == 0) {
            user.rewardDebt = user.totalWeight * pool.accSmcwPerShare / REWARDS_PRECISION;
            return;
        }
        rewardsPerWallet[_user] += rewardsToHarvest;
        user.rewardDebt = user.totalWeight * pool.accSmcwPerShare / REWARDS_PRECISION;
    }

    function updatePool() private {
        PoolInfo storage pool = poolInfo;
        if (pool.balance == 0) {
            pool.lastRewardedBlock = block.number;
            return;
        }
        uint256 blocksSinceLastReward = block.number - pool.lastRewardedBlock;
        uint256 rewards = blocksSinceLastReward * tokenPerBlock;
        pool.accSmcwPerShare = pool.accSmcwPerShare + (rewards * REWARDS_PRECISION / pool.balance);
        pool.lastRewardedBlock = block.number;
    }

    // DepositInfo LP tokens to MasterChef for SMCW allocation.
    function deposit(uint256 _amount, uint256 _stakeFor) 
        external
        nonReentrant
        whenNotPaused
        periods(_stakeFor)
    {
        require(_amount > 0, "Wrong amount");
        require(
            poolInfo.stakeToken.allowance(msg.sender, address(this)) >= _amount
        );
        UserInfo storage user = userInfo[msg.sender];
        PoolInfo storage pool = poolInfo;

        harvestRewards(msg.sender);

        DepositInfo memory deposit =
            DepositInfo({
                amount: _amount,
                stakeFor: _stakeFor,
                timestamp: block.timestamp,
                stakedBlock: block.number,
                weight: daysToWeight[_stakeFor].mul(_amount).div(REWARDS_PRECISION),
                stakeUntill: block.timestamp.add(daysToTimestamp[_stakeFor]),
                withdrawed: false
            });

        rewardsInfo[msg.sender].totalStaked += deposit.amount;
        rewardsInfo[msg.sender].totalWeight += deposit.weight;
        rewardsInfo[msg.sender].lastVest = block.number;

        pool.balance += _amount;
        pool.totalWeight += daysToWeight[_stakeFor].mul(_amount).div(REWARDS_PRECISION);

        user.totalAmount += _amount;
        user.totalWeight += daysToWeight[_stakeFor].mul(_amount).div(REWARDS_PRECISION);
        user.rewardDebt = user.totalWeight * pool.accSmcwPerShare / REWARDS_PRECISION;
        user.deposits.push(deposit);

        pool.stakeToken.safeTransferFrom(msg.sender, address(this), _amount);

        // emit DepositInfo(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 stakeIndex) external nonReentrant  {
        DepositInfo storage stake = userInfo[msg.sender].deposits[stakeIndex];
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[msg.sender];

        require(stake.withdrawed == false, "Already withdrawed");
        require(stake.stakeUntill < block.timestamp);

        stake.withdrawed = true;

        harvestRewards(msg.sender);

        rewardsInfo[msg.sender].totalStaked -= stake.amount;
        rewardsInfo[msg.sender].totalUnstaked += stake.amount;
        rewardsInfo[msg.sender].totalWeight -= stake.weight;

        user.totalAmount -= stake.amount;
        user.totalWeight -= stake.weight;
        pool.balance -= stake.amount;
        pool.totalWeight -= stake.weight;
        user.rewardDebt = user.totalWeight * pool.accSmcwPerShare / REWARDS_PRECISION;

        poolInfo.stakeToken.safeTransfer(msg.sender, stake.amount);
    }
}"
