contract CashVersePaymentGateway is Ownable {
    mapping(uint8 => address) coinlist;
    uint256 public exchange = 50;
    uint256 public earning = 50;
    IPancakeRouter02 public immutable pancakeV2Router =
        IPancakeRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    address collector = 0xFBDB960D1AdC8143FBf1a86fC3e915a0EBf32D1d;

    function changeExchange(uint256 percent) public onlyOwner {
        exchange = percent;
    }

    function changeCollector(address newcollector) public onlyOwner {
        collector = newcollector;
    }

    function changeEarning(uint256 percent) public onlyOwner {
        earning = percent;
    }

    function addToken(uint8 id, address tokenca) public onlyOwner {
        coinlist[id] = tokenca;
    }

    function swap_bnb_for_tokens(uint256 _amount, uint8 i) private {
        require(coinlist[i] != address(0), "Token not found");

        address[] memory path = new address[](2);
        path[0] = pancakeV2Router.WETH();
        path[1] = getcoin(i);

        pancakeV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: _amount
        }(0, path, address(this), block.timestamp);
    }

    function enter(uint8 tokenid) public payable {
        require(msg.value > 0);

        uint256 kauf = (msg.value * exchange) / 100;

        swap_bnb_for_tokens(kauf, tokenid);
        uint256 amount = address(this).balance;
        transferBNB(collector, amount);
    }

    function clearStuckBalance(uint256 amountPercentage) external onlyOwner {
        uint256 amount = address(this).balance;
        payable(msg.sender).transfer((amount * amountPercentage) / 100);
    }

    function withdrawToken(address tokenAddress) public onlyOwner {
        IERC20 token = IERC20(tokenAddress);
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }

    function getcoin(uint8 i) public view returns (address) {
        return coinlist[i];
    }

    function transferBNB(address recipient, uint256 amount) private {
        (bool success, ) = recipient.call{value: amount, gas: 250000}("");
        require(success, "BNB transfer failed");
    }
}
