// 此致敬礼

// Contract inscriptions shouldn't be supported, otherwise what's the meaning of the limit set by depoly? 
// Theoretically one person can mint out, if that's everyone should stop playing.

// Hope evm-ink team will hurry up and update the source code.

// respect


// claim方法 count是打的次数 inscription填 data:,{"p":"prc-20","op":"mint","tick":"你要打的铭文名称","amt":"数量"}

pragma solidity ^0.7.0;

contract BulkBRC20Mint {

    function claim(uint count,string calldata inscription) public {
        bytes memory inscriptionBytes = bytes(inscription);
        for (uint i = 0; i < count; i++) {
            (bool sent, ) = msg.sender.call{value:0}(inscriptionBytes);
            require(sent, "Failed to send");
        }
    }

}
