contract StakeHodlHands is Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        uint256 earnedReward;
        uint256[] tokenIds;
    }

    // Info of each pool.
    struct PoolInfo {
        IHODLHAND handToken;           // Address of LP token contract.
        uint256 lastRewardBlock;  // Last block number that CAKEs distribution occurs.
        uint256 accRewardPerShare; // Accumulated CAKEs per share, times 1e12. See below.
    }

    IHODLHAND public HodlHand;
    IBEP20 public rewardToken;

    // CAKE tokens created per block.
    uint256 public rewardPerBlock;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (address => UserInfo) public userInfo;

    // The block number when CAKE mining starts.
    uint256 public startBlock;
    // The block number when CAKE mining ends.
    uint256 public bonusEndBlock;

    uint256 public depositFee;
    uint256 public withdrawFee;

    IPancakeRouter private pancakeRouter = IPancakeRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address private BUSDaddress = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56;

    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 amount);

    constructor(
        IHODLHAND _hand,
        IBEP20 _rewardToken,
        uint256 _rewardPerBlock,
        uint256 _startBlock,
        uint256 _bonusEndBlock,
        uint256 _depositFee,
        uint256 _withdrawFee
    ) {
        HodlHand = _hand;
        rewardToken = _rewardToken;
        rewardPerBlock = _rewardPerBlock;
        startBlock = _startBlock;
        bonusEndBlock = _bonusEndBlock;

        // staking pool
        poolInfo.push(PoolInfo({
            handToken: _hand,
            lastRewardBlock: startBlock,
            accRewardPerShare: 0
        }));

        depositFee = _depositFee*1e16;
        withdrawFee = _withdrawFee*1e16;

    }

    function getUserInfo(address _user) external view returns(
        uint256 amount,
        uint256 rewardDebt,
        uint256 earnedReward,
        uint256[] memory tokenIDs
    ) {
        UserInfo memory user = userInfo[_user];
        return (
            user.amount,
            user.rewardDebt,
            user.earnedReward,
            user.tokenIds
        );
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        if (_to <= bonusEndBlock) {
            return _to.sub(_from);
        } else if (_from >= bonusEndBlock) {
            return 0;
        } else {
            return bonusEndBlock.sub(_from);
        }
    }

    // View function to see pending Reward on frontend.
    function pendingReward(address _user) external view returns (uint256) {
        UserInfo memory user = userInfo[_user];
        PoolInfo memory pool = poolInfo[0];
        uint256 accRewardPerShare = pool.accRewardPerShare;
        uint256 lpSupply = pool.handToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 tokenReward = multiplier.mul(rewardPerBlock);
            accRewardPerShare = accRewardPerShare.add(tokenReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accRewardPerShare).div(1e12).add(user.earnedReward).sub(user.rewardDebt);
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {
        PoolInfo storage pool = poolInfo[0];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.handToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 tokenReward = multiplier.mul(rewardPerBlock);
        pool.accRewardPerShare = pool.accRewardPerShare.add(tokenReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    error WrongDepositFee(uint256 required, uint256 passed);
    error WrongWithdrawFee(uint256 required, uint256 passed);

    // Stake HodlHand tokens to SmartChef
    function deposit(uint256 _amount) public payable {
        PoolInfo storage pool = poolInfo[0];

        uint256[] memory tokenId = pool.handToken.walletOfOwner(msg.sender);
        require(_amount > 0 && tokenId.length >= _amount, "Not enough hands!");

        if (depositFee > 0) {
            if (msg.value < getFee()) {
                revert WrongDepositFee({
                    required: getFee(),
                    passed: msg.value
                });
            }
        }

        UserInfo storage user = userInfo[msg.sender];
        updatePool();

        if (user.amount > 0) {
            user.earnedReward = user.amount.mul(pool.accRewardPerShare).div(1e12).add(user.earnedReward).sub(user.rewardDebt);
        }
 
        for(uint256 i; i < _amount; i++) {
            pool.handToken.transferFrom(address(msg.sender), address(this), tokenId[i]);
            user.tokenIds.push(tokenId[i]);
        }
        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);

        emit Deposit(msg.sender, _amount);
    }

    // Withdraw HodlHand tokens from STAKING.
    function withdraw(uint256 _amount) public payable {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        if (withdrawFee > 0) {
            if (msg.value < getWithdrawFee()) {
                revert WrongWithdrawFee({
                    required: getWithdrawFee(),
                    passed: msg.value
                });
            }
        }

        updatePool();
        uint256 pending = user.amount.mul(pool.accRewardPerShare).div(1e12).add(user.earnedReward).sub(user.rewardDebt);
        if(pending > 0) {
            rewardToken.safeTransfer(address(msg.sender), pending);
            user.earnedReward = 0;
        }
        if(_amount > 0) {
            if(user.amount >= _amount) {
                for(uint256 i; i < _amount; i++) {
                    pool.handToken.transferFrom(address(this), address(msg.sender), user.tokenIds[user.tokenIds.length - 1]);
                    user.tokenIds.pop();
                }
                user.amount = user.amount.sub(_amount);
            }
        }
        user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);

        emit Withdraw(msg.sender, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw() public payable {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[msg.sender];

        if (withdrawFee > 0) {
            if (msg.value < getWithdrawFee()) {
                revert WrongWithdrawFee({
                    required: getWithdrawFee(),
                    passed: msg.value
                });
            }
        }
        
        if (user.amount > 0) {
            for(uint256 i; i < user.amount; i++) {
                pool.handToken.transferFrom(address(this), address(msg.sender), user.tokenIds[user.tokenIds.length - 1]);
                user.tokenIds.pop();
            }
        }
        user.amount = 0;
        user.rewardDebt = 0;
        emit EmergencyWithdraw(msg.sender, user.amount);
    }

    function getFee() public view returns(uint256) {
        uint256 neededBNB;
        address[] memory path = new address[](2);
        path[0] = BUSDaddress;
        path[1] = pancakeRouter.WETH();
        neededBNB = pancakeRouter.getAmountsOut(depositFee, path)[1];
        return neededBNB;
    }

    function getWithdrawFee() public view returns(uint256) {
        uint256 neededBNB;
        address[] memory path = new address[](2);
        path[0] = BUSDaddress;
        path[1] = pancakeRouter.WETH();
        neededBNB = pancakeRouter.getAmountsOut(withdrawFee, path)[1];
        return neededBNB;
    }

    function changeDepositFee(uint256 _depositFee) external onlyOwner {
        depositFee = _depositFee*1e16;
    }

    function changeWithdrawFee(uint256 _withdrawFee) external onlyOwner {
        withdrawFee = _withdrawFee*1e16;
    }

    // Withdraw reward. EMERGENCY ONLY.
    function emergencyRewardWithdraw(uint256 _amount) public onlyOwner {
        require(_amount < rewardToken.balanceOf(address(this)), 'not enough token');
        rewardToken.safeTransfer(address(msg.sender), _amount);
    }

    function withdrawBNB() public payable onlyOwner {
        uint256 amount = address(this).balance;
        payable(owner()).transfer(amount);
    }

}
