pragma solidity ^0.8.17;


/// Agro Global staking contract

/// @Creator:     ZenithCore Labs - twitter.com/ZenithCoreLabs
/// @Developer:      0xAli - twitter.com/0xAli_

import "./Ownable.sol";
import "./IERC20.sol";
import "./SafeERC20.sol";

contract AgroGlobalStaking is Ownable {
    using SafeERC20 for IERC20;

    IERC20 public stakeToken; // token to stake
    IERC20 public rewardToken; // token for reward

    uint256 public totalStaked; // total staked
    uint256 public yearlyRewards; // yearly rewards
    uint256 public secondlyRewards; // secondly rewards
    uint256 public stakingStartTime; // staking start time
    uint256 public stakingEndTime; // staking end time

    uint256 public status; // 0 = inactive, 1 = active

    uint256 public secondsInYear = 31536000; // 365 days in seconds
    uint256 public secondsInDay = 86400; // 1 day in seconds
    uint256 public lockPeriod = 1296000; // 15 days in seconds (default)
    uint256 public emergencyExitFee = 10; // 10% emergency unstake fee

    address public emergencyFeeAddress; // emergency address


    //UserInfo
    mapping(address => uint256) public staked; // staker staked amount
    mapping(address => uint256) public stakedAt; // staker staked at time
    mapping(address => uint256) public rewards; // staker rewards
    mapping(address => uint256) public lastStaking; // last staking


    constructor (address _stakeToken, address _rewardToken) {
        stakeToken = IERC20(_stakeToken);
        rewardToken = IERC20(_rewardToken);
    }

    function addRewards(uint256 _amount) external onlyOwner {
        rewardToken.safeTransferFrom(msg.sender, address(this), _amount);
        yearlyRewards += _amount;
        secondlyRewards = yearlyRewards / secondsInYear;
    }

    function getRewards(uint256 _amount) external onlyOwner {
        rewardToken.safeTransfer(msg.sender, _amount);
        yearlyRewards -= _amount;
        secondlyRewards = yearlyRewards / secondsInYear;
    }

    function startStaking() external onlyOwner{
        require(status == 0, "Staking already started");
        stakingStartTime = block.timestamp;
        stakingEndTime = block.timestamp + 365 days;
        status = 1;
        totalStaked += 1;
    }

    function stopStaking() external onlyOwner{
        require(status == 1, "Staking already stopped");
        status = 0;
    }

    function setLockPeriod(uint256 _lockPeriod) external onlyOwner{
        lockPeriod = _lockPeriod * secondsInDay;
    }

    function setEmergencyExitFee(uint256 _emergencyExitFee) external onlyOwner{
        emergencyExitFee = _emergencyExitFee;
    }

    function changeEmergencyFeeAddress(address _address) external onlyOwner {
        emergencyFeeAddress = _address;
    }

    function stake(uint256 _amount) external {
        require(status == 1, "Staking not active");
        require(_amount > 0, "Amount must be greater than 0");
        require(stakingEndTime > block.timestamp, "Staking ended");

        stakeToken.safeTransferFrom(msg.sender, address(this), _amount);

        uint256 reward = calculateRewards();
        rewards[msg.sender] += reward;

        if (stakedAt[msg.sender] == 0){
            stakedAt[msg.sender] = block.timestamp;
        }

        lastStaking[msg.sender] = block.timestamp;

        staked[msg.sender] += _amount;    
        totalStaked += _amount;
    }

    function unstake()  external {
        require(status == 1, "Staking not active");
        require(staked[msg.sender] > 0, "Not staked");
        require(block.timestamp > stakedAt[msg.sender] + lockPeriod, "Please wait lock time");

        uint256 reward = calculateRewards();
        rewards[msg.sender] += reward;

        uint256 amount = staked[msg.sender];

        stakeToken.safeTransfer(msg.sender, amount);
        rewardToken.safeTransfer(msg.sender, reward);

        staked[msg.sender] = 0;
        stakedAt[msg.sender] = 0;
        lastStaking[msg.sender] = 0;
        rewards[msg.sender] = 0;
        totalStaked -= amount;
    }

    function emergencyUnstake() external {
        require(status == 1, "Staking not active");
        require(staked[msg.sender] > 0, "Not staked");

        uint256 amount = staked[msg.sender];
        uint256 fee = (amount * emergencyExitFee) / 100;

        stakeToken.safeTransfer(msg.sender, amount - fee);
        stakeToken.safeTransfer(emergencyFeeAddress, fee);

        staked[msg.sender] = 0;
        stakedAt[msg.sender] = 0;
        lastStaking[msg.sender] = 0;
        rewards[msg.sender] = 0;
        totalStaked -= amount;
    }

    function calculateRewards() public view returns (uint256) {
            uint256 rewardPerToken = (secondlyRewards * 1000000000000000000) / totalStaked;
            uint256 timeDiff = block.timestamp - lastStaking[msg.sender];
            uint256 reward = (rewardPerToken * staked[msg.sender] * timeDiff) / 1000000000000000000;

        return reward;
    }

    function calculateRewardByAddress (address _address) public view returns (uint256) {
        uint256 rewardPerToken = (secondlyRewards * 1000000000000000000) / totalStaked;
        uint256 timeDiff = block.timestamp - lastStaking[_address];
        uint256 reward = (rewardPerToken * staked[_address] * timeDiff) / 1000000000000000000;

        return reward;
    }

    function claimReward() public {
        uint256 reward = calculateRewards();
        rewards[msg.sender] += reward;
        require(rewards[msg.sender] > 0, "No rewards");

        rewardToken.transfer(_msgSender(), rewards[msg.sender]);
        lastStaking[msg.sender] = block.timestamp;
        rewards[msg.sender] = 0;
    }

    function checkEmergencyUnstake() public view returns (uint256) {
       if (stakedAt[msg.sender] + lockPeriod > block.timestamp) {
           return 1;
       }
       else {
           return 0;
       }
    }  

    function getRewardRateByToken() public view returns (uint256) {
        uint256 rewardPerToken = (secondlyRewards * 1000000000000000000) / totalStaked;
        return rewardPerToken/1000000000000000000;
    }

    function getUserEarned() public view returns (uint256){
        uint256 userRewards = rewards[msg.sender] + calculateRewardByAddress(msg.sender);
        return userRewards;
    }

    function getAprRate() public view returns (uint256) {
        //Yıllık Faiz = (Saniyede Verilen Toplam Ödül * 31,536,000) / Stake Edilen Toplam Token
        uint256 aprRate = (secondlyRewards * secondsInYear) / totalStaked;
        return aprRate;
    }

    function getStakingTimeLeft() public view returns (uint256) {
        uint256 stakingTimeLeft = (stakedAt[msg.sender] + lockPeriod) - block.timestamp;
        return stakingTimeLeft;
    }

    function calculateUserDailyRewards() public view returns (uint256){
        uint256 rewardPerToken = (secondlyRewards * 1000000000000000000) / totalStaked;
        return ((rewardPerToken * staked[msg.sender]) / 1000000000000000000) * secondsInDay;
    }

}"},"Context.sol":{"content":"// SPDX-License-Identifier: MIT
