    "src/tokens/RewardHolder.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.19;

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import {FixedPointMathLib} from "solmate/utils/FixedPointMathLib.sol";
import {Vault} from "../Vault.sol";

contract RewardHolder is Initializable, AccessControlUpgradeable, ReentrancyGuardUpgradeable {
    using FixedPointMathLib for uint256;

    /// @notice Emitted reward is claimed by a token owner
    event RewardClaimed(uint256 reward, address receiver);
    /// @notice Emitted when new rewards are deposited
    event RewardDeposited(address sender, uint value);
    /// @notice Emitted when a new owner is added
    event OwnerAdded(address owner, uint index);

    /// @notice Accumulator of the total earned interest rate since the opening of the token
    uint public rewardIndex;

    /// @notice The owners' reward indexes for eachas of the last time they accrued
    mapping(address => uint) public rewardOwnerIndex;

    /// @notice
    uint16 public numberCoins;

    Vault public vault;

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    /// See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
    uint256[50] private __gap;

    // 0xbb788f92e65e1a823e2c502bc4e7f9c3e55531bd56bfc7c0a895fb3ac9eb7716
    bytes32 public constant BALANCE_UPDATER_ROLE = keccak256("BALANCE_UPDATE_ROLE");

    /* ///////////////////////////// CONSTRUCTORS ///////////////////////////// */

    function __RewardHolder_init() internal onlyInitializing {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __RewardHolder_init_unchained();
    }

    function __RewardHolder_init_unchained() internal onlyInitializing {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        rewardIndex = 1;
    }

    /// @dev set vault
    /// @notice that is mandatory to be set before reward can be claimed
    function _setVault(Vault vault_) internal virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        if(address(vault) != address(0)) {
            revokeRole(BALANCE_UPDATER_ROLE, address(vault));
        }
        vault = vault_;
        grantRole(BALANCE_UPDATER_ROLE, address(vault));
    }

    /// @dev deposit reward to the contract to be claimed by token owners
    /// @notice only role with BALANCE_UPDATER_ROLE can call this function
    /// @param plusReward amount of reward to be deposited
    function depositReward(uint256 plusReward) external onlyRole(BALANCE_UPDATER_ROLE) nonReentrant {
        // update reward index for claim reward logic
        rewardIndex += plusReward;
        emit RewardDeposited(_msgSender(), plusReward);
    }

    /// @dev claim reward for token owner
    function claimReward() external nonReentrant {
        require(vault != Vault(address(0)), "Vault not set.");
        require(rewardOwnerIndex[msg.sender] != 0, "Caller doesn't have reward.");

        // calculate reward for token owner
        uint256 tokenOwnerReward = calcReward();
        rewardOwnerIndex[msg.sender] = rewardIndex;

        // transfer reward to token owner
        vault.transfer(msg.sender, tokenOwnerReward);
        emit RewardClaimed(tokenOwnerReward, address(msg.sender));
    }

    function calcReward() public view returns (uint256) {
        if(numberCoins == 0 || rewardOwnerIndex[msg.sender] == 0) {
            return 0;
        }
        uint deltaIndex = rewardIndex - rewardOwnerIndex[msg.sender];
        return deltaIndex / numberCoins;
    }

    /// @dev setup new owner for reward usually called when minting new token
    function setupNewOwner(address rewardOwner) internal virtual onlyRole(BALANCE_UPDATER_ROLE) {
        rewardOwnerIndex[rewardOwner] = rewardIndex;
        numberCoins++;
        emit OwnerAdded(rewardOwner, rewardIndex);
    }
}"
    },
    "src/tokens/SafeERC4626Upgradeable.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.19;

import {IERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import {SafeERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import {FixedPointMathLib} from "solmate/utils/FixedPointMathLib.sol";

import {ERC4626Upgradeable} from "./ERC4626Upgradeable.sol";

/// @title Safier and limited implementation of ERC-4626
/// @notice ERC-4626 standard allow deposit and withdraw not for message sender.
///  It commonly known issue, which hardly to test and much error prune.
///  Such interfaces caused vulnarabilities, which resulted in million dollars hacks.
///  On anther hand, this interfaces not have any use cases which cannot be implemented without `transferFrom` method.
///  This implementation prevent spends and allowances from any methods except transferFrom/send
///  Also main business logic simplified to reduce gas consumption.
abstract contract SafeERC4626Upgradeable is ERC4626Upgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using FixedPointMathLib for uint256;

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;

    // ------------------------------------------ Constructors ------------------------------------------

    /**
     * Constructor for the SafeERC4626Upgradeable contract
     * @param _asset which will be stored in this Vault
     * @dev `defaultOperators` may be an empty array.
     */
    function __SafeERC4626_init(
        IERC20Upgradeable _asset,
        string memory name_,
        string memory symbol_,
        address[] memory defaultOperators_
    ) internal onlyInitializing {
        __ERC4626_init(_asset, name_, symbol_, defaultOperators_);
    }

    /**
     * Unchained constructor for the SafeERC4626Upgradeable contract, without parents contracts init
     * @param _asset which will be stored in this Vault
     */
    function __SafeERC4626Upgradeable_init_unchained(IERC20Upgradeable _asset)
        internal
        onlyInitializing
    {
        __ERC4626_init_unchained(_asset);
    }

    /// @notice Mints the Vault shares for msg.sender, according to the number of deposited base tokens.
    /// - emits the Deposit event.
    /// - support ERC-20 approve / transferFrom on asset as a deposit flow.
    ///   MAY support an additional flow in which the underlying tokens are owned by the Vault contract
    ///   before the deposit execution, and are accounted for during deposit.
    /// - revert if all of assets cannot be deposited (due to deposit limit being reached, slippage, the user not approving enough underlying tokens to the Vault contract, etc).
    /// Note that most implementations will require pre-approval of the Vault with the Vault’s underlying asset token.
    function deposit(uint256 assets)
        public
        virtual
        nonReentrant
        returns (uint256 shares)
    {
        shares = previewDeposit(assets);
        // Check for rounding error since we round down in previewDeposit.
        require(shares != 0, "Given assets result in 0 shares.");

        _receiveAndDeposit(assets, shares, msg.sender);
    }

    /// @notice Mints exactly requested Vault shares to msg.sender by depositing any required amount of underlying tokens.
    /// - emits the Deposit event.
    /// - support ERC-20 approve / transferFrom on asset as a deposit flow.
    ///   MAY support an additional flow in which the underlying tokens are owned by the Vault contract
    ///   before the deposit execution, and are accounted for during deposit.
    /// - revert if all of assets cannot be deposited (due to deposit limit being reached, slippage, the user not approving enough underlying tokens to the Vault contract, etc).
    /// Note that most implementations will require pre-approval of the Vault with the Vault’s underlying asset token.
    function mint(uint256 shares)
        public
        virtual
        nonReentrant
        returns (uint256 assets)
    {
        // No need to check for rounding error, previewMint rounds up.
        assets = previewMint(shares);

        _receiveAndDeposit(assets, shares, msg.sender);
    }

    /// @notice Burns shares from msg.sender and sends exactly assets of underlying tokens to msg.sender.
    /// - emit the Withdraw event.
    /// - support a withdraw flow where the shares are burned from owner directly where owner is msg.sender.
    /// - MAY support an additional flow in which the shares are transferred to the Vault contract
    ///   before the withdraw execution, and are accounted for during withdraw.
    /// - revert if all of assets cannot be withdrawn (due to withdrawal limit being reached,
    ///   slippage, the owner not having enough shares, etc).
    /// Note that some implementations will require pre-requesting to the Vault
    /// before a withdrawal may be performed. Those methods should be performed separately.
    function withdraw(uint256 assets)
        public
        virtual
        nonReentrant
        returns (uint256 shares)
    {
        // No need to check for rounding error, previewWithdraw rounds up.
        shares = previewWithdraw(assets);

        _withdrawAndSend(assets, shares, msg.sender, msg.sender);
    }

    /// @notice Burns exactly shares from msg.sender and sends assets of underlying tokens to msg.sender.
    /// - emit the Withdraw event.
    /// - support a redeem flow where the shares are burned from owner directly where owner is msg.sender.
    /// - MAY support an additional flow in which the shares are transferred to the Vault contract
    ///   before the redeem execution, and are accounted for during redeem.
    /// - revert if all of shares cannot be redeemed (due to withdrawal limit being reached,
    ///   slippage, the owner not having enough shares, etc).
    /// Note that some implementations will require pre-requesting to the Vault
    /// before a withdrawal may be performed. Those methods should be performed separately.
    function redeem(uint256 shares)
        public
        virtual
        nonReentrant
        returns (uint256 assets)
    {
        assets = previewRedeem(shares);
        // Check for rounding error since we round down in previewRedeem.
        require(assets != 0, "Given shares result in 0 assets.");

        _withdrawAndSend(assets, shares, msg.sender, msg.sender);
    }

    /* //////////////////// Backwards compatible methods ////////////////////////// */

    /// @inheritdoc ERC4626Upgradeable
    function deposit(
        uint256 assets,
        address /* receiver */
    ) public virtual override returns (uint256 shares) {
        // nonReentrant under the hood
        return deposit(assets);
    }

    /// @inheritdoc ERC4626Upgradeable
    function mint(
        uint256 shares,
        address /* receiver */
    ) public virtual override returns (uint256 assets) {
        // nonReentrant under the hood
        return mint(shares);
    }

    /// @inheritdoc ERC4626Upgradeable
    function withdraw(
        uint256 assets,
        address, /* receiver */
        address /* owner */
    ) public virtual override returns (uint256 shares) {
        // nonReentrant under the hood
        return withdraw(assets);
    }

    /// @inheritdoc ERC4626Upgradeable
    function redeem(
        uint256 shares,
        address, /* receiver */
        address /* owner */
    ) public virtual override returns (uint256 assets) {
        // nonReentrant under the hood
        return redeem(shares);
    }
}
"
    },
    "src/tokens/VaultFounderToken.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.19;

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {IERC165Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/introspection/IERC165Upgradeable.sol";
import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import {ERC721URIStorageUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";

import {SafeInitializable} from "../upgradeable/SafeInitializable.sol";
import {SafeUUPSUpgradeable} from "../upgradeable/SafeUUPSUpgradeable.sol";
import {IVersionable} from "../upgradeable/IVersionable.sol";
import {ERC5484Upgradeable} from "./ERC5484Upgradeable.sol";
import {ERC4626Upgradeable} from "./ERC4626Upgradeable.sol";
import {IVaultFounderToken} from "./IVaultFounderToken.sol";
import {IVaultHook, ERC4626HookPayload} from "./IVaultHook.sol";
import {RewardHolder} from "./RewardHolder.sol";
import {Vault} from "../Vault.sol";

contract VaultFounderToken is IVaultFounderToken, SafeUUPSUpgradeable, ERC5484Upgradeable, IVaultHook, RewardHolder {

    uint256 public constant MAX_BPS = 10_000;

    // Max number of tokens that can be minted
    uint256 public maxCountTokens;

    // Next token price multiplier in percents
    uint256 public nextTokenPriceMultiplier;

    /// @dev return price for the next token
    uint256 public nextTokenPrice;

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    /// See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
    uint256[50] private __gap;

    /// @notice Emit when new token minted and token price updated.
    /// @param founder Address of the founder.
    /// @param requiredPrice Required price for the token.
    /// @param tokenPrice Price of the token.
    /// @param nextTokenPrice Price of the next token.
    /// @param nextTokenPriceMultiplier Next token price multiplier in percents.
    event FounderAdded(
        address indexed founder, 
        uint256 requiredPrice,
        uint256 tokenPrice, 
        uint256 nextTokenPrice, 
        uint256 nextTokenPriceMultiplier
    );

    /* ///////////////////////////// CONSTRUCTORS ///////////////////////////// */

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(bool needDisableInitializers) SafeInitializable(needDisableInitializers) {} // solhint-disable-line no-empty-blocks

    function initialize(
        uint256 maxCountTokens_,
        uint256 nextTokenPriceMultiplier_,
        uint256 initialTokenPrice_,
        string memory name_,
        string memory symbol_,
        Vault vault_
    ) public initializer {
        __SafeUUPSUpgradeable_init(); // owner init under the hood
        __ERC5484Upgradeable_init(name_, symbol_, BurnAuth.Neither, true); // __AccessControl_init inside
        __ReentrancyGuard_init();
        __RewardHolder_init_unchained(); // require __AccessControl_init

        maxCountTokens = maxCountTokens_;
        nextTokenPriceMultiplier = nextTokenPriceMultiplier_;
        nextTokenPrice = initialTokenPrice_;

        setVault(vault_);
    }

    /// @inheritdoc IVersionable
    function version() external pure returns (string memory) {
        return "0.2.1";
    }

    /// @dev set vault
    /// @notice that is mandatory to be set before reward can be claimed
    function setVault(Vault vault_) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if(address(vault) != address(0)) {
            revokeRole(MINTER_ROLE, address(vault_));
        }

        super._setVault(vault_);
        grantRole(MINTER_ROLE, address(vault_));
    }

    /// @dev function for mint new SBT token
    /// @param to address of user who will receive token
    /// @param uri token metadata uri
    /// @param amount holder balance, used as token price
    /// @return true if token was minted
    function tryToMint(address to, string memory uri, uint256 amount) public virtual onlyRole(MINTER_ROLE) returns(bool){
        if(amount < nextTokenPrice || totalSupply() >= maxCountTokens) {
            return false;
        }

        if(!_safeMint(to, uri)) { 
            return false;
        }

        setupNewOwner(to);
        uint256 _nextTokenPrice = amount * nextTokenPriceMultiplier / MAX_BPS;

        // Will be used to record price of current holder token in The Graph
        emit FounderAdded(to, nextTokenPrice, amount, _nextTokenPrice, nextTokenPriceMultiplier);

        nextTokenPrice = _nextTokenPrice;
        return true;
    }

    /// @dev set multiplicator for the next token in percents
    /// in case nextTokenPriceMultiplier_ = 13_000 the next price of the token will be: curPrice * 130%
    /// @param nextTokenPriceMultiplier_ persent multiplicator
    function setNextTokenMultiplier(uint256 nextTokenPriceMultiplier_) external onlyRole(DEFAULT_ADMIN_ROLE) {
        // the price of the previous token
        nextTokenPrice = nextTokenPrice * MAX_BPS / nextTokenPriceMultiplier;
        nextTokenPriceMultiplier = nextTokenPriceMultiplier_;
        // calculation the price with the new multiplier
        nextTokenPrice = nextTokenPrice * nextTokenPriceMultiplier_ / MAX_BPS;
    }

    /// @inheritdoc IVaultFounderToken
    function setTokenURI(string memory _tokenURI) external override {
        address tokenOwner = msg.sender;
        uint256 tokenId = tokenOfOwnerByIndex(tokenOwner, 0);
        _setTokenURI(tokenId, _tokenURI);
    }

    /// @inheritdoc IVaultFounderToken
    function setTokenURI(string memory _tokenURI, uint256 tokenId) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        _setTokenURI(tokenId, _tokenURI);
    }

    function afterDepositTrigger(ERC4626HookPayload memory request)
            external override
    {
        tryToMint(request.requestSender, "", request.senderMaxWithdraw);
    }

    /* solhint-disable no-empty-blocks */
    function beforeWithdrawTrigger(ERC4626HookPayload memory request) external override
    {
        //empty code
    }
    /* solhint-disable no-empty-blocks */

    /// @dev See {IERC165-supportsInterface}.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC5484Upgradeable, AccessControlUpgradeable, IERC165Upgradeable)
        virtual
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}"
    },
