// ***********************************************************************************************
// ***********************************************************************************************
// ***********************************************************************************************
abstract contract AdminRights {
    bytes32 public constant POOL_ADMIN = keccak256("POOL_ADMIN");
}

// ***********************************************************************************************
// ***********************************************************************************************
// ***********************************************************************************************
contract GTS_Pool is Ownable, AccessControl, AdminRights, Initializable {
    using Address  for address;
    using Strings  for uint;
    using EnumerableSet for EnumerableSet.AddressSet;

    // v 0.01 ******************************************************************
    string public version;

    struct singleHoldersEntry {
        address user;
        uint256 amount;
    }

    enum PoolState {PAUSED, DEPOSIT_USD, DISTR_TOKENS, EMERGENCY, FINISHED}
    PoolState public state = PoolState.PAUSED;

    mapping(address => uint256) private holdersUSDBalance;
    mapping(address => uint256) private holdersTokenBalance;

    EnumerableSet.AddressSet private holdersUSD_addresses;
    EnumerableSet.AddressSet private holdersToken_addresses;

    address public usdAddress;
    address public tokenAddress;

    string public projectName;

    uint256 public minDepositAmount;
    uint256 public maxDepositAmount;
    uint256 public stepDepositAmount;
    uint256 public maxPoolAmount;
    uint256 public currentPoolAmount;
    uint256 public currentUnclaimedTokens;

    // v 0.02 ******************************************************************
    // some new STATIC variables
    // uint foo;

    // **********************************************************************
    // **********************************************************************
    address constant dead_address = 0x000000000000000000000000000000000000dEaD;
    address constant zero_address = 0x0000000000000000000000000000000000000000;

    // **********************************************************************
    event USDDeposited(uint256, address, uint256);
    event USDTransfered(uint256, address, uint256);
    event UserUSDTransfered(uint256, address, address, uint256);

    event TokensDistributedToHolder(uint256, address, uint256);
    event TotalTokensDistributed(uint256, uint256, uint256);
    event TokensClaimed(uint256, address, uint256);

    event StateChanged(uint256, address, PoolState);

    // **********************************************************************
    // FOR Clones FACTORY

    function initialize() public initializer {
        version = "3.01";

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());  // FABRIC to be able setup roles
        grantRole(POOL_ADMIN, _msgSender());           // FABRIC to setup name

        state = PoolState.PAUSED;

        // !!! TEST TEST TEST !!! ==========================================
        // set_all_parameters(0x0000000000000000000000000000000000000000, 
        // 0x0000000000000000000000000000000000000000, 
        // 10, 100, 10, 1000);

        // state = PoolState.DEPOSIT_USD;
        // !!! TEST TEST TEST !!! ==========================================
    }
    
    // **********************************************************************
    constructor() { }

    // **********************************************************************
    // You have to Call USD_Token.approve(THIS_CONTRACT_ADDRESS, 0xFF..FF ) ! in the FRONTEND
    // **********************************************************************
    function depositUSD(uint amount) public {
        require(state == PoolState.DEPOSIT_USD, "DEPO: state != DEPOSIT_USD");
        require(amount >= minDepositAmount, "DEPO: amount < minDepoAmount");
        require(amount + holdersUSDBalance[msg.sender] <= maxDepositAmount, "DEPO: maxDepoAmount reached");
        require(currentPoolAmount+amount <= maxPoolAmount, "DEPO: Max Pool Reached");
        require(amount % stepDepositAmount == 0, "DEPO: Step not agreed");

        require(IERC20(usdAddress).allowance(msg.sender, address(this)) >= amount, "DEPO: ALLOW ERROR");
        require(IERC20(usdAddress).transferFrom(msg.sender, address(this), amount), "DEPO: TRANSFER ERROR");

        holdersUSDBalance[msg.sender] += amount;
        currentPoolAmount += amount;
        require(IERC20(usdAddress).balanceOf(address(this)) == currentPoolAmount, "DEPO: USD_Contract_Balance != PoolAmount");

        holdersUSD_addresses.add(msg.sender);

        emit USDDeposited(block.timestamp, msg.sender, amount);
    }

    function transferUSDtoMe() public onlyRole(POOL_ADMIN){
        address _pool = _msgSender();
        require(!_pool.isContract(), "TRANS: POOL_ADMIN only!");

        uint balance = IERC20(usdAddress).balanceOf(address(this));

        require(state == PoolState.DEPOSIT_USD, "TRANS: state != DEPOSIT_USD");
        require(balance != 0, "TRANS: balance == 0");

        require(IERC20(usdAddress).transfer(payable(_pool), balance), "TRANS: TRANSFER ERROR");
        
        currentPoolAmount = 0;
        state = PoolState.DISTR_TOKENS; // ???

        emit USDTransfered(block.timestamp, _pool, balance);
    }

    function transferUserUSDtoMe(address user) public onlyRole(POOL_ADMIN){
        address _pool = _msgSender();
        require(!_pool.isContract(), "TRANS: POOL_ADMIN only!");

        uint balance = IERC20(usdAddress).balanceOf(address(this));
        uint user_balance = holdersUSDBalance[user];

        require(state == PoolState.DEPOSIT_USD, "TRANS: state != DEPOSIT_USD");
        require(user_balance != 0, "TRANS: balance == 0");
        require(balance > user_balance, "TRANS: balance < user_balance");

        require(IERC20(usdAddress).transfer(payable(_pool), user_balance), "TRANS: TRANSFER ERROR");
        
        currentPoolAmount -= user_balance;
        holdersUSDBalance[user] = 0;

        holdersUSD_addresses.remove(user);

        emit UserUSDTransfered(block.timestamp, _pool, user, user_balance);
    }

    // **********************************************************************
    // You have to send TOKENS to THIS CONTRACT BEFORE CALL
    // **********************************************************************
    function distrTokensToHolders(singleHoldersEntry[] memory _data) public onlyRole(POOL_ADMIN){
        require(state == PoolState.DISTR_TOKENS, "DISTR: state != DISTR_TOKENS");

        uint256 this_balance = IERC20(tokenAddress).balanceOf(address(this));
        require( this_balance != 0, "DISTR: Contract balance == 0");

        uint256 _summHolders;
        for(uint i = 0; i<_data.length; i++){
            require(!Address.isContract(_data[i].user), "DISTR: contract address disallowed");
            _summHolders += _data[i].amount;
        }
        require(currentUnclaimedTokens + _summHolders == this_balance, "DISTR: Balance != sum_holders");

        for(uint i = 0; i<_data.length; i++){
            holdersTokenBalance[_data[i].user] += _data[i].amount;
            emit TokensDistributedToHolder(block.timestamp, _data[i].user, _data[i].amount);
        }
        currentUnclaimedTokens += _summHolders;

        emit TotalTokensDistributed(block.timestamp, _data.length, _summHolders);
    }

    function claimTokens() public {
        require(state == PoolState.DISTR_TOKENS, "CLAIM: state != DISTR_TOKENS");
        require(!Address.isContract(_msgSender()), "CLAIM: NO from contract address.");

        uint amount = holdersTokenBalance[msg.sender];
        require( amount != 0, "CLAIM: Already Claimed");
        require( IERC20(tokenAddress).balanceOf(address(this)) >= amount, "CLAIM: balance < amount");

        require(IERC20(tokenAddress).transfer(msg.sender, holdersTokenBalance[msg.sender]),"CLAIM: Transfer ERROR");

        holdersTokenBalance[msg.sender] = 0;
        currentUnclaimedTokens -= amount;

        emit TokensClaimed(block.timestamp, msg.sender, amount);
    }

    // **********************************************************************
    function getUserUSDAmount(address user) public view returns(uint256) {
        return holdersUSDBalance[user];
    }

    function getUserTokenAmount(address user) public view returns(uint256) {
        return holdersTokenBalance[user];
    }

    // **********************************************************************
    // **********************************************************************
    function set_state(PoolState _value) public onlyRole(POOL_ADMIN){
        state = _value;
        emit StateChanged(block.timestamp, msg.sender, _value);
    }
    function set_minDepositAmount(uint256 _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        minDepositAmount = _value;
    }
    function set_maxDepositAmount(uint256 _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        maxDepositAmount = _value;
    }
    function set_stepDepositAmount(uint256 _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        stepDepositAmount = _value;
    }
    function set_maxPoolAmount(uint256 _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        maxPoolAmount = _value;
    }
    function set_usdAddress(address _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        usdAddress = _value;
    }
    function set_tokenAddress(address _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        tokenAddress = _value;
    }
    function set_projectName(string memory _value) public onlyRole(POOL_ADMIN){
        require(state == PoolState.PAUSED, "state != PAUSED");
        projectName = _value;
    }
    // **********************************************************************
    function set_all_parameters(address _usdAddress, address _tokenAddress, 
                                uint256 _minDepositAmount, uint256 _maxDepositAmount, uint256 _stepDepositAmount,
                                uint256 _maxPoolAmount) public onlyRole(POOL_ADMIN){
        
        // if (bytes(_name).length != 0) { set_projectName(_name); }

        if (_usdAddress != address(0))   { set_usdAddress(_usdAddress);}
        if (_tokenAddress != address(0)) { set_tokenAddress(_tokenAddress);}

        if (_minDepositAmount != 0)  {set_minDepositAmount(_minDepositAmount);}
        if (_maxDepositAmount != 0)  {set_maxDepositAmount(_maxDepositAmount);}
        if (_stepDepositAmount != 0) {set_stepDepositAmount(_stepDepositAmount);}

        if (_maxPoolAmount != 0)     {set_maxPoolAmount(_maxPoolAmount);}
    }

    // **********************************************************************
    // **********************************************************************
    function _emergencyWithdraw(address _token) private {
        require(state == PoolState.EMERGENCY, "state != EMERGENCY");

        uint256 _balance = IERC20(_token).balanceOf(address(this));
        require(IERC20(_token).transfer(msg.sender, _balance),"TRANSFER ERROR");
    }

    function emergencyWithdrawTokens() public onlyRole(POOL_ADMIN){
        _emergencyWithdraw(tokenAddress);
    }

    function emergencyWithdrawAnyTokens(address _tokenAddress) public onlyRole(POOL_ADMIN){
        _emergencyWithdraw(_tokenAddress);
    }

    function emergencyWithdrawUSD() public onlyRole(POOL_ADMIN){
        _emergencyWithdraw(usdAddress);
    }

    function emergencyWithdrawETH() public onlyRole(POOL_ADMIN){
        require(state == PoolState.EMERGENCY, "state != EMERGENCY");
        payable(msg.sender).transfer(address(this).balance);
    }

    // **********************************************************************
    function get_USDHolders() public view returns (singleHoldersEntry[] memory) {
        uint256 _length = holdersUSD_addresses.length();

        singleHoldersEntry[] memory ret = new singleHoldersEntry[](_length);

        address[] memory _holders = holdersUSD_addresses.values();

        for (uint i; i<_length; i++){
            singleHoldersEntry memory _entry = singleHoldersEntry(_holders[i], holdersUSDBalance[_holders[i]]);
            ret[i] = _entry;
        }

        return ret;
    }

    // **********************************************************************
    function get_TokenHolders() public view returns (singleHoldersEntry[] memory) {
        uint256 _length = holdersToken_addresses.length();

        singleHoldersEntry[] memory ret = new singleHoldersEntry[](_length);

        address[] memory _holders = holdersToken_addresses.values();

        for (uint i; i<_length; i++){
            singleHoldersEntry memory _entry = singleHoldersEntry(_holders[i], holdersTokenBalance[_holders[i]]);
            ret[i] = _entry;
        }

        return ret;
    }

    // **********************************************************************
    // **********************************************************************

}


/**
 * @dev https://eips.ethereum.org/EIPS/eip-1167[EIP 1167] is a standard for
 * deploying minimal proxy contracts, also known as "clones".
 *
 * > To simply and cheaply clone contract functionality in an immutable way, this standard specifies
 * > a minimal bytecode implementation that delegates all calls to a known, fixed address.
 *
 * The library includes functions to deploy a proxy using either `create` (traditional deployment) or `create2`
 * (salted deterministic deployment). It also includes functions to predict the addresses of clones deployed using the
 * deterministic method.
 *
 * _Available since v3.4._
 */
library Clones {
    /**
     * @dev Deploys and returns the address of a clone that mimics the behaviour of `implementation`.
     *
     * This function uses the create opcode, which should never revert.
     */
    function clone(address implementation) internal returns (address instance) {
        assembly {
            let ptr := mload(0x40)
            mstore(ptr, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
            mstore(add(ptr, 0x14), shl(0x60, implementation))
            mstore(add(ptr, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
            instance := create(0, ptr, 0x37)
        }
        require(instance != address(0), "ERC1167: create failed");
    }

    /**
     * @dev Deploys and returns the address of a clone that mimics the behaviour of `implementation`.
     *
     * This function uses the create2 opcode and a `salt` to deterministically deploy
     * the clone. Using the same `implementation` and `salt` multiple time will revert, since
     * the clones cannot be deployed twice at the same address.
     */
    function cloneDeterministic(address implementation, bytes32 salt) internal returns (address instance) {
        assembly {
            let ptr := mload(0x40)
            mstore(ptr, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
            mstore(add(ptr, 0x14), shl(0x60, implementation))
            mstore(add(ptr, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
            instance := create2(0, ptr, 0x37, salt)
        }
        require(instance != address(0), "ERC1167: create2 failed");
    }

    /**
     * @dev Computes the address of a clone deployed using {Clones-cloneDeterministic}.
     */
    function predictDeterministicAddress(
        address implementation,
        bytes32 salt,
        address deployer
    ) internal pure returns (address predicted) {
        assembly {
            let ptr := mload(0x40)
            mstore(ptr, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
            mstore(add(ptr, 0x14), shl(0x60, implementation))
            mstore(add(ptr, 0x28), 0x5af43d82803e903d91602b57fd5bf3ff00000000000000000000000000000000)
            mstore(add(ptr, 0x38), shl(0x60, deployer))
            mstore(add(ptr, 0x4c), salt)
            mstore(add(ptr, 0x6c), keccak256(ptr, 0x37))
            predicted := keccak256(add(ptr, 0x37), 0x55)
        }
    }

    /**
     * @dev Computes the address of a clone deployed using {Clones-cloneDeterministic}.
     */
    function predictDeterministicAddress(address implementation, bytes32 salt)
        internal
        view
        returns (address predicted)
    {
        return predictDeterministicAddress(implementation, salt, address(this));
    }
}


