// ***********************************************************************************************
contract GTS_Pools_Factory is Ownable, AccessControl, AdminRights {
    using StorageSlot for uint256;

    // *************************************************************************
    // v 0.01 ******************************************************************
    string public version;
    bytes32 internal _INIT_SLOT;

    mapping(string => GTS_Pool) private gts_pools;
    string[] private pool_names;

    address[4] private pool_admins;

    address private implementation;

    // v 0.02 ******************************************************************
    // SOME STATIC VARS

    // **********************************************************************
    // **********************************************************************
    event PoolCreated(string, GTS_Pool);
    event Initialized(string);


    // **********************************************************************
    // **********************************************************************
    function initialize() public returns (address){
        version = "Factory_3.01";

        _INIT_SLOT = bytes32(uint256(keccak256(abi.encodePacked("eip1967.proxy.implementation", version))) - 1);
        uint256 _inited = StorageSlot.getUint256Slot(_INIT_SLOT).value;
        require(_inited == 0, "Contract version already inited.");
        StorageSlot.getUint256Slot(_INIT_SLOT).value = 1;

        pool_admins = [
            0x73E3A5d8a07BdeE3CdBCc988e755271B4eb3D98a,
            0xbF20586f55c13519181e15744064f01e1AcA3a50
        ];

        // console.log("FACTORY msg_sender: ", _msgSender());
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        for (uint i=0; i<pool_admins.length; i++){
            // console.log("adm - ", pool_admins[i]);
            grantRole(POOL_ADMIN, pool_admins[i]);
        }
        _revokeRole(DEFAULT_ADMIN_ROLE, _msgSender());

        emit Initialized(version);

        implementation = address(new GTS_Pool());
        return implementation;
    }

    // **********************************************************************
    // **********************************************************************
    constructor() {
        // initialize();
    }

    // **********************************************************************
    function createPool(string memory pool_name) public onlyRole(POOL_ADMIN) returns (GTS_Pool) {
        GTS_Pool pool = GTS_Pool(Clones.clone(implementation));

        pool.initialize(); // set ADMIN + DEF ADMIN for this contract
        
        for (uint i; i<pool_admins.length; i++){
            pool.grantRole(POOL_ADMIN, pool_admins[i]);
        }
        
        pool.set_projectName(pool_name);

        // console.log(address(this));

        pool.revokeRole(POOL_ADMIN, address(this));
        pool.revokeRole(DEFAULT_ADMIN_ROLE, address(this));
        // pool.transferOwnership(msg.sender);

        pool_names.push(pool_name);
        gts_pools[pool_name] = pool;

        emit PoolCreated(pool_name, pool);
        return pool;
    }

    // **********************************************************************
    function get_pool_names() public view returns (string[] memory) {
        string[] memory res = pool_names;
        return res;        
    }

    // **********************************************************************
    function get_pool_address(string memory name) public view returns (GTS_Pool) {
        return gts_pools[name];
    }

    // **********************************************************************
    function get_implementation() public view returns (address) {
        return implementation;
    }

}

