interface IStaking {
    struct UserInfo {
        uint256 amount;
        int256 rewardDebt;
        uint256 lastDepositedAt;
    }
    function userInfo(address account) view external returns (UserInfo memory);
    function lpToken() view external returns (IERC20);
}

contract BeteroGovernance is Ownable {
    using SafeMath for uint256;
    using Address for address;

    IERC20 BTEToken = IERC20(0x7faAf8d4C411218415d9d3f82D56214658349dbb);

    uint256 public immutable VOTE_DOWN      = 1;
    uint256 public immutable VOTE_NEUTRAL   = 2;
    uint256 public immutable VOTE_UP        = 3;

    struct Proposal {
        string name;
        uint256 startTime;
        uint256 endTime;
        uint256 noVoteDown;
        uint256 noNeutral;
        uint256 noVoteUp;
        uint256 finalResult; //1 failed, 2 neutral, 3 successful
    }
    uint256 public currentProposal;
    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public voteInfo;

    address[] public lpStakingContracts;
    address[] public tokenStakingContracts;
    mapping(address => bool) public isLpStakingContracts;
    mapping(address => bool) public isTokenStakingContracts;

    function vote(uint256 selection) public {
        require(proposals[currentProposal].startTime < block.timestamp, "Not active time");
        require(proposals[currentProposal].endTime > block.timestamp, "Not active time");
        require(voteInfo[currentProposal][msg.sender] == false, "Already voted");
        require(selection <= VOTE_UP && selection >= VOTE_DOWN, "Incorrect selection");

        voteInfo[currentProposal][msg.sender] = true;

        if(selection == VOTE_DOWN){
            proposals[currentProposal].noVoteDown += calculateVotingPower(msg.sender);
        }else if(selection == VOTE_NEUTRAL){
            proposals[currentProposal].noNeutral += calculateVotingPower(msg.sender);
        }else{
            proposals[currentProposal].noVoteUp += calculateVotingPower(msg.sender);
        }
    }

    function finalize() public {
        if(proposals[currentProposal].endTime <= block.timestamp && proposals[currentProposal].finalResult == 0){
            if(proposals[currentProposal].noVoteDown == proposals[currentProposal].noVoteUp){
                proposals[currentProposal].finalResult = VOTE_NEUTRAL; // Neutral
            }else if(proposals[currentProposal].noVoteDown > proposals[currentProposal].noVoteUp){
                proposals[currentProposal].finalResult = VOTE_DOWN; // Fail
            }else{
                proposals[currentProposal].finalResult = VOTE_UP; // Success
            }
        }
    }

    function propose(string memory _proposalName, uint256 _startTime, uint256 _endTime) public onlyOwner{
        require(proposals[currentProposal].endTime <= block.timestamp, "Last campaign hasn't finished yet");
        require(_startTime >= block.timestamp && _startTime < block.timestamp + 7 days, "Start time must start within a week from now");
        require(_endTime > _startTime && _endTime < _startTime + 30 days, "Proposal must finish within 30 days after starting");
        // Finally the last one if it hasn't been finallized yet
        finalize();
        // New proposal
        currentProposal                         += 1;
        proposals[currentProposal].name         = _proposalName;
        proposals[currentProposal].startTime    = _startTime;
        proposals[currentProposal].endTime      = _endTime;
    }

    function addLpStakingContract(address _stakingAddress) public onlyOwner{
        require(isLpStakingContracts[_stakingAddress] == false, "Adready in staking list");
        require(_stakingAddress.isContract(), "Must be contract");
        isLpStakingContracts[_stakingAddress] = true;
        lpStakingContracts.push(_stakingAddress);
    }

    function removeLpStakingContract(address _stakingAddress) public onlyOwner{
        require(isLpStakingContracts[_stakingAddress] == true, "Not in staking list");
        require(_stakingAddress.isContract(), "Must be contract");
        isLpStakingContracts[_stakingAddress] = false;
        for (uint256 i = 0; i < lpStakingContracts.length; i++) {
            if (lpStakingContracts[i] == _stakingAddress) {
                lpStakingContracts[i] = lpStakingContracts[lpStakingContracts.length - 1];
                lpStakingContracts.pop();
                break;
            }
        }
    }

    function addTokenStakingContract(address _stakingAddress) public onlyOwner{
        require(isTokenStakingContracts[_stakingAddress] == false, "Adready in staking list");
        require(_stakingAddress.isContract(), "Must be contract");
        isTokenStakingContracts[_stakingAddress] = true;
        tokenStakingContracts.push(_stakingAddress);
    }

    function removeTokenStakingContract(address _stakingAddress) public onlyOwner{
        require(isTokenStakingContracts[_stakingAddress] == true, "Not in staking list");
        require(_stakingAddress.isContract(), "Must be contract");
        isTokenStakingContracts[_stakingAddress] = false;
        for (uint256 i = 0; i < tokenStakingContracts.length; i++) {
            if (tokenStakingContracts[i] == _stakingAddress) {
                tokenStakingContracts[i] = tokenStakingContracts[tokenStakingContracts.length - 1];
                tokenStakingContracts.pop();
                break;
            }
        }
    }

    function getTokenPerLP(address lpContractAddress) private view returns(uint256){
        IERC20 lpToken = IERC20(lpContractAddress);
        uint256 totalLPSupply = lpToken.totalSupply();
        uint256 totalBTEInLP = BTEToken.balanceOf(lpContractAddress);
        return totalBTEInLP.div(totalLPSupply);
    }

    function calculateVotingPower(address account) public view returns(uint256){
        uint256 yourPower = 0; //BTEToken.balanceOf(account);

        // Calculate token in lp staking pool
        for (uint256 i = 0; i < lpStakingContracts.length; i++) {
            IStaking.UserInfo memory infor = IStaking(lpStakingContracts[i]).userInfo(account);
            yourPower = yourPower.add(infor.amount.mul(getTokenPerLP(address(IStaking(lpStakingContracts[i]).lpToken()))));
        }

        // Calculate token in single staking pool
        for (uint256 i = 0; i < tokenStakingContracts.length; i++) {
            IStaking.UserInfo memory infor = IStaking(tokenStakingContracts[i]).userInfo(account);
            yourPower = yourPower.add(infor.amount);
        }

        return yourPower;
    }
}
