contract PepeTV is IERC20, Ownable {
    using SafeMath for uint256;
    // fees. all uint8 for gas efficiency and storage.
    /* @dev   
        all fees are set with 1 decimal places added, please remember this when setting fees.
    */
    // denominator. uint 16 for storage efficiency - makes the above fees all to 1 dp.

    // trading control;
    bool public canTrade = false;

    // tokenomics - uint256 BN but located here fro storage efficiency
    uint256 _totalSupply = 4 * 10 ** 14 * (10 ** _decimals); //400 tril
    uint256 public _maxTxAmount = _totalSupply / 50; // 2%
    uint256 public _maxHoldAmount = _totalSupply / 50; // 2%
    uint256 public swapThreshold = _totalSupply / 500; // 0.2%
    uint256 public buyFee = 50;
    uint256 public sellFee = 50;
    uint16 public AllfeeDenominator = 1000;

    //Important addresses
    address DEAD = 0x000000000000000000000000000000000000dEaD;
    address ZERO = 0x0000000000000000000000000000000000000000;

    address public marketingFeeReceiver =
        0xC440acBa8A15Ad1257a8DDC02cc2387944E59622;

    address public pair;

    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) _allowances;

    mapping(address => bool) public pairs;

    mapping(address => bool) public isFeeExempt;
    mapping(address => bool) public isTxLimitExempt;
    mapping(address => bool) public isMaxHoldExempt;

    IDEXRouter public router;

    bool public swapEnabled = false;
    bool inSwap;

    modifier swapping() {
        inSwap = true;
        _;
        inSwap = false;
    }

    string constant _name = "PepeTV";
    string constant _symbol = "$PEPETV";
    uint8 constant _decimals = 18;

    constructor() {
        isMaxHoldExempt[marketingFeeReceiver] = true;
        isMaxHoldExempt[DEAD] = true;
        isMaxHoldExempt[ZERO] = true;
        isMaxHoldExempt[owner()] = true;
        isMaxHoldExempt[address(this)] = true;

        isTxLimitExempt[owner()] = true;
        isTxLimitExempt[marketingFeeReceiver] = true;
        isTxLimitExempt[address(this)] = true;
        isTxLimitExempt[DEAD] = true;
        isTxLimitExempt[ZERO] = true;

        isFeeExempt[owner()] = true;
        isFeeExempt[marketingFeeReceiver] = true;
        isFeeExempt[address(this)] = true;
        isFeeExempt[DEAD] = true;
        isFeeExempt[ZERO] = true;

        _balances[owner()] = _totalSupply;

        emit Transfer(address(0), owner(), _totalSupply);
    }

    function initialize() external payable onlyOwner {
        router = IDEXRouter(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D); //Mainnet Uniswap
        pair = IDEXFactory(router.factory()).createPair(
            router.WETH(),
            address(this)
        ); // ETH pair

        pairs[pair] = true;
        isMaxHoldExempt[pair] = true;

        _allowances[address(this)][address(router)] = type(uint256).max;

        router.addLiquidityETH{value: msg.value}(
            address(this),
            balanceOf(address(this)),
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    receive() external payable {}

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    function decimals() external pure override returns (uint8) {
        return _decimals;
    }

    function symbol() external pure override returns (string memory) {
        return _symbol;
    }

    function name() external pure override returns (string memory) {
        return _name;
    }

    function getOwner() external view override returns (address) {
        return owner();
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(
        address holder,
        address spender
    ) external view override returns (uint256) {
        return _allowances[holder][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function setSwapThresholdDivisor(uint divisor) external onlyOwner {
        require(divisor >= 100, "PepeTV: max sell percent is 1%");
        swapThreshold = _totalSupply / divisor;
    }

    function approveMax(address spender) external returns (bool) {
        return approve(spender, _totalSupply);
    }

    function setmaxholdpercentage(uint256 percentageMul10) external onlyOwner {
        require(
            percentageMul10 >= 5,
            "PepeTV, max hold cannot be less than 0.5%"
        ); // cant change percentage below 0.5%, so everyone can hold the percentage
        _maxHoldAmount = (_totalSupply * percentageMul10) / 1000; // percentage based on amount
    }

    function allowtrading() external onlyOwner {
        canTrade = true;
        swapEnabled = true;
    }

    function removeLimits() external onlyOwner {
        _maxTxAmount = type(uint256).max;
        _maxHoldAmount = type(uint256).max;
    }

    function addNewPair(address newPair) external onlyOwner {
        pairs[newPair] = true;
        isMaxHoldExempt[newPair] = true;
    }

    function removePair(address pairToRemove) external onlyOwner {
        pairs[pairToRemove] = false;
        isMaxHoldExempt[pairToRemove] = false;
    }

    function transfer(
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        return _transferFrom(msg.sender, recipient, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        if (_allowances[sender][msg.sender] != uint256(_totalSupply)) {
            _allowances[sender][msg.sender] = _allowances[sender][msg.sender]
                .sub(amount, "Insufficient Allowance");
        }

        return _transferFrom(sender, recipient, amount);
    }

    function _transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        if (!canTrade) {
            require(
                isFeeExempt[sender] || isFeeExempt[recipient],
                "PepeTV, Only owner() or presale Contract allowed to add LP"
            ); // only owner() allowed to trade or add liquidity
        }

        if (inSwap || !swapEnabled) {
            return _basicTransfer(sender, recipient, amount);
        }

        if (sender != owner() && recipient != owner()) {
            if (!pairs[recipient] && !isMaxHoldExempt[recipient]) {
                require(
                    balanceOf(recipient) + amount <= _maxHoldAmount,
                    "PepeTV, cant hold more than max hold dude, sorry"
                );
            }
        }

        checkTxLimit(sender, recipient, amount);

        if (shouldSwapBack(sender, recipient)) {
            swapBack();
        }

        uint256 amountReceived = takeFee(sender, recipient, amount);

        _balances[sender] = _balances[sender].sub(
            amount,
            "PepeTV, Insufficient Balance"
        );

        _balances[recipient] = _balances[recipient].add(amountReceived);

        emit Transfer(sender, recipient, amountReceived);
        return true;
    }

    function _basicTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(
            amount,
            "Insufficient Balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function checkTxLimit(
        address sender,
        address reciever,
        uint256 amount
    ) internal view {
        if (sender != owner() && reciever != owner()) {
            require(
                amount <= _maxTxAmount || isTxLimitExempt[sender],
                "TX Limit Exceeded"
            );
        }
    }

    function shouldTakeFee(address endpt) internal view returns (bool) {
        return !isFeeExempt[endpt];
    }

    function takeFee(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (uint256) {
        uint fee = AllfeeDenominator;
        address feeReceiver = address(this);
        if (isFeeExempt[sender]) {
            feeReceiver = sender;
        } else if (pairs[sender]) {
            fee = buyFee;
        } else if (pairs[recipient]) {
            fee = sellFee;
        } else {
            fee = 0;
        }

        uint256 feeAmount = amount.mul(fee).div(AllfeeDenominator);
        if (feeAmount > 0) {
            _balances[feeReceiver] = _balances[feeReceiver].add(feeAmount);
            emit Transfer(sender, feeReceiver, feeAmount);
            feeAmount = 0;
        }

        return amount.sub(feeAmount);
    }

    // returns any mis-sent tokens to the marketing wallet
    function claimtokensback(IERC20 tokenAddress) external onlyOwner {
        payable(marketingFeeReceiver).transfer(address(this).balance);
        tokenAddress.transfer(
            marketingFeeReceiver,
            tokenAddress.balanceOf(address(this))
        );
    }

    function setTxLimit(uint256 amount) external onlyOwner {
        require(
            amount >= _totalSupply / 200,
            "PepeTV, must be higher than 0.5%"
        );
        require(
            amount > _maxTxAmount,
            "PepeTV, can only ever increase the tx limit"
        );
        _maxTxAmount = amount;
    }

    function setIsFeeExempt(address holder, bool exempt) external onlyOwner {
        isFeeExempt[holder] = exempt;
    }

    function setIsTxLimitExempt(
        address holder,
        bool exempt
    ) external onlyOwner {
        isTxLimitExempt[holder] = exempt;
    }

    /*
    Dev sets the individual buy fees
    */
    function swapBack() internal swapping {
        uint256 amountToSwap = swapThreshold; // swap everything bar the liquidity tokens. we need to add a pair

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountToSwap,
            0,
            path,
            address(this),
            block.timestamp + 100
        );

        uint256 amountETH = address(this).balance;

        if (amountETH > 0) {
            payable(marketingFeeReceiver).transfer(amountETH);
        }
    }

    function setFeeReceivers(address _marketingFeeReceiver) external onlyOwner {
        marketingFeeReceiver = _marketingFeeReceiver;
    }

    function setSwapBackSettings(
        bool _enabled,
        uint256 _amount
    ) external onlyOwner {
        swapEnabled = _enabled;
        swapThreshold = _amount;
    }

    function shouldSwapBack(
        address sender,
        address recipient
    ) internal view returns (bool) {
        return
            !inSwap &&
            swapEnabled &&
            pairs[recipient] &&
            _balances[address(this)] >= swapThreshold &&
            !isFeeExempt[sender] &&
            !isFeeExempt[recipient];
    }

    function getCirculatingSupply() public view returns (uint256) {
        return _totalSupply.sub(balanceOf(DEAD)).sub(balanceOf(ZERO));
    }

    event AutoLiquify(uint256 amountPairToken, uint256 amountToken);
}
