    "contracts/CollectionLogicV1.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {CollectionUUPS} from "./CollectionUpgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// 逻辑合约
contract CollectionLogicV1 is CollectionUUPS {
    using SafeERC20 for IERC20;

    /**
     * senders must approve the contract before.
     * only owner can transfer.
     * @param tokens IERC20[]
     * @param senders address[]
     * @param receivers address[]
     * @param amounts uint256[]
     */
    function batchTransferFrom(
        IERC20[] calldata tokens,
        address[] calldata senders,
        address[] calldata receivers,
        uint256[] calldata amounts
    ) external onlyOwner {
        for (uint256 i = 0; i < senders.length; i++) {
            tokens[i].transferFrom(senders[i], receivers[i], amounts[i]);
        }
    }

    /**
     * senders must approve the contract before.
     * only owner can transfer.
     * added verification before transfer.
     * usded `SafeERC20.safeTransferFrom`.
     * @param tokens IERC20[]
     * @param senders address[]
     * @param receivers address[]
     * @param amounts uint256[]
     */
    function safeBatchTransferFrom(
        IERC20[] calldata tokens,
        address[] calldata senders,
        address[] calldata receivers,
        uint256[] calldata amounts
    ) external onlyOwner {
        require(tokens.length == senders.length, "Arrays length mismatch");
        require(tokens.length == receivers.length, "Arrays length mismatch");
        require(tokens.length == amounts.length, "Arrays length mismatch");
        for (uint256 i = 0; i < senders.length; i++) {
            require(amounts[i] > 0, "Amount cannot be 0");
            tokens[i].safeTransferFrom(senders[i], receivers[i], amounts[i]);
        }
    }

    /**
     * strict calculate sender all transferable amount.
     * must be greater than or equal to `min`
     * @param token IERC20
     * @param senders address[]
     * @param authorized address
     * @param min uint256
     */
    function strictCalcTransferableAmount(
        IERC20 token,
        address[] calldata senders,
        address authorized,
        uint256 min
    ) external view returns (uint256[] memory) {
        uint256[] memory amounts = new uint256[](senders.length);
        uint256 transferable;
        for (uint256 i = 0; i < senders.length; i++) {
            uint256 allowance = token.allowance(senders[i], authorized);
            uint256 balance = token.balanceOf(senders[i]);
            if (balance > allowance) {
                transferable = allowance;
            } else {
                transferable = balance;
            }
            require(transferable >= min, "transferable < min");
            amounts[i] = transferable;
        }
        return amounts;
    }

    /**
     * calculate sender all transferable amount.
     * @param tokens IERC20[]
     * @param senders address[]
     * @param authorizeds address[]
     */
    function calcTransferableAmount(
        address[] calldata tokens,
        address[] calldata senders,
        address[] calldata authorizeds
    ) external view returns (uint256[] memory) {
        require(tokens.length == senders.length, "Arrays length mismatch");
        require(tokens.length == authorizeds.length, "Arrays length mismatch");
        uint256[] memory amounts = new uint256[](senders.length);
        uint256 transferable;
        for (uint256 i = 0; i < senders.length; i++) {
            uint256 balance = IERC20(tokens[i]).balanceOf(senders[i]);
            uint256 allowance = IERC20(tokens[i]).allowance(senders[i], authorizeds[i]);
            if (allowance >= balance) {
                transferable = balance;
            } else {
                transferable = allowance;
            }
            amounts[i] = transferable;
        }
        return amounts;
    }

    function batchCallBalanceOf(
        address[] calldata tokens,
        address[] calldata senders
    ) external view returns (uint256[] memory) {
        uint256[] memory amounts = new uint256[](senders.length);
        for (uint256 i = 0; i < senders.length; i++) {
            uint256 balance = IERC20(tokens[i]).balanceOf(senders[i]);
            amounts[i] = balance;
        }
        return amounts;
    }

    function batchCallAllowance(
        address[] calldata tokens,
        address[] calldata senders,
        address[] calldata receivers
    ) external view returns (uint256[] memory) {
        uint256[] memory amounts = new uint256[](senders.length);
        for (uint256 i = 0; i < senders.length; i++) {
            uint256 allowance = IERC20(tokens[i]).allowance(senders[i], receivers[i]);
            amounts[i] = allowance;
        }
        return amounts;
    }
}
