pragma solidity ^0.8.20;

import {UUPSUpgradeable} from "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";

// 最小代理合约
contract MinimalProxy {
    address[] public created;
    uint256 public totalCreated;

    function clone(address target) external returns (address result) {
        bytes20 targetBytes = bytes20(target);
        assembly {
            let clone := mload(0x40)
            mstore(clone, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
            mstore(add(clone, 0x14), targetBytes)
            mstore(add(clone, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
            result := create(0, clone, 0x37)
        }
        created.push(result);
        totalCreated++;
    }

    function lastCreated() external view returns (address) {
        return created[totalCreated - 1];
    }
}

// 所有合约必须继承
contract CollectionStorageStructure {
    address public implementation;
    address public owner;
    bool public initialized;
}

// 使用合约
contract CollectionBasic is CollectionStorageStructure {
    function initialize(address _implementation) external {
        require(!initialized, "already initialized");
        initialized = true;
        implementation = _implementation;
        owner = msg.sender;
    }

    function _delegate(address _implementation) internal virtual {
        assembly {
            // Copy msg.data. We take full control of memory in this inline assembly
            // block because it will not return to Solidity code. We overwrite the
            // Solidity scratch pad at memory position 0.

            // calldatacopy(t, f, s) - copy s bytes from calldata at position f to mem at position t
            // calldatasize() - size of call data in bytes
            calldatacopy(0, 0, calldatasize())

            // Call the implementation.
            // out and outsize are 0 because we don't know the size yet.

            // delegatecall(g, a, in, insize, out, outsize) -
            // - call contract at address a
            // - with input mem[in…(in+insize))
            // - providing g gas
            // - and output area mem[out…(out+outsize))
            // - returning 0 on error (eg. out of gas) and 1 on success
            let result := delegatecall(gas(), _implementation, 0, calldatasize(), 0, 0)

            // Copy the returned data.
            // returndatacopy(t, f, s) - copy s bytes from returndata at position f to mem at position t
            // returndatasize() - size of the last returndata
            returndatacopy(0, 0, returndatasize())

            switch result
            // delegatecall returns 0 on error.
            case 0 {
                // revert(p, s) - end execution, revert state changes, return data mem[p…(p+s))
                revert(0, returndatasize())
            }
            default {
                // return(p, s) - end execution, return data mem[p…(p+s))
                return(0, returndatasize())
            }
        }
    }

    function _fallback() private {
        _delegate(implementation);
    }

    fallback() external payable {
        _fallback();
    }

    receive() external payable {
        _fallback();
    }
}

// 逻辑合约必须继承
contract CollectionUUPS is CollectionStorageStructure, UUPSUpgradeable {
    function _authorizeUpgrade(address newImplementation) internal view override onlyOwner {
        // Perform additional authorization checks if needed
        require(msg.sender == owner, "not authorized");
        require(newImplementation != address(0), "Upgrade to the zero address is not allowed");
    }

    function upgradeTo(address _implementation) external {
        _authorizeUpgrade(_implementation);
        implementation = _implementation;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "ZeroAddress");
        owner = newOwner;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "OwnableUnauthorizedAccount");
        _;
    }
}
