    "fair.sol": {
      "content": "// SPDX-License-Identifier: MIT;
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "./nft/FairNft.sol";

contract FairToken is Context,IERC20,Ownable {
    using SafeMath for uint256;

    // 地址余额的数据映射
    mapping (address => uint256) private _balances;

    mapping (address => mapping (address => uint256)) private _allowances;

    // 总量
    uint256 private _totalSupply;
    // 代币精度
    uint8 private _decimals;
    // 代币符号
    string private _symbol;
    // 代币名称
    string private _name;

    // 交易对地址存储（用户判断是否是在swap交易）
    mapping(address => bool) public automatedMarketMakerPairs;

    // 白名单列表，交易不扣手续费
    mapping(address => bool) public _isExcludedFromFee;

    // 链游
    address public lianyou_address = 0x17b9336D2857247AfDEBAC05fbDd0486b3047e48;
    // 底池
    address public dichi_address = 0x17b9336D2857247AfDEBAC05fbDd0486b3047e48;
    // 空头
    address public kongtou_address = 0x17b9336D2857247AfDEBAC05fbDd0486b3047e48;
    // 锁仓
    address public suocang_address = 0x17b9336D2857247AfDEBAC05fbDd0486b3047e48;


    // lp分润总金额
    uint256 public lpProfitAmount;
    // lp分润开关阀值
    uint256 public lpProfitSwitch = 100000;

    // 营销分润地址
    address private yingxiao_profit_address = 0x9Ee4ac246cc8ED956FFB424b1C8E6a28A29fE16C;
    // NFT合约地址
    address private nft_contract_address = 0x62aF888eF012c8A8f0943392B17488bd2af62Bcd;
    // 黑洞地址
    address private blackHole = 0x0000000000000000000000000000000000000000;

    // 筑底记录
    mapping (address => uint256) public _zhudiSum;
    // 筑底key用户循环mapping
    address[] public _zhudiKeys;
    // 筑底map用于判断筑底key是否存在
    mapping (address => bool) public _zhudiMap;

    // 买入nft分润比例
    uint256 public _ruNftRate = 3;
    // 买入营销分润比例
    uint256 public _ruYingxiaoRate = 1;
    // 卖出nft分润比例
    uint256 public _chuNftRate = 3;
    // 卖出LP分红比例
    uint256 public _chuLpRate = 2;
    // 卖出营销分润比例
    uint256 public _chuYingxiaoRate = 1;

    // 已分红nft总金额纪录
    mapping(uint=>uint256) public _nftProfitCategoryTotal;
    // 未分红nft余额纪录
    mapping(uint=>uint256) public _nftProfitCategoryNotTotal;

    // nft分类分润比例
    mapping(uint=>uint256) public _nftCategoryRateList;

    // 路由合约
    IUniswapV2Router public uniswapV2Router;
    // 交易对地址
    address public uniswapV2Pair;

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    constructor() {
        _name = "Fair Coin";
        _symbol = "Fair";
        _decimals = 18;
        _totalSupply = 320000000 * 10 ** uint256(_decimals);

        // 40% 8亿枚 锁仓用于链游
        uint256 lianyou_num = 320000000 * 10 ** uint256(_decimals);
        _balances[lianyou_address] = lianyou_num;
        emit Transfer(address(0), lianyou_address, lianyou_num);

        IUniswapV2Router _uniswapV2Router = IUniswapV2Router(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
            // 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3
        );
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), 0x55d398326f99059fF775485246999027B3197955);

        // set the rest of the contract variables
        setAutomatedMarketMakerPair(uniswapV2Pair, true);
        uniswapV2Router = _uniswapV2Router;

        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;

        // 设置nft分类分润比例
        _nftCategoryRateList[1] = 25;
        _nftCategoryRateList[2] = 25;
        _nftCategoryRateList[3] = 25;
        _nftCategoryRateList[4] = 25;
    }

    /**
     * @dev Returns the bep token owner.
   */
    function getOwner() external view returns (address) {
        return owner();
    }

    /**
     * @dev Returns the token decimals.
   */
    function decimals() external view returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Returns the token symbol.
   */
    function symbol() external view returns (string memory) {
        return _symbol;
    }

    /**
    * @dev Returns the token name.
  */
    function name() external view returns (string memory) {
        return _name;
    }

    /**
     * @dev See {BEP20-totalSupply}.
   */
    function totalSupply() external view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {BEP20-balanceOf}.
   */
    function balanceOf(address account) external view virtual override returns (uint256) {
        return _balances[account];
    }

    // 获取交易对地址
    function getPair() external view returns(address){
        return uniswapV2Pair;
    }

    /**
     * 设置LP分润开关阀值 
     */
    function setLpProfitSwitch(uint256 value) public onlyOwner {
        lpProfitSwitch = value;
    }


    /**
     * 
     * 设置交易对地址
     */
    function setAutomatedMarketMakerPair(address pair, bool value)
    public
    onlyOwner
    {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    /**
     * 
     * 设置白名单，交易不扣手续费
     */
    function excludeFromFee(address account, bool excluded) public onlyOwner {
        if (_isExcludedFromFee[account] != excluded) {
            _isExcludedFromFee[account] = excluded;
        }
    }

    /**
     *
     *  批量设置白名单
     */ 
    function excludeMultipleAccountsFromFee(
        address[] memory accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFee[accounts[i]] = excluded;
        }
    }

    /**
     *
     *  设置nft收益地址
     */ 
    function setNftContractAddress(address account) public onlyOwner {
        if(nft_contract_address != account){
            nft_contract_address = account;
        }
    }

    /**
     *
     *  设置营销收益地址
     */ 
    function setYingxiaoAddress(address account) public onlyOwner {
        if(yingxiao_profit_address != account){
            yingxiao_profit_address = account;
        }
    }


    // 设置买入nft分润比例
    function setRuNftRate(uint256 fee) public onlyOwner{
        _ruNftRate = fee;
    }
    // 设置直推分润比例
    function setRuYingxiaoRate(uint256 fee) public onlyOwner{
        _ruYingxiaoRate = fee;
    }
    // 设置卖出NFT分润比例
    function setChunftRate(uint256 fee) public onlyOwner{
        _chuNftRate = fee;
    }
    // 设置卖出LP分润比例
    function setChuLpRate(uint256 fee) public onlyOwner{
        _chuLpRate = fee;
    }
    // 设置卖出营销比例
    function setChuYingxiaoRate(uint256 fee) public onlyOwner{
        _chuYingxiaoRate = fee;
    }

    /**
     * 设置nft每种分类分润比例
     */
    function setNftCategoryRateList(uint key,uint256 value) public onlyOwner{
        _nftCategoryRateList[key] = value;
    }

    /**
     * @dev See {BEP20-transfer}.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
    function transfer(address recipient, uint256 amount) external virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {BEP20-allowance}.
   */
    function allowance(address owner, address spender) external view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {BEP20-approve}.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
    function approve(address spender, uint256 amount) external virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {BEP20-transferFrom}.
   *
   * Emits an {Approval} event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of {BEP20};
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
    function transferFrom(address sender, address recipient, uint256 amount) external virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "BEP20: transfer amount exceeds allowance"));
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "BEP20: decreased allowance below zero"));
        return true;
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");

        _balances[sender] = _balances[sender].sub(amount, "BEP20: transfer amount exceeds balance");
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient]) {
            takeFee = false;
        }

        // 当接收地址是交易对地址时，说明是swap交易卖出，扣除对应比例手续费
        if(takeFee && automatedMarketMakerPairs[recipient]){
            // 到账数量
            uint256 rate = 100;
            rate = rate.sub(_chuNftRate).sub(_chuYingxiaoRate).sub(_chuLpRate);
            uint256 daozhang_num = rate.mul(amount).div(100);
            _balances[recipient] = _balances[recipient].add(daozhang_num);

            // nft
            uint256 nftFee = _chuNftRate.mul(amount).div(100);
            if(nftFee > 0){
                _balances[address(this)] = _balances[address(this)].add(nftFee);
                _nftProfit(nftFee);
            }

            // 营销
            uint256 yingxiaoFee = _chuYingxiaoRate.mul(amount).div(100);
            _balances[yingxiao_profit_address] = _balances[yingxiao_profit_address].add(yingxiaoFee);
            emit Transfer(sender, yingxiao_profit_address, yingxiaoFee);

            // 销毁
            uint256 xhFee = _chuLpRate.mul(amount).div(100);
            _balances[blackHole] = _balances[blackHole].add(xhFee);
            emit Transfer(sender, blackHole, xhFee);

        }else if(takeFee && automatedMarketMakerPairs[sender]){
            // 当发起地址是交易对地址时，说明是swap交易买入，扣除对应比例手续费
            // 到账数量
            uint256 rate = 100;
            rate = rate.sub(_ruNftRate).sub(_ruYingxiaoRate).sub(_chuLpRate);
            uint256 daozhang_num = rate.mul(amount).div(100);
            _balances[recipient] = _balances[recipient].add(daozhang_num);

            // nft
            uint256 nftFee = _ruNftRate.mul(amount).div(100);
            if(nftFee > 0){
                _balances[address(this)] = _balances[address(this)].add(nftFee);
                _nftProfit(nftFee);
            }

            // 营销
            uint256 yingxiaoFee = _ruYingxiaoRate.mul(amount).div(100);
            _balances[yingxiao_profit_address] = _balances[yingxiao_profit_address].add(yingxiaoFee);
            emit Transfer(recipient, yingxiao_profit_address, yingxiaoFee);

            // 销毁
            uint256 xhFee = _chuLpRate.mul(amount).div(100);
            _balances[blackHole] = _balances[blackHole].add(xhFee);
            emit Transfer(recipient, blackHole, xhFee);
        }else{
            _balances[recipient] = _balances[recipient].add(amount);
        }

        emit Transfer(sender, recipient, amount);
    }

   /**
    * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
    *
    * This is internal function is equivalent to `approve`, and can be used to
    * e.g. set automatic allowances for certain subsystems, etc.
    *
    * Emits an {Approval} event.
    *
    * Requirements:
    *
    * - `owner` cannot be the zero address.
    * - `spender` cannot be the zero address.
   */
    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "BEP20: approve from the zero address");
        require(spender != address(0), "BEP20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    // lp分润
    function _lpProfit() private returns(bool) {
        // 获取总筑底金额
        uint256 total = 0;
        for(uint i=0;i<_zhudiKeys.length;i++){
            total = total.add(_zhudiSum[_zhudiKeys[i]]);
        }
        // 本次分币金额
        uint256 thisLpAmount = lpProfitAmount;
        // 计算每个地址的获得百分比
        for(uint i=0;i<_zhudiKeys.length;i++){
            uint256 bili = _zhudiSum[_zhudiKeys[i]].mul(10000).div(total);
            // lp分润
            if(bili > 0){
                uint256 amount = thisLpAmount.mul(bili).div(10000);
                require(lpProfitAmount >= amount,"LP service charge is lower than the amount of this dividend");
                this.transfer(_zhudiKeys[i],amount);
                lpProfitAmount = lpProfitAmount.sub(amount);
            }
        }
        return true;
    }


    /**
     * nft分润，amount为本次分润金额
     * 记录存储
     */
    function _nftProfit(uint256 amount) private returns(bool) {
        for(uint256 i=1;i<=4;i++){
            // 查询这个分类应该分多少代币
            _nftProfitCategoryNotTotal[i] = amount.mul(_nftCategoryRateList[i]).div(100).add(_nftProfitCategoryNotTotal[i]);
        }
        return true;
    }

    /**
     * 发放nft分红，线下统一触发
     */ 
    function nftProfit() public returns(bool) {
        FairNft _token = FairNft(nft_contract_address);
        address[] memory nftListAddress = _token.getKeys();
        for(uint256 i=1;i<=4;i++){
            uint256 t = 0;
            for(uint256 j=0;j<nftListAddress.length;j++){
                t += _token.balanceOf(nftListAddress[j],i);
            }
            // 查询这个分类应该分多少代币
            uint256 thisAmount = _nftProfitCategoryNotTotal[i];
            // 如果此分类有nft持有用户，就分红，没有就累加到记录下次有的时候再分
            if(t > 0){
                // 计算一张nft的单价
                uint256 price = thisAmount.div(t);
                for(uint256 j=0;j<nftListAddress.length;j++){
                    uint num = _token.balanceOf(nftListAddress[j],i);
                    uint256 money = price.mul(num);
                    _nftProfitCategoryTotal[i] += money;
                    this.transfer(nftListAddress[j],money);
                }
                _nftProfitCategoryNotTotal[i] = 0;
            }
        }
        return true;
    }
}

interface IUniswapV2Router {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
    external
    returns (
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    );

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
    external
    payable
    returns (
        uint256 amountToken,
        uint256 amountETH,
        uint256 liquidity
    );

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(uint256 amountIn, address[] calldata path)
    external
    view
    returns (uint256[] memory amounts);

    function getAmountsIn(uint256 amountOut, address[] calldata path)
    external
    view
    returns (uint256[] memory amounts);

    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

interface IUniswapV2Factory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB)
    external
    view
    returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function createPair(address tokenA, address tokenB)
    external
    returns (address pair);

    function setFeeTo(address) external;

    function setFeeToSetter(address) external;
}"
    },
    "nft/FairNft.sol": {
      "content": "// SPDX-License-Identifier: MIT;

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";


contract FairNft is ERC1155,Ownable {

    using Counters for Counters.Counter;
    Counters.Counter private _tokenId;

    mapping(uint => string) private _tokenURIs;

    mapping(address => bool) private _checkKey;
    address[] private _keys;

    // 销毁地址
    address public _destroy_address = 0x1ed77fE90e863A70dc9CDDB927F10dDE53910065;


    //  name
    string public name;
    //  symbol
    string public symbol;
    constructor() ERC1155("") {
        name = "Fair NFT";
        symbol = "FAIR";
        mint(msg.sender,1500,"http://webstatic.ifairdao.com/uploads/1.jpg");
        mint(msg.sender,500,"http://webstatic.ifairdao.com/uploads/2.jpg");
        mint(msg.sender,300,"http://webstatic.ifairdao.com/uploads/3.jpg");
        mint(msg.sender,100,"http://webstatic.ifairdao.com/uploads/4.jpg");
    }

    function setDestroyAddress(address add) public returns(bool){
        _destroy_address = add;
        return true;
    }

    function mint(address _recipient,uint _amount, string memory _tokenUrl) public onlyOwner returns(uint _mintTokenId){
        require(bytes(_tokenUrl).length > 0,"The _tokenUrl must be have");
        require(_amount > 0,"The _amount must be have");
        _tokenId.increment();
        uint newTokenId = _tokenId.current();
        _mint(_recipient, newTokenId,_amount,"");
        _tokenURIs[newTokenId] = _tokenUrl;
        return newTokenId;
    }

    function uri(uint256 _id) public view override returns(string memory _tokenUrl){
        return _tokenURIs[_id];
    }

    function uriBatch(uint[] memory _tokenIds) public view returns(string[] memory _tokenUrls){
        require(_tokenIds.length > 0,"The _tokenId must be have");

        string[] memory returnTokenUrl = new string[](_tokenIds.length);

        for(uint256 i=0;i<_tokenIds.length;i++){
            returnTokenUrl[i] = _tokenURIs[_tokenIds[i]];
        }

        return returnTokenUrl;
    }


    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual override {
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: caller is not token owner nor approved"
        );
        if(owner() != to && _destroy_address != to){
            // 接收地址不为发币地址时，记录收款地址
            if(!_checkKey[to]){
                _checkKey[to] = true;
                _keys.push(to);
            }
        }
        _safeTransferFrom(from, to, id, amount, data);
    }

    function getKeys() public view returns(address[] memory)
    {
        return _keys;
    }
}"
