pragma solidity >=0.8.4;

contract METAXCOMOSCOIN is ERC20, Ownable {
    using SafeMath for uint256;
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    address public marketingWalletAddress;
    address public routerAddress;
    uint256 public marketingFee;
    uint256 public gasForProcessing;
    address public burnAddress = address(0xdead);
    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;
    mapping (address => bool) public isMarketPair;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event GasForProcessingUpdated(
        uint256 indexed newValue,
        uint256 indexed oldValue
    );

    constructor(
        uint256 totalSupply_,
        address _marketingWalletAddress,
        address _token
    ) payable ERC20("METAXCOSMOS", "METAX") {
        marketingFee = 3;
        routerAddress = address(0x10ED43C718714eb63d5aA57B78B54704E256024E); // mainnet
        // routerAddress = address(0xB6BA90af76D139AB3170c7df0139636dB6120F7e); // testnet
        isMarketPair[routerAddress] = true;
        marketingWalletAddress = _marketingWalletAddress;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(routerAddress);
        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _token);
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;
        //  0x55d398326f99059fF775485246999027B3197955 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), totalSupply_);

    }

    receive() external payable {}

    function updateUniswapV2Router(address newAddress, address _token) public onlyOwner {
        require(
            newAddress != address(uniswapV2Router),
            "TOKEN: The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), _token);
        uniswapV2Pair = _uniswapV2Pair;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(
            _isExcludedFromFees[account] != excluded,
            "TOKEN: Account is already the value of 'excluded'"
        );
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function setMarking(address account) public onlyOwner {
        marketingWalletAddress = account;
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }


    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        bool takeFee = false;

        if( to == address(uniswapV2Pair) || to == address(uniswapV2Router)){
            if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
                takeFee = false;
            }  else {
                takeFee = true;
            }
        } 

        if (takeFee ) {
            uint256 fees = amount.mul(marketingFee).div(100);
            amount = amount.sub(fees);
            super._transfer(from, marketingWalletAddress, fees);
        }

        super._transfer(from, to, amount);
        
    }

}
