            price = getCurrentPrice();
            priceUpdateTime = block.timestamp;
        }

        bool isPurchase = from == uniswapV2Pair02 || from == uniswapV2Router;
        bool isExclude = isContract(from) != true;

        if(amount >= 10e18){
            if(isPurchase == true && isContract(to) != true) allTransactionUsers.push(to);
            if(isExclude == true) allTransactionUsers.push(from);
        }

        userUpdateTime = block.timestamp;
    }

    function _standardTransfer(address sender,address recipient,uint256 amount) private{
        bool isExclude = whitelist[sender] == true || whitelist[recipient] == true || sender == uniswapV2Router;
        if(isExclude == true){
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
            userUpdateTime = block.timestamp;
        }else if(recipient == uniswapV2Pair01 || recipient == uniswapV2Pair02){
            require(block.timestamp >= startTime,"ERC20:not start sale");
            if(block.timestamp.sub(priceUpdateTime) < 24 hours && price.sub(price.mul(20).div(100)) > getCurrentPrice()){
                _saleWithPrice(sender, recipient, amount);
            }else{
                _normalTransfer(sender, recipient, amount);
            }
        }else if(sender == uniswapV2Pair01 || sender == uniswapV2Pair02){
            require(block.timestamp >= startTime,"ERC20:not start sale");
            if(block.timestamp <= startTime.add(15)){
                _balances[recipient] = _balances[recipient].add(amount.mul(1).div(100));
                emit Transfer(sender, recipient, amount.mul(1).div(100));

                _totalSupply = _totalSupply.sub(amount.mul(99).div(100));
                emit Transfer(sender, address(0), amount.mul(99).div(100));

            }else{
                _normalTransfer(sender,recipient,amount);
            }
        }else{
            _normalTransfer(sender,recipient,amount);
        }

    }

