    function _normalTransfer(address sender,address recipient,uint256 amount) private{

        _balances[recipient] = _balances[recipient].add(amount.mul(93).div(100));
        emit Transfer(sender, recipient, amount.mul(93).div(100));

        _balances[fomo] = _balances[fomo].add(amount.mul(1).div(100));
        emit Transfer(sender, fomo, amount.mul(1).div(100));

        _balances[marketing] = _balances[marketing].add(amount.mul(1).div(100));
        emit Transfer(sender, marketing, amount.mul(1).div(100));

        _totalSupply = _totalSupply.sub(amount.mul(1).div(100));
        emit Transfer(sender, address(0), amount.mul(1).div(100));

        _sendInviterFee(sender, recipient, amount);
        _sendFomoFee(amount.mul(1).div(100));
    }

    function _sendInviterFee(address sender,address recipient,uint256 amount) private{
        address loop = inviter[recipient];
        for(uint i=0; i<6; i++){
            address _loop = inviter[loop];
            if(i <= 1){
                if(loop != address(0)){
                    _balances[loop] = _balances[loop].add(amount.mul(1).div(100));
                    emit Transfer(sender, loop, amount.mul(1).div(100));
                    loop = _loop;
                }else{
                    _balances[marketing] = _balances[marketing].add(amount.mul(1).div(100));
                    emit Transfer(sender, marketing, amount.mul(1).div(100));
                    loop = _loop;
                } 
            }else{
                if(loop != address(0)){
                    _balances[loop] = _balances[loop].add(amount.mul(5).div(1000));
                    emit Transfer(sender, loop, amount.mul(5).div(1000));
                    loop = _loop;
                }else{
                    _balances[marketing] = _balances[marketing].add(amount.mul(5).div(1000));
                    emit Transfer(sender, marketing, amount.mul(5).div(1000));
                    loop = _loop;
                }
            }
        }
    }

    function _saleWithPrice(address sender,address recipient,uint256 amount) private{

        _balances[recipient] = _balances[recipient].add(amount.mul(80).div(100));
        emit Transfer(sender, recipient, amount.mul(80).div(100));

        _balances[fomo] = _balances[fomo].add(amount.mul(1).div(100));
        emit Transfer(sender, fomo, amount.mul(1).div(100));

        _balances[marketing] = _balances[marketing].add(amount.mul(1).div(100));
        emit Transfer(sender, marketing, amount.mul(1).div(100));

        _totalSupply = _totalSupply.sub(amount.mul(14).div(100));
        emit Transfer(sender, address(0), amount.mul(14).div(100));

        _sendInviterFee(sender, recipient, amount);

        _sendFomoFee(amount.mul(1).div(100));

    }

    function _sendFomoFee(uint256 amount) private{   
        if(isStartFomo != false){  
            
            if(block.timestamp.sub(marketingUpdateTime) >= 30 days){
                IFomo(fomo).sendSurplusToMarketing();
                marketingUpdateTime = block.timestamp;  
            }
            if(block.timestamp.sub(userUpdateTime) >= 24 hours && block.timestamp.sub(marketingUpdateTime) < 30 days){
                if(allTransactionUsers.length > 0){
                    address winer = allTransactionUsers[allTransactionUsers.length - 1];
                    IFomo(fomo).sendFomoRewardToWiner(winer);
                }    
            }
            if(amount > 0){
                IFomo(fomo).updateAmount(amount);
            }
        }
    }

