// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./ERC20.sol";
import "./Administrator.sol";


// CFB coin
contract CfbCoin is ERC20, Administrator {
 using SafeMath for uint256;

  // transfer paused for lock ico balance
  bool public paused = true;

  // shoud mark for any tx before lock
  mapping (address => uint256) internal _lockBalances;
  // user total unlock balance
  mapping(address => uint256) internal _unlockedAmount;

  // start lock time,lock ico user 80% balance, unlock 10% per month,
  uint256 startLockTime;

  // lock address
  address public lockedAddress;

  // fee address
  address public feeTo;

  // fee status
  bool public feeStatus = true;

  // fee rate, use should div(1000)
  uint256 public feeRate = 110;

  // fee white list
  mapping(address => bool) fromWhiteList;
  mapping(address => bool) toWhiteList;

  // true:fee for inner，false：fee for outer
  bool feeForWhiteList = true;

  // can burn
  bool canBurn;

  // lock transfer
  event LockAccountTransfer(address indexed from, address indexed to, uint256 value);
  // lock event
  event Locked(address indexed user, uint256 amount);
  // unlock event
  event Unlocked(address indexed user, uint256 amount);
  // release
  event Released(address indexed user, uint256 amount);

  constructor(
      string memory _name,
      string memory _symbol,
      uint8 _decimals,
      uint256 _totalSupply
  ) ERC20(_name, _symbol, _decimals) {
      uint256 initSupply = _totalSupply * 10 ** uint256(decimals());
      _mint(address(this), initSupply);
  }

  // get account locked balance
  function lockedBalanceOf(address _account) public view returns (uint256) {
    return _lockBalances[_account].sub(_unlockedAmount[_account]);
  }

  /**
  * set if transfer paused
   */
  function setPaused(bool _paused) public onlyOwner() {
    paused = _paused;
  }

  /**
  * set can burn
   */
  function setCanBurn(bool _canBurn) public onlyOwner() {
    canBurn = _canBurn;
  }

  function setStartLockTime(uint256 _startLockTime) public onlyOwner() {
    require(startLockTime == 0, "start lock time already set!");
    startLockTime = _startLockTime;
  }

  /**
  * set lock address
   */
  function setLockedAddress(address _address) public onlyOwner() {
    lockedAddress = _address;
  }

  /**
  * set fee to
   */
  function setFeeTo(address _feeTo) public onlyOwner() {
    feeTo = _feeTo;
  }

  /**
  * set fee status
   */
  function setFeeStatus(bool _status) public onlyOwner() {
    feeStatus = _status;
  }

  /**
  * set feeForWhiteList
   */
  function setFeeForWhiteList(bool _feeForWhiteList) public onlyOwner() {
    feeForWhiteList = _feeForWhiteList;
  }

  /**
  * set fee to
   */
  function setFeeRate(uint256 _feeRate) public onlyOwner() {
    feeRate = _feeRate;
  }

  // set white list for address
  function setWhiteList(address _addr, bool _setForm, bool _setTo) public onlyOwner() {
    fromWhiteList[_addr] = _setForm;
    toWhiteList[_addr] = _setTo;
  }

  // release from contract, for ico Foundation airdrop liquidity pool ...etc
  function release(address _recipient, uint256 _amount) public onlyOwner() returns (bool) {
    super._transfer(address(this), _recipient, _amount);
    emit Released(_recipient, _amount);
    return true;
  }

  // view whether transfer is paused after start line
  function transferPaused() public view returns (bool) {
    return paused == true && block.timestamp >= startLockTime && startLockTime > 0;
  }

  // whether sender is in from white list
  function isFromWhiteList(address _addr) public view returns (bool){
    return fromWhiteList[_addr];
  }
  
  // whether recipient is in to white list
  function isToWhiteList(address _addr) public view returns(bool){
    return toWhiteList[_addr];
  }

  /**
  * @dev Destroys `amount` tokens from the caller.
  *
  * See {ERC20-_burn}.
  */
  function burn(uint256 amount) public returns (bool) {
    require(canBurn, "token can not burn");
    _burn(msg.sender, amount);
    return true;
  }

  /**
  * @dev Destroys `amount` tokens from `account`, deducting from the caller's
  * allowance.
  *
  * See {ERC20-_burn} and {ERC20-allowance}.
  *
  * Requirements:
  *
  * - the caller must have allowance for ``accounts``'s tokens of at least
  * `amount`.
  */
  function burnFrom(address account, uint256 amount) public returns (bool) {
    require(canBurn, "token can not burn");
    _burnFrom(account, amount);
    return true;
  }

  /**
    * @dev Moves tokens `amount` from `sender` to `recipient`.
    *
    * This is internal function is equivalent to `transfer`, and can be used to
    * e.g. implement automatic token fees, slashing mechanisms, etc.
    *
    * Emits a `Transfer` event.
    *
    * Requirements:
    *
    * - `sender` cannot be the zero address.
    * - `recipient` cannot be the zero address.
    * - `sender` must have a balance of at least `amount`.
    */
  function _transfer(address sender, address recipient, uint256 amount) internal override virtual {
    // when start up dex, paused transfer for a litter while for lock 80% ico balance
    require(!transferPaused(), "now can not transfer!");
    if ((startLockTime == 0 || block.timestamp < startLockTime) && _lockBalances[sender] > 0) {
      // if transfer from ico balance (10,000,000),should add record map
      uint256 unLockBalance = balanceOf(sender).sub(_lockBalances[sender]);
      if (unLockBalance < amount) {
        // transfer free balance firstly
        uint256 lockAmount = amount.sub(unLockBalance);
        _lockBalances[sender] = _lockBalances[sender].sub(lockAmount);
        _lockBalances[recipient] = _lockBalances[recipient].add(lockAmount);
        emit LockAccountTransfer(sender, recipient, lockAmount);
      }
      super._transfer(sender, recipient, amount);
      return;
    } else if (feeStatus && feeRate > 0 && feeTo != address(0)) {
      // after start time, got fee
      _feeTransfer(sender, recipient, amount);
    } else {
      super._transfer(sender, recipient, amount);
    }
    // account can't transfer without lock 80% when he has lock-balance
    if (_lockBalances[sender] > 0) {
      require(_unlockedAmount[sender] > 0, "account should lock 80%");
    }
  }

  // transfer fee
  function _feeTransfer(address sender, address recipient, uint256 amount) internal {
    require(sender != address(0), "ERC20: transfer from the zero address");
    require(recipient != address(0), "ERC20: transfer to the zero address");
    uint256 fee;
    if (feeForWhiteList) {
      // got fee when in white list
      if (isFromWhiteList(sender) || isToWhiteList(recipient)) {
        fee = amount.mul(feeRate).div(1000);
      }
    } else {
      //got fee for not in white list
      if (!isFromWhiteList(sender) && !isToWhiteList(recipient)) {
        fee = amount.mul(feeRate).div(1000);
      }
    }
    _balances[sender] = _balances[sender].sub(amount);
    if (fee > 0) {
      _balances[feeTo] = _balances[feeTo].add(fee);
      emit Transfer(sender, feeTo, fee);
      _balances[recipient] = _balances[recipient].add(amount.sub(fee));
      emit Transfer(sender, recipient, amount.sub(fee));
    } else {
      _balances[recipient] = _balances[recipient].add(amount);
      emit Transfer(sender, recipient, amount);
    }
  }

  // add ico acctoun to lock map,10000000per for listen
  function LockAccount(address _account, uint256 _amount) public admin() returns (bool) {
    require(balanceOf(_account) >= _amount, "lock amount more then balance");
    require(_lockBalances[_account] == 0, "account already locked");
    require(startLockTime == 0 || block.timestamp < startLockTime, "can not set lock!");
    _lockBalances[_account] = _amount;
    return true;
  }

  // lock 80% ico user balance when start
  function lock(address _account) public admin() returns (bool) {
    require(_lockBalances[_account] > 0, "user has not locked balance");
    require(_unlockedAmount[_account] == 0, "user has not unlock balance");
    uint256 lockAmount = _lockBalances[_account].mul(80).div(100);
    super._transfer(_account, lockedAddress, lockAmount);
    _unlockedAmount[_account] = _lockBalances[_account].sub(lockAmount);
    emit Locked(_account, lockAmount);
    emit Unlocked(_account, _unlockedAmount[_account]);
    return true;
  }

  // unlock 10% every month
  function unlock(address _account) public admin() returns (bool) {
    require(_lockBalances[_account] > 0, "user has not locked balance");
    require(_lockBalances[_account] > _unlockedAmount[_account], "user has not locked balance");
    uint256 unlockAmount = _lockBalances[_account].mul(10).div(100);
    super._transfer(lockedAddress, _account, unlockAmount);
    _unlockedAmount[_account] = _unlockedAmount[_account].add(unlockAmount);
    emit Unlocked(_account, unlockAmount);
    return true;
  }

}"
