// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";


contract Administrator is Ownable {

   // administrators address
  mapping(address => bool) administrators;

  modifier admin() {
      require(administrators[msg.sender], "auth error!");
      _;
  }

  constructor() {
    administrators[msg.sender] = true;
  }

  function isAdministrator(address _addr) public view returns(bool){
    return administrators[_addr];
  }

  function setAdministrator(address _addr, bool _status) public onlyOwner() {
    administrators[_addr] = _status;
  }

}"
