abstract contract BPContract{
    function protect( address sender, address receiver, uint256 amount ) external virtual;
}

contract MitaToken is ERC20, Ownable {
    address public admin;

    // bot protection
    BPContract public BP;
    bool public bpEnabled;
    bool public bpDisabledForever;

    mapping(address => mapping(uint256 => bool)) public signed; // Check transaction is ready to handle or not

    event BridgeTokenBurn (address indexed from, string to, uint256 amount, uint256 date, uint256 nonce);

    constructor(string memory name, string memory symbol) ERC20(name, symbol) {}

    function decimals() public pure override(ERC20) returns (uint8) {
        return 8;
    }

    // start bot protection
    function setBPAddrss(address _bp) external onlyOwner {
        require(address(BP)== address(0), "Can only be initialized once");
        BP = BPContract(_bp);
    }

    function setBpEnabled(bool _enabled) external onlyOwner {
        bpEnabled = _enabled;
    }

    function setBotProtectionDisableForever() external onlyOwner{
        require(bpDisabledForever == false, "Bot protection is enable");
        bpDisabledForever = true;
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override(ERC20) {
        super._beforeTokenTransfer(from, to, amount);
        
        if (bpEnabled && !bpDisabledForever){
            BP.protect(from, to, amount);
        }
    }
    // end bot protection

    // Mint token for an address. We will use it in case we want to transfer 
    // MITA token from Klaytn to BSC
    function mintToken(address from, address to, uint256 amount, uint256 nonce, bytes calldata signature) external onlyOwner {
       bytes32 message = ECDSA.toEthSignedMessageHash(keccak256(abi.encodePacked(from, to, amount, nonce)));

       require(SignatureChecker.isValidSignatureNow(msg.sender, message, signature), "Wrong signature");
       require(signed[to][nonce] == false, "Transfer is ready processed");

       signed[to][nonce] = true;
       _mint(to,  amount);       
    }

    // Burn token in case the user wants to transfer token from BSC to Klaytn
    function burnToken(string memory to , uint256 amount, uint256 nonce) external onlyOwner {
       require(signed[msg.sender][nonce] == false, "Transfer is ready processed");
       signed[msg.sender][nonce] = true;

       _burn(msg.sender, amount);

       emit BridgeTokenBurn(msg.sender, to, amount, block.timestamp, nonce);
    }
}
