contract BMCToken is ERC20Capped, Orchestrated {
    using SafeMath for uint256;

    uint public nodeFee = 300;
    uint public lpFee = 100;
    uint public marketFee = 100;
    uint public burnFee = 100;
    uint public transferFee = 100;

    uint public needAddLp;
    uint public addLpMaxAmount = 10*1e18;
    bool private inSwapAndLiquify;

    address public nodeFeeWallet = address(this);
    address public marketFeeWallet = address(this);

    mapping (address => bool) public _isSwapInclude;
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    address public usdtToken;

    bool public senderLimit;
    bool public recipientLimit;
    mapping(address => bool) public senderWhite;
    mapping(address => bool) public recipientWhite;
    mapping(address => bool) public white;
    mapping(address => bool) public feeWhite;

    constructor() ERC20Capped(890000 * 1e18) ERC20("Binance Mine Coin", "BMC") {
        ERC20._mint(msg.sender, 890000 * 1e18);

        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        usdtToken = address(0x55d398326f99059fF775485246999027B3197955);//USDT

        // Create a uniswap pair for this new token
        IUniswapV2Factory factory =  IUniswapV2Factory(uniswapV2Router.factory());
        address _uniswapV2Pair = factory.createPair(address(this), usdtToken);
        uniswapV2Pair = _uniswapV2Pair;
        _isSwapInclude[uniswapV2Pair] = true;

        address ipBnbPair = factory.createPair(address(this), uniswapV2Router.WETH());
        _isSwapInclude[ipBnbPair] = true;
        white[address(this)] = true;
        white[_msgSender()] = true;
        senderLimit = true;
    }

    function setFeeConfig(uint _nodeFee, uint _lpFee, uint _marketFee, uint _burnFee, uint _transferFee) public onlyOwner {
        nodeFee = _nodeFee;
        lpFee = _lpFee;
        marketFee = _marketFee;
        burnFee = _burnFee;
        transferFee = _transferFee;
    }

    function swapInclude(address account, bool isInclude) public onlyOwner() {
        _isSwapInclude[account] = isInclude;
    }

    function setSenderLimit(bool _senderLimit) public onlyOwner {
        senderLimit = _senderLimit;
    }

    function setRecipientLimit(bool _recipientLimit) public onlyOwner {
        recipientLimit = _recipientLimit;
    }

    function setSenderWhite(address _account, bool _white) public onlyOwner {
        if(_account != address(this)){
            senderWhite[_account] = _white;
            emit SetSenderWhite(_account, _white);
        }
    }

    function setRecipientWhite(address _account, bool _white) public onlyOwner {
        if(_account != address(this)){
            recipientWhite[_account] = _white;
            emit SetRecipientWhite(_account, _white);
        }
    }

    function setWhite(address _account, bool _white) public onlyOwner {
        if(_account != address(this)){
            white[_account] = _white;
            emit SetWhite(_account, _white);
        }
    }

    function setFeeWhite(address _account, bool _white) public onlyOwner {
        if(_account != address(this)){
            feeWhite[_account] = _white;
        }
    }

    function setWallet(address _nodeFeeWallet, address _marketFeeWallet) public onlyOwner {
        nodeFeeWallet = _nodeFeeWallet;
        marketFeeWallet = _marketFeeWallet;
    }

    function setAddLpMaxAmount(uint _addLpMaxAmount) public onlyOwner {
        addLpMaxAmount = _addLpMaxAmount;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer recipient the zero address");
        require(!senderLimit || !isContract(sender) || senderWhite[sender] || white[sender] || white[recipient] || address(0) == sender, "Token: senderLimit");
        require(!recipientLimit || !isContract(recipient) || recipientWhite[recipient] || white[sender] || white[recipient], "Token: recipientLimit");

        _beforeTokenTransfer(sender, recipient, amount);

        bool takeFee = true;
        if(recipient == address(uniswapV2Pair)){
            (uint reserve0, uint reserve1, ) = IUniswapV2Pair(uniswapV2Pair).getReserves();
            if(address(this) == IUniswapV2Pair(uniswapV2Pair).token0() && IERC20(usdtToken).balanceOf(uniswapV2Pair) != reserve1){
                takeFee = false;
            }
            if(address(this) == IUniswapV2Pair(uniswapV2Pair).token1() && IERC20(usdtToken).balanceOf(uniswapV2Pair) != reserve0){
                takeFee = false;
            }
        }

        if(feeWhite[sender] || feeWhite[recipient]) {
            takeFee = false;
        }

        uint256 tokens = amount;
        if(takeFee) {
            if(_isSwapInclude[sender] || _isSwapInclude[recipient]) {
                if(nodeFee > 0) {
                    uint256 nodeFees = amount.mul(nodeFee).div(10000);
                    super._transfer(sender, address(nodeFeeWallet), nodeFees);
                    tokens = tokens.sub(nodeFees);
                }
                if(lpFee > 0) {
                    uint256 lpFees = amount.mul(lpFee).div(10000);
                    needAddLp = needAddLp.add(lpFees);
                    if(needAddLp >= addLpMaxAmount) {
                        swapAndLiquify(needAddLp, IERC20(address(this)), IERC20(usdtToken));
                        needAddLp = 0;
                    }
                    tokens = tokens.sub(lpFee);
                }

                if(marketFee > 0) {
                    uint256 marketFees = amount.mul(marketFee).div(10000);
                    super._transfer(sender, address(marketFeeWallet), marketFees);
                    tokens = tokens.sub(marketFees);
                }
                if(burnFee > 0) {
                    uint256 burnFees = amount.mul(burnFee).div(10000);
                    super._transfer(sender, address(0x000000000000000000000000000000000000dEaD), burnFees);
                    tokens = tokens.sub(burnFees);
                }
            }
        }

        bool takeTransferFee = true;
        if(feeWhite[sender] || feeWhite[recipient]) {
            takeTransferFee = false;
        }
        if(takeTransferFee) {
            if(!isContract(sender) && !isContract(recipient)){
                if(transferFee > 0) {
                    uint256 transferFees = amount.mul(transferFee).div(10000);
                    super._transfer(sender, address(0x000000000000000000000000000000000000dEaD), transferFees);
                    tokens = tokens.sub(transferFees);
                }
            }
        }
        super._transfer(sender, recipient, tokens);
    }

    function isContract(address _address) internal view returns (bool) {
        uint256 size;
        assembly { size := extcodesize(_address) }
        return size > 0;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);
    }

    function burn(uint256 amount) public virtual returns(bool) {
        _burn(_msgSender(), amount);
        emit Burn(_msgSender(), amount);
        return true;
    }

    function burnFrom(address account, uint256 amount) public virtual {
        uint256 currentAllowance = allowance(account, _msgSender());
        require(currentAllowance >= amount, "ERC20: burn amount exceeds allowance");
    unchecked {
        _approve(account, _msgSender(), currentAllowance - amount);
    }
        _burn(account, amount);
    }

    function transferForeignToken(address _token, address _to, uint _amount) public onlyOwner returns(bool _sent){
        _sent = IERC20(_token).transfer(_to, _amount);
    }

    function Sweep(address _to, uint _amount) external onlyOwner {
        payable(_to).transfer(_amount);
    }

    function swapAndLiquify(uint256 tokenAAmountToAddLiquify, IERC20 tokenA, IERC20 tokenB) private lockTheSwap {
        // 把tokenA分成2份
        uint256 half = tokenAAmountToAddLiquify.div(2);
        uint256 otherHalf = tokenAAmountToAddLiquify.sub(half);

        uint256 initialTokenBBalance = tokenB.balanceOf(address(this));

        // swap TokenA for TokenB
        swapTokenAForTokenB(tokenA, tokenB, half);

        // how much Token did we just swap into?
        uint256 swapTokenBAmount = tokenB.balanceOf(address(this)).sub(initialTokenBBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, swapTokenBAmount, tokenA, tokenB);

        emit SwapAndLiquify(address(tokenA), address(tokenB), half, swapTokenBAmount, otherHalf);
    }

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function swapTokenAForTokenB(IERC20 tokenA, IERC20 tokenB, uint256 tokenAAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(tokenA);
        path[1] = address(tokenB);

        tokenA.approve(address(uniswapV2Router), tokenAAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAAmount,
            0, // accept any amount of token
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAAmount, uint256 tokenBAmount, IERC20 tokenA, IERC20 tokenB) private {
        // 授权操作
        tokenA.approve(address(uniswapV2Router), tokenAAmount);
        tokenB.approve(address(uniswapV2Router), tokenBAmount);

        // 添加流动性
        uniswapV2Router.addLiquidity(
            address(tokenA),
            address(tokenB),
            tokenAAmount,
            tokenBAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    event Burn(address indexed account, uint256 amount);
    event SetWhite(address indexed account, bool white);
    event SetSenderWhite(address indexed account, bool white);
    event SetRecipientWhite(address indexed account, bool white);
    event SwapAndLiquify(address tokenA, address tokenB, uint256 usdtIntoSwap, uint256 tokenReceived, uint256 usdtIntoLiqudity);
}
