    IUniswapV2Pair public immutable pair;
    address private developer = address(0xdFf30304B0eC881AFc45b4A15E9A3506306b8661);
    address private community = address(0x939eC6c142dEF6a9b0F57B2ad9f5b741Ce8EdB6c);
