        require(hasRole(ADMIN_ROLE, msg.sender) || owner() == msg.sender, "You don't have permission");
        revokeRole(ADMIN_ROLE, admin);
    }

    function adminRole(address admin) public view returns(bool){
        return hasRole(ADMIN_ROLE,admin);
    }
    
