    function burnFrom(address account, uint256 amount) public virtual {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }
}

// File: contracts/TokenVesting.sol


pragma solidity ^0.8.0;






abstract contract TokenVesting is ERC20Burnable, Ownable {
    using SafeMath for uint256;
    // Date-related constants for sanity-checking dates to reject obvious erroneous inputs
    // and conversions from seconds to days and years that are more or less leap year-aware.
    uint32 private constant THOUSAND_YEARS_DAYS = 365243;                   /* See https://www.timeanddate.com/date/durationresult.html?m1=1&d1=1&y1=2000&m2=1&d2=1&y2=3000 */
    uint32 private constant THIRTY_YEARS_DAYS = (THOUSAND_YEARS_DAYS / 100) * 3;     /* Includes leap years (though it doesn't really matter) */
    uint32 private constant SECONDS_PER_DAY = 24 * 60 * 60;                 /* 86400 seconds in a day */
    uint32 private constant JAN_1_2000_SECONDS = 946684800;                 /* Saturday, January 1, 2000 0:00:00 (GMT) (see https://www.epochconverter.com/) */
    uint32 private constant JAN_1_2000_DAYS = JAN_1_2000_SECONDS / SECONDS_PER_DAY;
    uint32 private constant JAN_1_3000_DAYS = JAN_1_2000_DAYS + THOUSAND_YEARS_DAYS;
    uint32 private constant MONTH_DAYS = 30;
    uint32 private constant WEEK_DAYS = 7;
    // Total months each 3 years
    uint32 private constant THREE_YEARS = 12 * 3;
    uint32 private constant SIX_YEARS = THREE_YEARS * 2;
    uint32 private constant NINE_YEARS = THREE_YEARS * 3;
    uint32 private constant TWELVE_YEARS = THREE_YEARS * 4;
    uint32 private constant FIFTEEN_YEARS = THREE_YEARS * 5;
    uint32 private constant EIGHTEEN_YEARS = THREE_YEARS * 6;
    uint32 private constant TWENTY_ONE_YEARS = THREE_YEARS * 7;
    // Game release percent each 3 years
    uint32 private constant PERCENT_THREE_YEAR = 500;      // 50.0%
    uint32 private constant PERCENT_SIX_YEARS = 250;       // 25.0%
    uint32 private constant PERCENT_NINE_YEARS = 125;      // 12.5%
    uint32 private constant PERCENT_TWELVE_YEARS = 62;     // 6.2%
    uint32 private constant PERCENT_FIFTEEN_YEARS = 30;    // 3.0%
    uint32 private constant PERCENT_EIGHTEEN_YEARS = 20;   // 2.0%
    uint32 private constant PERCENT_TWENTY_ONE_YEARS = 13; // 1.3%
 
    // token allowcation
    uint256 private constant TOKEN_WEI = 10 ** (18);
    uint256 private constant amountSeed = 18 * (10 ** 6);       // 3% = 18M
    uint256 private constant amountPrivate = 48 * (10 ** 6);    // 8% = 48M
    uint256 private constant amountPublic = 24 * (10 ** 6);      // 4% = 24M
    uint256 private constant amountLiqidity = 60 * (10 ** 6);   // 10% = 60M
    uint256 private constant amountTeam = 90 * (10 ** 6);       // 15% = 90M
    uint256 private constant amountMarketing = 90 * (10 ** 6);  // 15% = 90M
    uint256 private constant amountGame = 270 * (10 ** 6);      // 45% = 270M
    
    // total token seed, private, public, lidqidity 
    uint256 private _totalAmountSeed = amountSeed * TOKEN_WEI;
    uint256 private _totalAmountPrivate = amountPrivate * TOKEN_WEI;
    uint256 private _totalAmountPublic = amountPublic * TOKEN_WEI;
    uint256 private _totalAmountLiqidity = amountLiqidity * TOKEN_WEI;
    // total token for team and marketing
    uint256 private _totalAmountTeam = amountTeam * TOKEN_WEI;
    uint256 private _totalAmountMarketing = amountMarketing * TOKEN_WEI;
    // total token for game move2earn
    uint256 private _totalAmountGame = amountGame * TOKEN_WEI;

    // Specific percent allocation
    enum Allocation {
        SEED,
        PRIVATE,
        PUBLIC,
        LISTING_LIQIDITY,
        TEAM,
        MARKETING,
        GAME
    }

    // vesting schedule
    struct vestingSchedule {
        bool isValid;               /* true if an entry exists and is valid */
        uint32 cliffDuration;       /* Duration of the cliff, with respect to the grant start day, in days. */
        uint32 duration;            /* Duration of the vesting schedule, with respect to the grant start day, in days. */
        uint32 interval;            /* Duration in days of the vesting interval. */
    }   
    // token grant
    struct tokenGrant {
        bool isActive;              /* true if this vesting entry is active and in-effect entry. */
        uint32 startDay;            /* Start day of the grant, in days since the UNIX epoch (start of day). */
        uint256 amount;             /* Total number of tokens that vest. */
        address beneficiary;        /* Address of wallet that is holding the vesting schedule. */
        address grantor;            /* Grantor that made the grant */
        Allocation allocation;      /* Allocation type*/
    }
    // release schedule
    struct releaseSchedule {
        // percent release for each stage
        bool isValid;
        uint32 tgePercent;
        uint32 intervalPercent;
    }

    mapping(Allocation => vestingSchedule) private _vestingSchedules;
    mapping(Allocation => releaseSchedule) private _releaseSchedules;
    mapping(address => tokenGrant) private _tokenGrants;
    mapping(Allocation => uint256) _totalAmountAllocations;
    event VestingTokensGranted(address grantor, address beneficiary, uint256 vestingAmount, uint256 startDay);

    // init total amount for each allocation
    function _initAllocationAmount() internal {
        _totalAmountAllocations[Allocation.SEED] = _totalAmountSeed;
        _totalAmountAllocations[Allocation.PRIVATE] = _totalAmountPrivate;
        _totalAmountAllocations[Allocation.PUBLIC] = _totalAmountPublic;
        _totalAmountAllocations[Allocation.LISTING_LIQIDITY] = _totalAmountLiqidity;
        _totalAmountAllocations[Allocation.TEAM] = _totalAmountTeam;
        _totalAmountAllocations[Allocation.GAME] = _totalAmountGame;
        _totalAmountAllocations[Allocation.MARKETING] = _totalAmountMarketing;
        
    }

    // make sure allcation valid before grant vesting
    modifier onlyAllocationAmountValid(Allocation allocation, uint256 amount) {
        require(_totalAmountAllocations[allocation] >= amount, "allocation insufficient funds");
        _;
    }
    
    // make sure available amount before perform transfer
    modifier onlyIfFundsAvailableNow(address account, uint256 amount) {
        // Distinguish insufficient overall balance from insufficient vested funds balance.
        require(_fundsAreAvailableOn(account, amount, today()),
            balanceOf(account) < amount ? "insufficient funds" : "insufficient vested funds");
        _;
    }

    function _fundsAreAvailableOn(address account, uint256 amount, uint32 onDay) internal view returns (bool ok) {
        return (amount <= getAvailableAmount(account, onDay));
    }

    function _calculateGameInterval(uint256 totalAmount, uint256 numOfIntervals) 
    internal pure returns (uint256) {
        uint256[7] memory amountStage;
        uint256 amountInterval;
        // amount token release in first 3 years
        if(numOfIntervals <= THREE_YEARS) {
            return totalAmount.mul(PERCENT_THREE_YEAR).mul(numOfIntervals).div(1000).div(THREE_YEARS);
        }
        // amount token release in 6 years
        amountStage[0] = totalAmount.mul(PERCENT_THREE_YEAR).div(1000);
        if (numOfIntervals > THREE_YEARS) {
            if (numOfIntervals <= SIX_YEARS) {
                amountInterval = numOfIntervals.sub(THREE_YEARS);
                amountStage[1] = totalAmount.mul(PERCENT_SIX_YEARS).mul(amountInterval).div(1000).div(THREE_YEARS);
                return amountStage[0].add(amountStage[1]);
            }
            amountStage[1] = totalAmount.mul(PERCENT_SIX_YEARS).div(1000);
        }
        // amount token release in 9 years
        if (numOfIntervals > SIX_YEARS) {
            if (numOfIntervals <= NINE_YEARS) {
                amountStage[2] = totalAmount.mul(PERCENT_NINE_YEARS).mul(numOfIntervals - SIX_YEARS).div(1000).div(THREE_YEARS);
                return amountStage[0] + amountStage[1] + amountStage[2];
            }
            amountStage[2] = totalAmount.mul(PERCENT_NINE_YEARS).div(1000);
        }
        // amount token release in 12 years
        if (numOfIntervals > NINE_YEARS) {
            if (numOfIntervals <= TWELVE_YEARS) {
                amountStage[3] = totalAmount.mul(PERCENT_TWELVE_YEARS).mul(numOfIntervals - NINE_YEARS).div(1000).div(THREE_YEARS);
                return amountStage[0] + amountStage[1] + amountStage[2] + amountStage[3];
            }
            amountStage[3] = totalAmount.mul(PERCENT_TWELVE_YEARS).div(1000);
        }
        // amount token release in 15 years
        if (numOfIntervals > TWELVE_YEARS) {
            if (numOfIntervals <= FIFTEEN_YEARS) {
                amountStage[4] = totalAmount.mul(PERCENT_FIFTEEN_YEARS).mul(numOfIntervals - TWELVE_YEARS).div(1000).div(THREE_YEARS);
                return amountStage[0].add(amountStage[1]).add(amountStage[2]).add(amountStage[3]).add(amountStage[4]);
            }
            amountStage[4] = totalAmount.mul(PERCENT_FIFTEEN_YEARS).div(1000);        
        }
        // amount token release in 18 years
        if (numOfIntervals > FIFTEEN_YEARS) {
            if (numOfIntervals <= EIGHTEEN_YEARS) {
                amountStage[5] = totalAmount.mul(PERCENT_EIGHTEEN_YEARS).mul(numOfIntervals - FIFTEEN_YEARS).div(1000).div(THREE_YEARS);
                return amountStage[0] + amountStage[1] + amountStage[2] + amountStage[3] +
                amountStage[4] + amountStage[5];
            }
            amountStage[5] = totalAmount.mul(PERCENT_EIGHTEEN_YEARS).div(1000);            
        }
        // amount token release in 21 years
        if (numOfIntervals > EIGHTEEN_YEARS) {
            amountStage[6] = totalAmount.mul(PERCENT_TWENTY_ONE_YEARS).mul(numOfIntervals - EIGHTEEN_YEARS).div(1000).div(THREE_YEARS);  
        }
        return amountStage[0] + amountStage[1] + amountStage[2] + amountStage[3] + 
        amountStage[4] + amountStage[5] + amountStage[6];
    }

    function _updateAllocationAmount(
        Allocation allocation,
        uint256 amount) internal returns(bool) {
        _totalAmountAllocations[allocation] = _totalAmountAllocations[allocation].sub(amount);
        return true;
    }

    function _isValidAllocation(Allocation allocation) internal pure returns(bool) {
        if (allocation != Allocation.SEED 
        && allocation != Allocation.PRIVATE
        && allocation != Allocation.PUBLIC
        && allocation != Allocation.LISTING_LIQIDITY
        && allocation != Allocation.GAME
        && allocation != Allocation.TEAM
        && allocation != Allocation.MARKETING
        ) {
            return false;
        }
        return true;
    }

    function _getNotVestedAmount(address grantHolder, uint32 onDay) internal view returns (uint256 amountNotVested) {
        tokenGrant memory grant = _tokenGrants[grantHolder];
        vestingSchedule memory vesting = _vestingSchedules[grant.allocation];
        releaseSchedule memory release = _releaseSchedules[grant.allocation];
        // If there's no schedule, or before the vesting cliff, then the full amount is not vested.
        if (!grant.isActive || onDay < grant.startDay)
        {
            // None are vested (all are not vested)
            return grant.amount;
        }
        // If after end of vesting, then the not vested amount is zero (all are vested).
        else if (onDay >= grant.startDay + vesting.duration)
        {
            // All are vested (none are not vested)
            return uint256(0);
        }
        // on TGE 
        else if (onDay >= grant.startDay && onDay < grant.startDay + vesting.cliffDuration) {
            uint256 tgeAmount = grant.amount.mul(release.tgePercent).div(100);
            return grant.amount.sub(tgeAmount);
        }
        // Otherwise a fractional amount is vested.
        else
        {
            // Compute the exact number of days vested.
            uint32 effectiveDaysVested = onDay - (grant.startDay + vesting.cliffDuration);
            uint32 numOfIntervals = effectiveDaysVested / vesting.interval;
            uint256 tgeAmount = grant.amount.mul(release.tgePercent).div(100);
            uint256 intervalsAmount;      
            if(grant.allocation == Allocation.GAME) {
                intervalsAmount = _calculateGameInterval(grant.amount, numOfIntervals);
            } else {
                intervalsAmount = grant.amount.mul(release.intervalPercent).mul(numOfIntervals).div(100);
            }
            uint256 result = grant.amount.sub(tgeAmount).sub(intervalsAmount);
            return result;
            
        }
    }

    function setReleaseSchedule(
        Allocation allocation, 
        uint32 tgePercent, 
        uint32 intervalPercent) public onlyOwner returns(bool) {
        _releaseSchedules[allocation] = releaseSchedule(
            true, /*isValid*/
            tgePercent,
            intervalPercent
        );
        return true;
    }

    function setVestingSchedule(
        Allocation allocation,
        uint32 cliffDuration, uint32 duration, uint32 interval) 
        public onlyOwner returns (bool ok) {
        // Check for a valid vesting schedule given (disallow absurd values to reject likely bad input).
        require(
            duration > 0 && duration <= THIRTY_YEARS_DAYS
            && cliffDuration < duration
            && interval >= 1,
            "invalid vesting schedule"
        );
        // Make sure the duration values are in harmony with interval (both should be an exact multiple of interval).
        require(
            duration % interval == 0 && cliffDuration % interval == 0,
            "invalid cliff/duration for interval"
        );
    
        // Create and populate a vesting schedule.
        _vestingSchedules[allocation] = vestingSchedule(
            true/*isValid*/,
            cliffDuration, duration, interval
        );
        return true;
    }

    function getAvailableAllocation(Allocation allocation) public view returns(uint256) {
        return _totalAmountAllocations[allocation];
    }

    function hasVestingSchedule(Allocation allocation) public view returns (bool) {
        return _vestingSchedules[allocation].isValid;
    }

    function hasReleaseSchedule(Allocation allocation) public view returns (bool) {
        return _releaseSchedules[allocation].isValid;
    }

    function grantVestingTokens( address beneficiary, uint256 vestingAmount, uint32 startDay, Allocation allocation) 
        public onlyOwner 
        onlyAllocationAmountValid(allocation, vestingAmount)
        returns(bool ok) {
        // Make sure no prior grant is in effect.
        require(!_tokenGrants[beneficiary].isActive, "grant already exists");
        // Check allocation valid
        require(_isValidAllocation(allocation), "invalid allocation");
        // Make sure start day is in future or today
        require(startDay >= today(), "start day must be greater than the current date");
        // Check for valid vestingAmount
        require(vestingAmount > 0 && startDay < JAN_1_3000_DAYS, "invalid vesting params");
        // Make sure the vesting schedule we are about to use is valid.
        require(hasVestingSchedule(allocation), "no such vesting schedule");
        require(_totalAmountAllocations[allocation] >= vestingAmount, "allocation insufficiant fund");
        // Transfer the total number of tokens from grantor into the account's holdings.
        _transfer(msg.sender, beneficiary, vestingAmount);
        // Create and populate a token grant, referencing vesting schedule.
        _tokenGrants[beneficiary] = tokenGrant(
            true/*isActive*/,
            startDay,
            vestingAmount,
            beneficiary, /* The wallet address where the vesting schedule is kept. */
            msg.sender, /* granter */
            allocation
        );
        _updateAllocationAmount(allocation, vestingAmount);
        // Emit the event and return success.
        emit VestingTokensGranted(msg.sender, beneficiary, vestingAmount, startDay);
        return true;
    }

    function getAvailableAmount(address grantHolder, uint32 onDay) public view returns (uint256 amountAvailable) {
        uint256 totalTokens = balanceOf(grantHolder);
        uint256 vested = totalTokens.sub(_getNotVestedAmount(grantHolder, onDay));
        return vested;
    }

    function transfer(address to, uint256 value) override public onlyIfFundsAvailableNow(msg.sender, value) returns (bool ok) {
        return super.transfer(to, value);
    }

    function approve(address spender, uint256 value) override public onlyIfFundsAvailableNow(msg.sender, value) returns (bool ok) {
        return super.approve(spender, value);
    }

    function today() virtual public view returns (uint32 dayNumber) {
        return uint32(block.timestamp / SECONDS_PER_DAY);
    }

}

// File: contracts/GovernaceToken.sol


pragma solidity ^0.8.4;





contract GovernanceToken is Pausable, Ownable, TokenVesting {
    constructor() ERC20("Gofit Token", "GOF") {
        _initAllocationAmount();
        _mint(msg.sender, 600000000 * 10 ** decimals());
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(from, to, amount);
    }
}
