    "contracts/LEVEL.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED









pragma solidity ^0.8.15;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "contracts/libraries/SafeMath8.sol";
import "contracts/interfaces/IOracle.sol";
import "contracts/libraries/Operator.sol";
import "contracts/interfaces/IUniswapV2Factory.sol";
import "contracts/interfaces/IUniswapV2Pair.sol";
import "contracts/interfaces/IUniswapV2Router02.sol";
import "contracts/interfaces/ITaxOffice.sol";

contract LEVEL is ERC20Burnable, Operator {
    using SafeMath8 for uint8;
    using SafeMath for uint256;


    // Have the rewards been distributed to the pools
    bool public rewardPoolDistributed = false;

    //mutables
    address public oracle;
    address public taxCollectorAddress;
    address public admin;
    address public PairLodgeShare;
    address public taxOffice = address(0);

    bool public started;

    // immutables
    IUniswapV2Router02 public immutable uniswapV2Router;

    IERC20 public immutable USDC;
    IERC20 public immutable WETH;
    address public immutable PairWETH;
    address public immutable PairUSDC;
    address public immutable genesisAddress;
    address public immutable boardroom;
    address public immutable treasury;

    mapping(address => bool) public automatedMarketMakerPairs;
    // whitelist fee and whitelist bots
    mapping(address => bool) public whitelist;

    // tax rate
    uint256 public taxRate;

    uint256 public staticTaxRate = 1500;

    uint256 public dynamicTaxRate;
    bool public enableDynamicTax = false;
    uint256 public constant MAX_TAX_RATE = 2500;

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event SwapAndLiquify(uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiqudity);
    // modifiers
    modifier onlyTaxCollector() {
        require(taxCollectorAddress == _msgSender(), "caller is not the taxCollector");
        _;
    }
    modifier onlyTaxOffice() {
        require(taxOffice == _msgSender(), "caller is not the taxOffice");
        _;
    }

    modifier onlyAdmin() {
        require(admin == _msgSender(), "You are not the admin");
        _;
    }

    function start() external onlyOperator {
        require(!started, "already started");
        uint256 balanceLEVEL = balanceOf(address(this));
        uint256 balanceWETH = WETH.balanceOf(address(this));
        _approve(address(this), address(uniswapV2Router), balanceLEVEL);
        WETH.approve(address(uniswapV2Router), WETH.balanceOf(address(this)));
        uniswapV2Router.addLiquidity(
            address(this),
            address(WETH),
            balanceLEVEL,
            balanceWETH,
            balanceLEVEL,
            balanceWETH,
            msg.sender,
            block.timestamp
        );
        started = true;
    }

    constructor(
        address _router,
        address _genesisAddress,
        address _boardroom,
        address _treasury,
        address _USDC
    ) ERC20("LEVEL", "LEVEL") {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(_router);
        _mint(address(this), 1 ether);

        // set router
        uniswapV2Router = _uniswapV2Router;

        WETH = IERC20(_uniswapV2Router.WETH());
        USDC = IERC20(_USDC);

        // Create pairs
        PairWETH = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(
            address(this),
            _uniswapV2Router.WETH()
        );

        PairUSDC = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(
            address(this),
            address(USDC)
        );
        _setAutomatedMarketMakerPair(PairWETH, true);
        _setAutomatedMarketMakerPair(PairUSDC, true);

        // set admin and tax collector role
        admin = msg.sender;
        taxCollectorAddress = msg.sender;

        // set core contracts
        genesisAddress = _genesisAddress;
        treasury = _treasury;
        boardroom = _boardroom;

        // whitelist core contracts from fee
        whitelist[genesisAddress] = true;
        whitelist[treasury] = true;
        whitelist[boardroom] = true;
        whitelist[msg.sender] = true;
        whitelist[address(this)] = true;

        // distribute the rewards
        rewardPoolDistributed = true;
    }

    function _getPrice() internal view returns (uint256 _Price) {
        try IOracle(oracle).twap(address(this), 1e18) returns (uint144 _price) {
            return uint256(_price);
        } catch {
            revert("failed to fetch token price from Oracle");
        }
    }

    function isContract(address _addr) private view returns (bool) {
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
        return (size > 0);
    }

    // set whitelist
    function setWhiteList(address _WhiteList) public onlyAdmin {
        require(isContract(_WhiteList) == true, "only contracts can be whitelisted");
        require(
            address(uniswapV2Router) != _WhiteList,
            "set tax to 0 if you want to remove fee from trading"
        );
        require(PairWETH != _WhiteList, "set tax to 0 if you want to remove fee from trading");
        require(PairUSDC != _WhiteList, "set tax to 0 if you want to remove fee from trading");
        require(PairLodgeShare != address(0), "set PairLodgeShare first");
        require(
            PairLodgeShare != _WhiteList,
            "set tax to 0 if you want to remove fee from trading"
        );
        whitelist[_WhiteList] = true;
    }

    // setPairLodgeShare function gets called from share token
    function setPairLodgeShare(address _pairLodgeShare) public onlyAdmin {
        require(_pairLodgeShare != address(0), "already set, only one");
        PairLodgeShare = _pairLodgeShare;
        _setAutomatedMarketMakerPair(PairLodgeShare, true);
    }

    function setAdmin(address _admin) public onlyAdmin {
        admin = _admin;
    }

    function setOracle(address _oracle) public onlyAdmin {
        require(_oracle != address(0), "oracle address cannot be 0 address");
        oracle = _oracle;
    }

    function setTaxCollectorAddress(address _taxCollectorAddress) public onlyTaxCollector {
        require(
            _taxCollectorAddress != address(0),
            "taxCollectorAddress address cannot be 0 address"
        );
        taxCollectorAddress = _taxCollectorAddress;
    }

    function mint(address recipient_, uint256 amount_) public onlyOperator returns (bool) {
        uint256 balanceBefore = balanceOf(recipient_);
        _mint(recipient_, amount_);
        uint256 balanceAfter = balanceOf(recipient_);
        return balanceAfter > balanceBefore;
    }

    function burn(uint256 amount) public override {
        super.burn(amount);
    }

    function burnFrom(address account, uint256 amount) public override onlyOperator {
        super.burnFrom(account, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool successFlag) {
        if (automatedMarketMakerPairs[recipient] && whitelist[sender] == false) {
            uint256 taxAmount = amount.mul(getCurrentTaxRate()).div(10000);
            uint256 amountAfterTax = amount.sub(taxAmount);
            _handleTax(sender, taxAmount);
            _transfer(sender, recipient, amountAfterTax);
        } else {
            super._transfer(sender, recipient, amount);
        }
        _approve(
            sender,
            _msgSender(),
            allowance(sender, _msgSender()).sub(amount, "ERC20: transfer amount exceeds allowance")
        );
        return true;
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        if (whitelist[_msgSender()] == false && automatedMarketMakerPairs[recipient]) {
            uint256 taxAmount = amount.mul(getCurrentTaxRate()).div(10000);
            uint256 amountAfterTax = amount.sub(taxAmount);
            _transfer(_msgSender(), taxCollectorAddress, taxAmount);
            _transfer(_msgSender(), recipient, amountAfterTax);
        } else {
            super._transfer(_msgSender(), recipient, amount);
        }

        return true;
    }

    function getCurrentTaxRate() public returns (uint256) {
        taxRate = staticTaxRate;
        if (enableDynamicTax == true) {
            _updateDynamicTaxRate();
            if (dynamicTaxRate > MAX_TAX_RATE) {
                dynamicTaxRate = MAX_TAX_RATE;
            }
            taxRate = dynamicTaxRate;
        }
        return taxRate;
    }

    function setStaticTaxRate(uint256 _taxRate) external onlyTaxOffice {
        require(_taxRate <= MAX_TAX_RATE, "Error: Max tax rate exceeded.");
        staticTaxRate = _taxRate;
    }

    function setEnableDynamicTax(bool _enableDynamicTax) external onlyTaxOffice {
        enableDynamicTax = _enableDynamicTax;
    }

    function _updateDynamicTaxRate() internal {
        dynamicTaxRate = ITaxOffice(taxOffice).calculateMainTokenTax();
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function setTaxOffice(address _taxOffice) public onlyAdmin {
        require(_taxOffice != address(0), "Error: Zero address");
        whitelist[_taxOffice] = true;
        taxOffice = _taxOffice;
    }

    function getUSDCPAIR() public view returns (address) {
        return PairUSDC;
    }

    function _handleTax(address _sender, uint256 _amount) internal {
        _transfer(_sender, address(taxOffice), _amount);
        ITaxOffice(taxOffice).handleMainTokenTax(_amount);
    }
}
"
    },
    "contracts/libraries/Operator.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED





pragma solidity ^0.8.15;

import "@openzeppelin/contracts/access/Ownable.sol";


abstract contract Operator is Context, Ownable {
    address private _operator;

    event OperatorTransferred(address indexed previousOperator, address indexed newOperator);

    constructor() {
        _operator = _msgSender();
        emit OperatorTransferred(address(0), _operator);
    }

    function operator() public view returns (address) {
        return _operator;
    }

    modifier onlyOperator() {
        require(_operator == msg.sender, "operator: caller is not the operator");
        _;
    }

    function isOperator() public view returns (bool) {
        return _msgSender() == _operator;
    }

    function transferOperator(address newOperator_) public onlyOwner {
        _transferOperator(newOperator_);
    }

    function _transferOperator(address newOperator_) internal {
        require(newOperator_ != address(0), "operator: zero address given for new operator");
        emit OperatorTransferred(address(0), newOperator_);
        _operator = newOperator_;
    }
}"
    },
