struct Node {
    uint64 id;
    uint64 next;
    uint8 juniorNum;    
    uint64 superior;
}

struct Share {
    uint64 id;
    uint32 monthBuys;
    uint32 lastBuyTime;
    uint32 lastBuys;
    uint256 lastEarns;
    uint256 pendingEarn;
        
    uint32 lastEarnTime;
    uint32 lastShareTime;
    uint256 monthEarn;
    uint256 monthEarns;
    uint8 shareIndex;
}

struct Intercept{
    uint256 pendingIntercept;
    uint64[] interceptNodes;
    uint8[] interceptBuys;
    uint256[] interceptNodeFunds;
