contract BTLMachine is ReentrancyGuard{
    using SafeERC20 for IERC20;
    address public owner;

    address public btl;
    address public usdt;
    address private cSigner;
    address public vault;
    address public mineV1;

    mapping(address => uint256) public mineNonces;

    bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)");
    bytes32 constant public HARVEST_TYPEHASH = keccak256("harvest(address miner,uint256 amount,uint256 nonce)");

    event HARVEST(address miner, uint256 amount, uint256 nonce, uint256 timestamp);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    
    modifier onlyOwner() {
        require(owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    constructor(address _btl, address _usdt, address _signer, address _vault, address _owner){
    // function initialize(address _btl, address _usdt, address _signer, address _vault, address _owner) public initializer{
        btl = _btl;
        usdt = _usdt;
        cSigner = _signer;
        vault = _vault;
        owner = _owner;
    }

    function setMineV1(address _mine) public onlyOwner{
        require(IBTLMine(_mine).usdt()==usdt, "error BTLMine");
        mineV1 = _mine;
    }

    function setBtl(address _btl) public onlyOwner{
        btl = _btl;
    }

    function setVault(address _vault) public onlyOwner{
        vault = _vault;
    }

    function setSigner(address _signer) public onlyOwner{
        require(_signer!=address(0),"zero address");
        cSigner = _signer;
    }

    function harvest(uint256 amount, uint8 v, bytes32 r, bytes32 s) external nonReentrant{
        if(mineNonces[msg.sender]==0){
            if(IBTLMine(mineV1).mineNonces(msg.sender)>0){
                mineNonces[msg.sender]= IBTLMine(mineV1).mineNonces(msg.sender);
            }
        }
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes("BTLMine")), keccak256(bytes("1")),block.chainid, address(this)));
        bytes32 structHash = keccak256(abi.encode(HARVEST_TYPEHASH, msg.sender, amount, 1+mineNonces[msg.sender]++));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));

        require(ecrecover(digest, v, r, s) == cSigner, "Harvest: Invalid signer");

        IERC20(btl).safeTransfer( msg.sender, amount);
        emit HARVEST(msg.sender, amount, mineNonces[msg.sender], block.timestamp);
    }

    function emergenceWithdraw(address token) external onlyOwner{
        IERC20(token).transfer(msg.sender, IERC20(token).balanceOf(address(this)));
    }

       /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

     /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    } 

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }
}
