// File: contracts/v2/LpStakeManager.sol


pragma solidity ^0.8.0;





contract LpStakeManager is ReentrancyGuard {
    using SafeMath for uint256;

    // The Moox token
    IBEP20 public immutable moox;

    // The LP token for MOOX/BUSD
    IPancakeswapPair public immutable lpToken;

    // Total deposited MOOX for awarding
    uint256 public totalDepositedMoox;

    // Deposited MOOX amount on the last reward update
    uint256 public lastRewardDepositedMoox;

    // Accumulated moox per share
    uint256 public accMooxPerShare;

    // Info of each user that stakes tokens
    mapping(address => UserInfo) public userInfo;

    struct UserInfo {
        uint256 amount; // How many MOOX/BUSD LP tokens the user has provided
        uint256 rewardDebt; // Reward debt
    }

    event ManualMooxDeposit(address indexed user, uint256 amount);
    event Deposit(address indexed user, uint256 amount, uint256 mooxAward);
    event Withdraw(address indexed user, uint256 amount, uint256 mooxAward);
    event EmergencyWithdraw(address indexed user, uint256 amount);

    constructor(IBEP20 _moox, IPancakeswapPair _lpToken) {
        moox = _moox;
        lpToken = _lpToken;
    }

    function mooxDeposit(uint256 _amount) external nonReentrant {
        require(msg.sender == address(moox), 'StakeManager: Not allowed');

        totalDepositedMoox = totalDepositedMoox.add(_amount);
    }

    function manualMooxDeposit(uint256 _amount) external nonReentrant {
        require(lpToken.balanceOf(address(this)) > 0, 'StakeManager: No LP token staked');
        require(moox.transferFrom(msg.sender, address(this), _amount), 'StakeManager: Moox transfer failed');

        totalDepositedMoox = totalDepositedMoox.add(_amount);

        emit ManualMooxDeposit(msg.sender, _amount);
    }

    /**
     * @notice Deposit staked tokens and collect reward tokens (if any)
     * @param _amount: amount to deposit
     */
    function deposit(uint256 _amount) external nonReentrant {
        UserInfo storage user = userInfo[msg.sender];

        _updatePool();

        uint256 pending = 0;

        if (user.amount > 0) {
            pending = user.amount.mul(accMooxPerShare).div(1e12).sub(user.rewardDebt);

            if (pending > 0) {
                safeMooxTransfer(address(msg.sender), pending);
            }
        }

        if (_amount > 0) {
            user.amount = user.amount.add(_amount);
            require(
                lpToken.transferFrom(address(msg.sender), address(this), _amount),
                'LpStakeManager: Cannot transfer LP token'
            );
        }

        user.rewardDebt = user.amount.mul(accMooxPerShare).div(1e12);

        emit Deposit(msg.sender, _amount, pending);
    }

    /**
     * @notice Withdraw staked tokens and collect reward
     * @param _amount: amount to withdraw
     */
    function withdraw(uint256 _amount) external nonReentrant {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= _amount, 'StakeManager: Withdraw amount gt user.amount');

        _updatePool();

        uint256 pending = user.amount.mul(accMooxPerShare).div(1e12).sub(user.rewardDebt);

        if (_amount > 0) {
            user.amount = user.amount - _amount;
            require(lpToken.transfer(address(msg.sender), _amount), 'LpStakeManager: Cannot transfer LP token');
        }

        if (pending > 0) {
            safeMooxTransfer(address(msg.sender), pending);
        }

        user.rewardDebt = user.amount.mul(accMooxPerShare).div(1e12);

        emit Withdraw(msg.sender, _amount, pending);
    }

    /**
     * @dev Update reward variables of the pool to be up-to-date.
     */
    function _updatePool() internal {
        uint256 lpTokenSupply = lpToken.balanceOf(address(this));

        if (totalDepositedMoox <= lastRewardDepositedMoox) {
            return;
        }

        if (lpTokenSupply <= 0) {
            lastRewardDepositedMoox = totalDepositedMoox;
            return;
        }

        uint256 mooxReward = totalDepositedMoox.sub(lastRewardDepositedMoox);
        accMooxPerShare = accMooxPerShare.add(mooxReward.mul(1e12).div(lpTokenSupply));
        lastRewardDepositedMoox = totalDepositedMoox;
    }

    /**
     * @dev Safe moox transfer function, just in case if rounding error causes pool to not have enough moox.
     * @param _to: address
     * @param _amount: amount
     */
    function safeMooxTransfer(address _to, uint256 _amount) private {
        uint256 mooxBalance = moox.balanceOf(address(this));

        if (_amount > mooxBalance) {
            require(moox.transfer(_to, mooxBalance), 'LpStakeManager: Cannot transfer MOOX');
        } else {
            require(moox.transfer(_to, _amount), 'LpStakeManager: Cannot transfer MOOX');
        }
    }

    /**
     * @dev View function to see pending reward on frontend.
     * @param _user: user address
     *
     * @return Pending reward for a given user
     */
    function pendingReward(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 lpTokenSupply = lpToken.balanceOf(address(this));

        if (totalDepositedMoox > lastRewardDepositedMoox && lpTokenSupply != 0) {
            uint256 mooxReward = totalDepositedMoox.sub(lastRewardDepositedMoox);
            uint256 adjustedMooxPerShare = accMooxPerShare.add(mooxReward.mul(1e12).div(lpTokenSupply));

            return user.amount.mul(adjustedMooxPerShare).div(1e12).sub(user.rewardDebt);
        } else {
            return user.amount.mul(accMooxPerShare).div(1e12).sub(user.rewardDebt);
        }
    }

    /**
     * @notice Withdraw staked tokens without caring about rewards rewards
     * @dev Needs to be for emergency.
     */
    function emergencyWithdraw() external nonReentrant {
        UserInfo storage user = userInfo[msg.sender];
        uint256 amountToTransfer = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;

        if (amountToTransfer > 0) {
            require(
                lpToken.transfer(address(msg.sender), amountToTransfer),
                'LpStakeManager: Cannot transfer LP token'
            );
        }

        emit EmergencyWithdraw(msg.sender, user.amount);
    }
}

// File: contracts/v2/LiquidityProxy.sol


pragma solidity ^0.8.0;






contract LiquidityProxyV2 is ReentrancyGuard {
    using SafeMath for uint256;

    IBEP20 public immutable moox;
    IBEP20 public immutable busd;
    IPancakeswapPair public immutable pair;
    IPancakeswapRouter public immutable router;

    constructor(
        IBEP20 _moox,
        IBEP20 _busd,
        IPancakeswapPair _pair,
        IPancakeswapRouter _router
    ) {
        moox = _moox;
        busd = _busd;
        pair = _pair;
        router = _router;
    }

    function addLiquidity(
        uint256 _amountMooxDesired,
        uint256 _amountBusdDesired,
        uint256 _amountMooxMin,
        uint256 _amountBusdMin,
        address _to,
        uint256 _deadline
    )
        external
        nonReentrant
        returns (
            uint256 amountMoox,
            uint256 amountBusd,
            uint256 liquidity
        )
    {
        if (moox.allowance(address(this), address(router)) < _amountMooxDesired) {
            require(moox.approve(address(router), type(uint256).max), "LiquidityProxyV2: Cannot approve MOOX");
        }

        if (busd.allowance(address(this), address(router)) < _amountBusdDesired) {
            require(busd.approve(address(router), type(uint256).max), "LiquidityProxyV2: Cannot approve BUSD");
        }

        require(moox.transferFrom(msg.sender, address(this), _amountMooxDesired), "LiquidityProxyV2: Cannot transfer MOOX");
        require(busd.transferFrom(msg.sender, address(this), _amountBusdDesired), "LiquidityProxyV2: Cannot transfer BUSD");

        (amountMoox, amountBusd, liquidity) = router.addLiquidity(
            address(moox),
            address(busd),
            _amountMooxDesired,
            _amountBusdDesired,
            _amountMooxMin,
            _amountBusdMin,
            _to,
            _deadline
        );

        if (_amountMooxDesired > amountMoox) {
            require(
                moox.transfer(msg.sender, _amountMooxDesired.sub(amountMoox)),
                'LiquidityProxyV2: MOOX_TRANSFER_FAILED'
            );
        }

        if (_amountBusdDesired > amountBusd) {
            require(
                busd.transfer(msg.sender, _amountBusdDesired.sub(amountBusd)),
                'LiquidityProxyV2: BUSD_TRANSFER_FAILED'
            );
        }
    }

    function removeLiquidityWithPermit(
        uint256 _liquidity,
        uint256 _amountMooxMin,
        uint256 _amountBusdMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256 amountMoox, uint256 amountBusd) {
        uint256 value = _approveMax ? type(uint256).max : _liquidity;

        pair.permit(msg.sender, address(this), value, _deadline, _v, _r, _s);

        (amountMoox, amountBusd) = removeLiquidity(_liquidity, _amountMooxMin, _amountBusdMin, _to, _deadline);
    }

    function removeLiquidity(
        uint256 _liquidity,
        uint256 _amountMooxMin,
        uint256 _amountBusdMin,
        address _to,
        uint256 _deadline
    ) public nonReentrant returns (uint256 amountMoox, uint256 amountBusd) {
        require(pair.transferFrom(msg.sender, address(this), _liquidity), "LiquidityProxyV2: Cannot transfer LP token");

        if (pair.allowance(address(this), address(router)) < _liquidity) {
            require(pair.approve(address(router), type(uint256).max), "LiquidityProxyV2: Cannot approve LP token");
        }

        (amountMoox, amountBusd) = router.removeLiquidity(
            address(moox),
            address(busd),
            _liquidity,
            _amountMooxMin,
            _amountBusdMin,
            address(this),
            _deadline
        );

        require(moox.transfer(_to, amountMoox), 'LiquidityProxyV2: MOOX_TRANSFER_FAILED');
        require(busd.transfer(_to, amountBusd), 'LiquidityProxyV2: BUSD_TRANSFER_FAILED');
    }
}

