                uint256 drawFee = 0;

                _balances[protocolFeeReceiver] = _balances[protocolFeeReceiver].add(protocolFee);

                emit Transfer(sender, protocolFeeReceiver, protocolFee);

                if (IBEP20(pancakeswapBusdPair).balanceOf(address(lpStakeManager)) > 0) {
                    lpStakeFee = protocolFee.mul(5);
                    _balances[address(lpStakeManager)] = _balances[address(lpStakeManager)].add(lpStakeFee);
                    lpStakeManager.mooxDeposit(lpStakeFee);

                    emit Transfer(sender, address(lpStakeManager), lpStakeFee);
                }

                if (recipient != address(drawManager)) {
                    drawFee = protocolFee.mul(4);
                    _balances[address(drawManager)] = _balances[address(drawManager)].add(drawFee);

                    emit Transfer(sender, address(drawManager), drawFee);
                }

                deductedAmount = protocolFee.add(lpStakeFee).add(drawFee);
            }

            if (sender == pancakeswapBusdPair && recipient != address(drawManager)) {
                drawManager.processBuyAward(amount);
            }
        }

        uint256 transferAmount = amount.sub(deductedAmount);

        _balances[recipient] = _balances[recipient].add(transferAmount);

        emit Transfer(sender, recipient, transferAmount);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
     *
     * This is internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), 'BEP20: approve from the zero address');
        require(spender != address(0), 'BEP20: approve to the zero address');

        _allowances[owner][spender] = amount;

        emit Approval(owner, spender, amount);
    }

    function deposit(uint256 amount) external {
        require(amount > 0, 'Moox: Amount zero');
        require(MOOX_V1.transferFrom(msg.sender, address(this), amount), 'Moox: Cannot transfer moox V1');

        uint256 v2Amount = v2AmountForV1(amount);
        _balances[msg.sender] = _balances[msg.sender].add(v2Amount);

        emit Deposit(msg.sender, amount, v2Amount);
        emit Transfer(address(0), msg.sender, v2Amount);
    }

    function withdraw(uint256 amount) external {
        require(amount > 0, 'Moox: Amount zero');
        require(_balances[msg.sender] >= amount, 'Moox: Withdraw amount exceeds balance');

        _balances[msg.sender] = _balances[msg.sender].sub(amount);

        emit Transfer(msg.sender, address(0), amount);

        uint256 transferAmount = v1AmountForV2(amount);
        uint256 mooxBalance = MOOX_V1.balanceOf(address(this));

        if (mooxBalance < transferAmount) {
            transferAmount = mooxBalance;
        }

        require(MOOX_V1.transfer(msg.sender, transferAmount), 'Moox: Cannot transfer MOOX V1');

        emit Withdrawal(msg.sender, amount, transferAmount);
    }

    function v2AmountForV1(uint256 v1Amount) public view returns (uint256) {
        return v1Amount.mul(INITIAL_MOOX_SUPPLY).div(MOOX_V1.totalSupply()).mul(50);
    }

    function v1AmountForV2(uint256 v2Amount) public view returns (uint256) {
        return v2Amount.div(50).mul(MOOX_V1.totalSupply()).div(INITIAL_MOOX_SUPPLY);
    }

    function setProtocolFeeReceiverSetter(address _setter) external {
        require(_setter != address(0), 'Moox: Invalid address');
        require(msg.sender == protocolFeeReceiverSetter, 'Moox: Not allowed');

        protocolFeeReceiverSetter = _setter;

        emit SetProtocolFeeReceiverSetter(_setter);
    }

    function setProtocolFeeReceiver(address _receiver) external {
        require(_receiver != address(0), 'Moox: Invalid address');
        require(msg.sender == protocolFeeReceiverSetter, 'Moox: Not allowed');

        protocolFeeReceiver = _receiver;

        emit SetProtocolFeeReceiver(msg.sender, _receiver);
    }
}
