library FullMath {
    function fullMul(uint256 x, uint256 y)
        internal
        pure
        returns (uint256 l, uint256 h)
    {
        unchecked {
            uint256 mm = mulmod(x, y, ~uint256(0));
            l = x * y;
            h = mm - l;
            if (mm < l) h -= 1;
        }
