    "contracts/core/staking/EGoldMining.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.2;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "../../interfaces/iEGoldMinerNFT.sol";
import "../../interfaces/iEGoldMiningFarm.sol";
import "../../interfaces/iEGoldCtr.sol";
import "../../interfaces/iEGoldToken.sol";

contract EGoldMining is AccessControl, Pausable  {
    using SafeMath for uint256;

    iEGoldMinerNFT miner;

    iEGoldMiningFarm farm;

    iEGoldCtr ctr;

    iEGoldToken rewardToken;

    address public wearhouse;

    uint256 public wearhouseRate;

    uint256 private rate;

    mapping ( uint256 => address ) public nftOwner;

    mapping ( address => mapping ( uint256 => userStruct )) public userData; // Possible hack

    struct userStruct{
        uint256 hashPowerTokens;
        uint256 depositBlock;
    }

    event stakeEvent ( uint256 indexed _id , address _sender  , uint256 hashpowToken );

    constructor(
        address _miner,
        address _farm,
        address _rewardToken,
        address _DFA
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, _DFA);

        miner = iEGoldMinerNFT(_miner);
        farm = iEGoldMiningFarm(_farm);
        rewardToken = iEGoldToken(_rewardToken);
    }

    function setRate( uint256 _rate ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        rate = _rate;
    }

    function setCtr( address _ctr ) external onlyRole(DEFAULT_ADMIN_ROLE){
        ctr = iEGoldCtr(_ctr);
    }

    function setWearhouse( address _wh ) external onlyRole(DEFAULT_ADMIN_ROLE){
        wearhouse = _wh;
    }

    function setWearhouserate( uint256 _whr ) external onlyRole(DEFAULT_ADMIN_ROLE){
        wearhouseRate = _whr;
    }

    function fetchRate( ) external view returns ( uint256 ){
        return rate;
    }

    function stakeNFT( uint256 _id ) external returns ( bool ){
        iStakeNFT(_id );
        return true;
    }

    function stakeNFTBulk( uint256[] memory _id ) external returns ( bool ){
        for (uint256 i = 0; i <= _id.length ; i += 1 ) {  //for loop example
            iStakeNFT(_id[i] );
        }
        return true;
    }

    function unstakeNFT( uint256 _id) external returns ( bool ){
        iunStakeNFT(_id);
        return true;
    }

    function unstakeNFTBulk( uint256[] memory _id) external returns ( bool ){
        for (uint256 i = 0; i <= _id.length ; i += 1 ) {  //for loop example
            iunStakeNFT(_id[i]);
        }
        return true;
    }

    function unstakeNFTEmmergency( uint256 _id) external returns ( bool ){
        iunStakeNFTEmergency(_id);
        return true;
    }

    function unstakeNFTEmmergencyBulk( uint256[] memory _id) external returns ( bool ){
        for (uint256 i = 0; i <= _id.length ; i += 1 ) {  //for loop example
            iunStakeNFTEmergency(_id[i]);
        }
        return true;
    }

    function iStakeNFT( uint256 _id ) internal {
        uint256 hashpower = miner.fetchMinerhashRate(_id);
        require( ctr.fetchCtr(_id) <= ctr.fetchMaxCtr() , "Max counter for state hit"); // Remove counter from here
        require( ctr.fetchCtr(_id) < ctr.fetchMaxCtr().div(2) , "Max counter for state hit"); // Remove counter from here
        uint256 hashpowToken = hashpower.mul(rate);
        userData[msg.sender][_id].hashPowerTokens = hashpowToken;
        userData[msg.sender][_id].depositBlock = block.number;
        miner.transferFrom( msg.sender , address(this) , _id );
        farm.deposit(0 , hashpowToken , _id);

        emit stakeEvent ( _id , msg.sender , hashpowToken );
        nftOwner[_id] = msg.sender;
    }

    function iunStakeNFT( uint256 _id ) internal {
        require( nftOwner[_id] == msg.sender ,"Owner Mismatch ");
        require( ctr.fetchCtr(_id) <= ctr.fetchMaxCtr() , "Max counter for state hit"); // Remove counter from here
        uint256 hashpowToken = userData[msg.sender][_id].hashPowerTokens;
        uint256 rbase = farm.withdraw(0, hashpowToken , _id);
        uint256 residue;
        uint256 whCost;
        uint256 reward;
        if ( ctr.fetchCtr(_id) == 0 ){
            reward = rbase;
        }else{
            reward = rbase.sub( (rbase.mul(ctr.fetchCtr(_id))).div(ctr.fetchMaxCtr()));
            residue = rbase.sub(reward);
        }
        // uint256 powerCost = miner.fetchMinerpowerFactor(_id).mul(block.number.sub(userData[msg.sender][_id].depositBlock));
        uint256 powerCost = (miner.fetchMinerpowerFactor(_id).mul(rbase)).div(miner.fetchMinerhashRate(_id));

        whCost = (rbase.mul(wearhouseRate)).div(10000);
        reward = reward.sub(whCost);
        ctr.incrementCtr(_id);
        userData[msg.sender][_id].hashPowerTokens = 0;
        userData[msg.sender][_id].depositBlock = 0;

        rewardToken.transferFrom(msg.sender, address(this),powerCost);
        rewardToken.burn(address(this),powerCost);
        rewardToken.burn(address(this),residue);

        rewardToken.transfer( wearhouse , whCost);
        rewardToken.transfer( msg.sender , reward);
        miner.transferFrom( address(this), msg.sender , _id );
    }

    function iunStakeNFTEmergency( uint256 _id ) internal {
        require( nftOwner[_id] == msg.sender ,"Owner Mismatch ");
        farm.emergencyWithdraw(0 , _id);
        miner.transferFrom( address(this), msg.sender , _id  );
        ctr.incrementCtr(_id);
        userData[msg.sender][_id].hashPowerTokens = 0;
        userData[msg.sender][_id].depositBlock = 0;
    }

}
"
