    "contracts/NAI.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './interface/IUniswapV2Router02.sol';
import './interface/IUniswapV2Factory.sol';
import './Swap.sol';
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import './utils/Constants.sol';

contract NAI is ERC20 {
    mapping(address => bool) private _whiteList;

    using SafeMath for uint256;

    IUniswapV2Router02 private uniswapRouter;

    address private marketAddress;

    address private pairAddress;

    Swap private swap;

    address private _admin;

    uint256 public first;

    uint256 public kill;

    constructor(address swapAddress_, address marketAddress_) ERC20("NAI", "NAI") {
        _mint(0xA4367724D89FeDfDDde513516b0960feC67F7Af9, 1000000 * 10 ** decimals());
        _whiteList[address(this)] = true;
        _whiteList[msg.sender] = true;
        _whiteList[Constants.uniswapRouterAddress] = true;
        _whiteList[swapAddress_] = true;
        _whiteList[address(0)] = true;

        _whiteList[address(0xA4367724D89FeDfDDde513516b0960feC67F7Af9)] = true;

        _whiteList[Constants.bonusAddress] = true;
        _whiteList[Constants.zeroAddress] = true;

        uniswapRouter = IUniswapV2Router02(Constants.uniswapRouterAddress);
        swap = Swap(swapAddress_);
        pairAddress = IUniswapV2Factory(uniswapRouter.factory()).createPair(address(this), Constants.usdtAddress);
        marketAddress = marketAddress_;
        _admin = msg.sender;
        kill = 30;
    }

    function setKing(uint256 newValue) public {
        require(msg.sender == _admin, "Unauthorized");
        kill = newValue;
    }

    function setMarketAddress(address address_) public {
        require(msg.sender == _admin, "Unauthorized");
        marketAddress = address_;
    }

    function addWhiteList(address address_) public {
        require(msg.sender == _admin, "Unauthorized");
        _whiteList[address_] = true;
    }

    function removeWhiteList(address address_) public {
        require(msg.sender == _admin, "Unauthorized");
        _whiteList[address_] = false;
    }

    function getPairAddress() external view returns (address) {
        return pairAddress;
    }

    function setSwap(address swapAddress_) public {
        require(msg.sender == _admin, "Unauthorized");
        swap = Swap(swapAddress_);
        _whiteList[swapAddress_] = true;
    }

    function getSwapAddress() external view returns (address) {
        return address(swap);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "ERC20: transfer amount must be greater than zero");
        if (to == pairAddress && balanceOf(address(pairAddress)) == 0) {
            first = block.number;
        }
        if (from == pairAddress && block.number < first + kill) {
            return super._transfer(from, address(0), amount);
        }
        if (_whiteList[from] == false && _whiteList[to] == false) {
            uint256 feeAmount = amount.mul(6).div(100);
            if (from == pairAddress) {
                require(balanceOf(pairAddress) != 0, "pairAddress zero");
                super._transfer(from, address(swap), feeAmount);
                swap.addAcLine(to, feeAmount, 1, false);
                super._transfer(from, to, amount - feeAmount);
            } else if (to == pairAddress) {
                require(balanceOf(pairAddress) != 0, "pairAddress zero");
                super._transfer(from, address(swap), feeAmount);
                swap.addAcLine(from, feeAmount, 2, true);
                super._transfer(from, to, amount - feeAmount);
            } else {
                super._transfer(from, marketAddress, feeAmount);
                super._transfer(from, to, amount - feeAmount);
                swap.toBc(from);
            }
        } else {
            super._transfer(from, to, amount);
        }

    }
}"
    },
    "contracts/interface/IUniswapV2Router02.sol": {
      "content": "import  "./IUniswapV2Router01.sol";

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}"
