    "contracts/Swap.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './utils/Constants.sol';
import './interface/IUniswapV2Router02.sol';
import './interface/IUniswapV2Factory.sol';
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./common/AdminBaseUpgradeable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";

contract Swap is AdminBaseUpgradeable {
    using SafeMath for uint256;

    address private marketAddress;

    address private acAddress;

    address private bcAddress;

    IUniswapV2Router02 uniswapRouter;

    struct Order {
        uint256 amount;
        uint orderType;
    }

    // ac定义映射来存储地址和其对应的订单列表
    mapping(address => Order[]) public acOrders;

    ERC20 ac;
    ERC20 bc;

    /**
     * 初始化
     */
    function initialize(address marketAddress_) public initializer {
        BaseUpgradeable.__Base_init();
        uniswapRouter = IUniswapV2Router02(Constants.uniswapRouterAddress);
        marketAddress = marketAddress_;
    }

    function setMarketAddress(address address_) public {
        require(msg.sender == admin, "Unauthorized");
        marketAddress = address_;
    }

    /**
     * 是否服务修饰符
     */
    modifier isServing() {
        require(!isPaused, "1001");
        _;
    }

    function addAcLine(address _account, uint _amount, uint _orderType, bool _auto) public {
        require(msg.sender == acAddress, "Unauthorized");
        Order memory newOrder = Order({
            amount: _amount,
            orderType: _orderType
        });
        acOrders[_account].push(newOrder);
        if (_auto) {
            toBc(_account);
        }
    }

    function toBc(address from) public {
        require(msg.sender == acAddress, "Unauthorized");
        address[] memory path = new address[](3);
        path[0] = acAddress;
        path[1] = Constants.usdtAddress;
        path[2] = bcAddress;
        uint256 buyAmount = 0;
        uint256 sellAmount = 0;
        for (uint256 i = 0; i < acOrders[from].length; i++) {
            if (acOrders[from][i].orderType == 1) {
                // buy
                buyAmount += acOrders[from][i].amount;
            } else {
                // sell
                sellAmount += acOrders[from][i].amount;
            }
        }

        uint256 amount = buyAmount + sellAmount;
        if (amount > 0) {
            ac.approve(Constants.uniswapRouterAddress, amount);
            uint256 ruinAmount = 0;
            uint256 toAmount = 0;
            if (buyAmount > 0) {
                toAmount += buyAmount / 6 * 4;
                ruinAmount += buyAmount / 6 * 2;
            }

            if (sellAmount > 0) {
                toAmount += sellAmount / 6 * 1;
                ruinAmount += sellAmount / 6 * 5;
            }

            if (ruinAmount > 0 && toAmount > 0) {
                uniswapRouter.swapExactTokensForTokens(
                    toAmount,
                    0,
                    path,
                    address(this),
                    block.timestamp + 3600
                );
                bc.transfer(from, bc.balanceOf(address(this)));
                uniswapRouter.swapExactTokensForTokens(
                    ruinAmount,
                    0,
                    path,
                    address(this),
                    block.timestamp + 3600
                );
                uint256 destroy = bc.balanceOf(address(this));
                uint256 bonus = destroy * 20 / 100;
                bc.transfer(Constants.zeroAddress, destroy - bonus);
                bc.transfer(Constants.bonusAddress, bonus);
            }
            delete acOrders[from];
        }
    }

    function toAc(address from, uint amount) public {
        require(msg.sender == bcAddress, "Unauthorized");
        address[] memory path = new address[](3);
        path[0] = bcAddress;
        path[1] = Constants.usdtAddress;
        path[2] = acAddress;

        if (amount > 0) {
            bc.approve(Constants.uniswapRouterAddress, amount);
            bc.transfer(marketAddress, amount.div(6).mul(2));
            uniswapRouter.swapExactTokensForTokens(
                amount.div(6).mul(4),
                0,
                path,
                Constants.zeroAddress,
                block.timestamp + 3600
            );
        }
    }

    function setAcAddress(address address_) public {
        require(msg.sender == admin, "Unauthorized");
        ac = ERC20(address_);
        acAddress = address_;
    }

    function setBcAddress(address address_) public {
        require(msg.sender == admin, "Unauthorized");
        bc = ERC20(address_);
        bcAddress = address_;
    }

    function getAcAddress() external view returns (address) {
        return acAddress;
    }

    function getBcAddress() external view returns (address) {
        return bcAddress;
    }
}"
