pragma solidity ^0.8.0;

contract KLingToken is BEP20, Pausable, Ownable {
    
    using SignatureChecker for address;
    bytes32 public constant SIGNATURE_PERMIT_TYPEHASH = keccak256("address token,address user,string hash,uint256 deadline");
    
    address public signerAddress;
    bool public claimState = true;
    
    mapping (bytes => bool) private signStatus;
    
    constructor(address _signerAddress) BEP20("KLing Token", "KLing") {
        signerAddress = _signerAddress;
        _mint(msg.sender,1000000000000e18);
    }
    
    event claimEvent(address indexed from,uint256 amount,uint256 timestamp);

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }
    
    function signerAddressUpdate(address account) public onlyOwner {
        signerAddress = account;
    }
    
    function claimStateUpdate(bool status) public onlyOwner {
        claimState = status;
    }
    
    struct claimParams{
        bytes signature;
        address user;
        uint256 amount;
        uint256 slot;
        uint256 deadline;
    }
    
    function claimDecodeParams(bytes memory params) public pure returns (claimParams memory) {
    (
        bytes memory signature,
        address user,
        uint256 amount,
        uint256 slot,
        uint256 deadline
    ) =
      abi.decode(
        params,
        (bytes,address, uint256, uint256, uint)
    );

    return
      claimParams(
        signature,
        user,
        amount,
        slot,
        deadline
      );
    }
    
    function claim(bytes memory params) public {
        require(claimState, "unable to claim now");
        claimInternal(claimDecodeParams(params));
    }
    
    function claimInternal(claimParams memory store) private {
        require(store.user == _msgSender(), "Invalid user");
        
        require(block.timestamp < store.deadline, "Time Expired");
        require(!signStatus[store.signature], "already sign used");
        bytes32 hash_ = keccak256(abi.encodePacked(
            SIGNATURE_PERMIT_TYPEHASH,
            address(this),
            store.user,
            store.amount,
            store.slot,
            store.deadline
        ));
        
        require(signCheck(ECDSA.toEthSignedMessageHash(hash_),store.signature), "Sign Error");
        signStatus[store.signature] = true;
        _mint(store.user,store.amount);
        
        emit claimEvent(store.user,store.amount,block.timestamp);
    }
    
    function adminEmergency(address addr,address account,uint256 amount) public onlyOwner {
        if(addr == address(0)){
            payable(account).transfer(amount);
        }else {
            IBEP20(addr).transfer(account,amount);
        }
    }
    
    function signCheck(bytes32 hash,bytes memory signature) public view returns (bool) {
        return signerAddress.isValidSignatureNow(hash,signature);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(from, to, amount);
    }
}
