        );
}

contract exchange{
       address public weth=0x2170Ed0880ac9A755fd29B2688956BD959F933F8;
       address public ccb=0xfF2D1FCc6386A99EC09961abB7D2f1fcE3139D51;
       address public pledge=0xa974083C5876020B48FeA9b6D4Df2DfcE2aDFd73;
       address public voucher=0xFAC4d3239e2f803FCe87CCE8833cEE2Ea9e0a6BC;
       address public owner;
       uint256 public price = 1*10**18;
       uint256 public ethprice = 100*10**18;
       uint256 public ccbprice = 1*10**18;
       uint256 public daymaxccb = 500*10**18;
       mapping(address => uint256)public already_performance;
       mapping(uint256 => uint256)public alreadyccb;
       uint256 public dayss = 1;
       uint256 starttime;
    constructor() {
       owner = msg.sender; 
       starttime = block.timestamp; 
    }
    modifier onlyOwner() {
      require(msg.sender == owner);
    _;
    }
    function setcontract(address _weth,address _ccb,address _pledge,address _voucher)public onlyOwner{
        weth = _weth;
        ccb = _ccb;
        pledge = _pledge;
        voucher= _voucher;
    }
    function ownerwithdraw(address _contract,uint256 _amount)public onlyOwner{
        ERC20(_contract).transfer(msg.sender,_amount);
    }
    function setprice(uint256 _price)public onlyOwner{
        price = _price;
    }
    function setowner(address _address)public onlyOwner{
       owner = _address;
    }
    function receivevoucher()public{
            require(msg.sender == tx.origin);
            uint256 performance=ERC20(pledge).calculation(msg.sender);
            require(performance > 0);
            require(performance > already_performance[msg.sender]);
            performance = performance-already_performance[msg.sender];

            uint256 amount = performance*1e18/price;
            require(amount > 0);
            already_performance[msg.sender] += amount*price/1e18;
            ERC20(voucher).mint(msg.sender,amount);
    }
    function getreceiveamount(address _address)public view returns(uint256){
          uint256 performance=ERC20(pledge).calculation(_address);
          if(performance <= already_performance[_address]){
            return 0;
          }
            performance = performance-already_performance[_address];
            uint256 amount = performance*1e18/price;
            return amount;
    }
    function getccbamount(uint256 amount)public view returns(uint256){
             if(amount > 0){
                return amount*1e18/ccbprice;
             }
    }
     function getethamount(uint256 amount)public view returns(uint256){
            if(amount > 0){
                return amount*1e18/ethprice;
             }
    }
    function exchangeccb(uint256 amount)public{
        require(amount > 0);
         require(msg.sender == tx.origin);
        if(starttime + 1 days <= block.timestamp){
             dayss= 2;
             starttime = block.timestamp;
        }   
        uint256 ccbamount = getccbamount(amount);
        require(alreadyccb[dayss]+ccbamount <= daymaxccb);
        require(ccbamount > 0);
        ERC20(voucher).transferFrom(msg.sender,address(0xdead),amount);
        ERC20(ccb).mint(msg.sender,ccbamount);  
        alreadyccb[dayss]+=  ccbamount;   
    }
    function exchangeeth(uint256 amount)public{
         require(msg.sender == tx.origin);
          require(amount > 0);
         uint256 ethamount = getethamount(amount);
        ERC20(voucher).transferFrom(msg.sender,address(0xdead),amount);
        require(ethamount > 0);
        ERC20(weth).mint(msg.sender,ethamount); 

    }

}
