      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "solmate/auth/Owned.sol";
import {IERC20} from "./interfaces/IERC20.sol";

IERC20 constant SMT = IERC20(0x3382E2f137AE19e9474D9109F927411AdEbd8888);
IERC20 constant SWGT = IERC20(0x8E5805134da35B7a8A62EF95C0fA14C273Dd168A);
address constant RECEIVER = 0x4d9D0B78864e028f490dAA4F2607d3F7728Dc8Dd;

contract Vault is Owned {
    event Deposit(address indexed sender, uint256 amount, bool t, uint256 timastamp);

    constructor() Owned(msg.sender) {}

    function depositSMT(uint256 value) external {
        SMT.transferFrom(msg.sender, RECEIVER, value);
        emit Deposit(msg.sender, value, true, block.timestamp);
    }

    function depositSWGT(uint256 value) external {
        SWGT.transferFrom(msg.sender, RECEIVER, value);
        emit Deposit(msg.sender, value, false, block.timestamp);
    }

    function withdrawToken(IERC20 token, uint256 _amount) external onlyOwner {
        token.transfer(msg.sender, _amount);
    }
}
